static int metal_shmem_try_map(struct metal_page_size *ps, int fd, size_t size,
                               struct metal_io_region **result)
{
    size_t pages, page, phys_size;
    struct metal_io_region *io;
    metal_phys_addr_t *phys;
    uint8_t *virt;
    void *mem;
    int error;

    /* Open log file for tracing control flow and state for the violation scenario */
    FILE *logf = fopen("/tmp/logs/312.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER: %s ps=%p fd=%d size(initial)=%zu ps->page_size=%zu ps->mmap_flags=0x%x ps->page_shift=%d pagemap_fd=%d\n",
                __func__, (void *)ps, fd, size, ps ? ps->page_size : 0, ps ? ps->mmap_flags : 0, ps ? ps->page_shift : 0, _metal.pagemap_fd);
        fflush(logf);
    }

    /* Capture original size to show that the parameter is modified (violation scenario) */
    {
        size_t original_size = size;
        size = metal_align_up(size, ps->page_size);
        if (logf) {
            fprintf(logf, "ACTION: aligned size from %zu to %zu using page_size %zu (size = metal_align_up(original_size, ps->page_size))\n",
                    original_size, size, ps->page_size);
            fflush(logf);
        }
    }

    pages = size / ps->page_size;
    if (logf) {
        fprintf(logf, "COMPUTE: pages=%zu (size=%zu / page_size=%zu)\n", pages, size, ps->page_size);
        fflush(logf);
    }

    error = metal_map(fd, 0, size, 1, ps->mmap_flags, &mem);
    if (error) {
        if (logf) {
            fprintf(logf, "ERROR: metal_map failed fd=%d size=%zu mmap_flags=0x%x error=%d strerror=%s\n",
                    fd, size, ps->mmap_flags, error, strerror(-error));
            fflush(logf);
            fclose(logf);
        }
        metal_log(METAL_LOG_WARNING,
              "failed to mmap shmem %ld,0x%x - %s\n",
              size, ps->mmap_flags, strerror(-error));
        return error;
    }

    if (logf) {
        fprintf(logf, "OK: metal_map returned mem=%p\n", mem);
        fflush(logf);
    }

    error = mlock(mem, size);
    if (error) {
        if (logf) {
            fprintf(logf, "WARN: mlock failed mem=%p size=%zu errno=%d strerror=%s\n",
                    mem, size, errno, strerror(errno));
            fflush(logf);
        }
        metal_log(METAL_LOG_WARNING, "failed to mlock shmem - %s\n",
              strerror(errno));
    } else {
        if (logf) {
            fprintf(logf, "OK: mlock succeeded mem=%p size=%zu\n", mem, size);
            fflush(logf);
        }
    }

    phys_size = sizeof(*phys) * pages;
    phys = malloc(phys_size);
    if (!phys) {
        if (logf) {
            fprintf(logf, "ERROR: malloc phys_size=%zu failed\n", phys_size);
            fflush(logf);
            fclose(logf);
        }
        metal_unmap(mem, size);
        return -ENOMEM;
    }
    if (logf) {
        fprintf(logf, "OK: allocated phys array=%p phys_size=%zu\n", (void *)phys, phys_size);
        fflush(logf);
    }

    io = malloc(sizeof(*io));
    if (!io) {
        if (logf) {
            fprintf(logf, "ERROR: malloc io failed\n");
            fflush(logf);
            fclose(logf);
        }
        free(phys);
        metal_unmap(mem, size);
        return -ENOMEM;
    }
    if (logf) {
        fprintf(logf, "OK: allocated io=%p sizeof=%zu\n", (void *)io, sizeof(*io));
        fflush(logf);
    }

    if (_metal.pagemap_fd < 0) {
        phys[0] = 0;
        if (logf) {
            fprintf(logf, "BRANCH: _metal.pagemap_fd < 0, using offset as pa. phys[0]=%llu\n", (unsigned long long)phys[0]);
            fflush(logf);
        }
        metal_log(METAL_LOG_WARNING,
        "shmem - failed to get va2pa mapping. use offset as pa.\n");
        metal_io_init(io, mem, phys, size, -1, 0, &metal_shmem_io_ops);
    } else {
        if (logf) {
            fprintf(logf, "BRANCH: _metal.pagemap_fd >= 0, resolving virt->phys for each page (pages=%zu)\n", pages);
            fflush(logf);
        }
        for (virt = mem, page = 0; page < pages; page++) {
            size_t offset = page * ps->page_size;

            error = metal_virt2phys(virt + offset, &phys[page]);
            if (error < 0)
                phys[page] = METAL_BAD_OFFSET;

            if (logf) {
                fprintf(logf, "PAGE[%zu]: virt=%p offset=%zu virt+offset=%p metal_virt2phys_error=%d phys[%zu]=%llu\n",
                        page, (void *)virt, offset, (void *)(virt + offset), error, page, (unsigned long long)phys[page]);
                fflush(logf);
            }
        }
        metal_io_init(io, mem, phys, size, ps->page_shift, 0,
            &metal_shmem_io_ops);
    }
    *result = io;

    if (logf) {
        fprintf(logf, "EXIT: success result io=%p mem=%p size=%zu pages=%zu\n", (void *)io, mem, size, pages);
        fflush(logf);
        fclose(logf);
    }

    return 0;
}
