static int metal_shmem_try_map(struct metal_page_size *ps, int fd, size_t size,
			       struct metal_io_region **result)
{
	FILE *logf = fopen("/tmp/logs/315.txt", "a");
	size_t pages, page, phys_size;
	struct metal_io_region *io;
	metal_phys_addr_t *phys;
	uint8_t *virt;
	void *mem;
	int error;

	/* Entry probe: record input pointer and parameters */
	if (logf) {
		fprintf(logf, "ENTER metal_shmem_try_map: ps=%p, fd=%d, size(in)=%zu\n",
			(void *)ps, fd, size);
		/* Dereference ps as the function does; this demonstrates the fields read */
		fprintf(logf, "ps->page_size=%zu, ps->mmap_flags=0x%x, ps->page_shift=%d\n",
			ps->page_size, ps->mmap_flags, ps->page_shift);
		fflush(logf);
	}

	size = metal_align_up(size, ps->page_size);
	if (logf) {
		fprintf(logf, "aligned size=%zu (page_size=%zu)\n",
			size, ps->page_size);
		fflush(logf);
	}

	pages = size / ps->page_size;
	if (logf) {
		fprintf(logf, "pages=%zu\n", pages);
		fflush(logf);
	}

	error = metal_map(fd, 0, size, 1, ps->mmap_flags, &mem);
	if (logf) {
		fprintf(logf, "metal_map returned %d, mem=%p\n", error, mem);
		fflush(logf);
	}
	if (error) {
		if (logf) {
			fprintf(logf, "FAILED metal_map: size=%zu, mmap_flags=0x%x, err=%s\n",
				size, ps->mmap_flags, strerror(-error));
			fclose(logf);
		}
		metal_log(METAL_LOG_WARNING,
			  "failed to mmap shmem %ld,0x%x - %s\n",
			  size, ps->mmap_flags, strerror(-error));
		return error;
	}

	error = mlock(mem, size);
	if (logf) {
		fprintf(logf, "mlock returned %d (errno=%d)\n", error, errno);
		fflush(logf);
	}
	if (error) {
		if (logf) {
			fprintf(logf, "WARNING: failed to mlock shmem - %s\n", strerror(errno));
			fflush(logf);
		}
		metal_log(METAL_LOG_WARNING, "failed to mlock shmem - %s\n",
			  strerror(errno));
	}

	phys_size = sizeof(*phys) * pages;
	if (logf) {
		fprintf(logf, "allocating phys array: phys_size=%zu\n", phys_size);
		fflush(logf);
	}
	phys = malloc(phys_size);
	if (!phys) {
		if (logf) {
			fprintf(logf, "malloc phys failed (size=%zu)\n", phys_size);
			fclose(logf);
		}
		metal_unmap(mem, size);
		return -ENOMEM;
	}

	io = malloc(sizeof(*io));
	if (logf) {
		fprintf(logf, "allocated phys=%p, allocating io...\n", (void *)phys);
		fflush(logf);
	}
	if (!io) {
		if (logf) {
			fprintf(logf, "malloc io failed\n");
			fclose(logf);
		}
		free(phys);
		metal_unmap(mem, size);
		return -ENOMEM;
	}

	if (_metal.pagemap_fd < 0) {
		phys[0] = 0;
		if (logf) {
			fprintf(logf, "_metal.pagemap_fd < 0: using offset as pa. phys[0]=%llu\n",
				(unsigned long long)phys[0]);
			fflush(logf);
		}
		metal_log(METAL_LOG_WARNING,
		"shmem - failed to get va2pa mapping. use offset as pa.\n");
		metal_io_init(io, mem, phys, size, -1, 0, &metal_shmem_io_ops);
	} else {
		if (logf) {
			fprintf(logf, "_metal.pagemap_fd >= 0: attempting virt->phys translation for %zu pages\n", pages);
			fflush(logf);
		}
		for (virt = mem, page = 0; page < pages; page++) {
			size_t offset = page * ps->page_size;

			error = metal_virt2phys(virt + offset, &phys[page]);
			if (logf) {
				fprintf(logf, "page=%zu, offset=%zu, virt=%p, metal_virt2phys err=%d, phys[%zu]=%llu\n",
					page, offset, (void *)(virt + offset), error, page,
					(unsigned long long)phys[page]);
				fflush(logf);
			}
			if (error < 0)
				phys[page] = METAL_BAD_OFFSET;
		}
		metal_io_init(io, mem, phys, size, ps->page_shift, 0,
			&metal_shmem_io_ops);
	}
	*result = io;

	if (logf) {
		fprintf(logf, "EXIT metal_shmem_try_map: success, io=%p, result set to %p\n",
			(void *)io, (void *)*result);
		fclose(logf);
	}

	return 0;
}
