static int metal_shmem_try_map(struct metal_page_size *ps, int fd, size_t size,
                               struct metal_io_region **result)
{
    size_t pages, page, phys_size;
    struct metal_io_region *io;
    metal_phys_addr_t *phys;
    uint8_t *virt;
    void *mem;
    int error;

    /* Open log file for probes. Keep appending so tests can inspect sequence. */
    FILE *log = fopen("/tmp/logs/315.txt", "a");
    if (log) {
        fprintf(log, "ENTER: metal_shmem_try_map(ps=%p, fd=%d, size=%zu)\n", (void *)ps, fd, size);
        fflush(log);
    } else {
        metal_log(METAL_LOG_WARNING, "failed to open probe log /tmp/logs/315.txt\n");
    }

    size = metal_align_up(size, ps->page_size);
    pages = size / ps->page_size;

    if (log) fprintf(log, "aligned size=%zu page_size=%zu pages=%zu\n", size, ps->page_size, pages);

    error = metal_map(fd, 0, size, 1, ps->mmap_flags, &mem);
    if (error) {
        metal_log(METAL_LOG_WARNING,
              "failed to mmap shmem %ld,0x%x - %s\n",
              size, ps->mmap_flags, strerror(-error));
        if (log) {
            fprintf(log, "metal_map FAILED error=%d (%s)\n", error, strerror(-error));
            fclose(log);
        }
        return error;
    }

    if (log) fprintf(log, "metal_map succeeded mem=%p mmap_flags=0x%x\n", mem, ps->mmap_flags);

    error = mlock(mem, size);
    if (error) {
        metal_log(METAL_LOG_WARNING, "failed to mlock shmem - %s\n",
              strerror(errno));
        if (log) fprintf(log, "mlock FAILED errno=%d (%s)\n", errno, strerror(errno));
    } else {
        if (log) fprintf(log, "mlock succeeded\n");
    }

    phys_size = sizeof(*phys) * pages;
    phys = malloc(phys_size);
    if (!phys) {
        metal_unmap(mem, size);
        if (log) {
            fprintf(log, "malloc(phys) FAILED phys_size=%zu - unmapping mem=%p and returning -ENOMEM\n", phys_size, mem);
            fclose(log);
        }
        return -ENOMEM;
    }

    if (log) fprintf(log, "allocated phys=%p phys_size=%zu\n", (void *)phys, phys_size);

    io = malloc(sizeof(*io));
    if (!io) {
        free(phys);
        metal_unmap(mem, size);
        if (log) {
            fprintf(log, "malloc(io) FAILED - freed phys and unmapped mem=%p; returning -ENOMEM\n", mem);
            fclose(log);
        }
        return -ENOMEM;
    }

    if (log) fprintf(log, "allocated io=%p\n", (void *)io);

    if (_metal.pagemap_fd < 0) {
        phys[0] = 0;
        metal_log(METAL_LOG_WARNING,
        "shmem - failed to get va2pa mapping. use offset as pa.\n");
        if (log) {
            fprintf(log, "_metal.pagemap_fd < 0: using offset as PA; phys[0]=0\n");
            fprintf(log, "calling metal_io_init(io=%p, mem=%p, phys=%p, size=%zu, shift=%d)\n",
                    (void *)io, mem, (void *)phys, size, -1);
            fclose(log);
        }
        metal_io_init(io, mem, phys, size, -1, 0, &metal_shmem_io_ops);
    } else {
        if (log) fprintf(log, "_metal.pagemap_fd >= 0: attempting va2pa for %zu pages\n", pages);

        for (virt = mem, page = 0; page < pages; page++) {
            size_t offset = page * ps->page_size;

            if (log) fprintf(log, "loop page=%zu offset=%zu virt=%p target_addr=%p\n",
                              page, offset, (void *)virt, (void *)(virt + offset));

            error = metal_virt2phys(virt + offset, &phys[page]);
            if (error < 0) {
                phys[page] = METAL_BAD_OFFSET;
                if (log) fprintf(log, "metal_virt2phys FAILED page=%zu error=%d -> phys[%zu]=METAL_BAD_OFFSET\n",
                                 page, error, page);
            } else {
                if (log) fprintf(log, "metal_virt2phys OK page=%zu -> phys[%zu]=%llu\n",
                                 page, page, (unsigned long long)phys[page]);
            }
        }
        if (log) {
            fprintf(log, "calling metal_io_init(io=%p, mem=%p, phys=%p, size=%zu, shift=%u)\n",
                    (void *)io, mem, (void *)phys, size, ps->page_shift);
            fclose(log);
        }
        metal_io_init(io, mem, phys, size, ps->page_shift, 0,
            &metal_shmem_io_ops);
    }
    *result = io;

    /* Final log entry */
    if (log) {
        log = fopen("/tmp/logs/315.txt", "a");
        if (log) {
            fprintf(log, "EXIT: metal_shmem_try_map returning io=%p result_ptr=%p\n", (void *)io, (void *)result);
            fclose(log);
        }
    }

    return 0;
}
