int metal_sys_init(const struct metal_init_params *params)
{
	const char *tmp_path;
	unsigned int seed;
	FILE *urandom;
	int result;
	FILE *logf = fopen("/tmp/logs/727.txt", "a");

	/* Minimal entry probe so tests show the function was entered. */
	if (logf) {
		fprintf(logf, "ENTER metal_sys_init params=%p\n", (void *)params);
		fflush(logf);
	}

	/* Find the temporary directory location. */
	tmp_path = getenv("TMPDIR");
	if (!tmp_path)
		tmp_path = "/tmp";
	_metal.tmp_path = tmp_path;
	if (logf) {
		fprintf(logf, "tmp_path=%s\n", tmp_path);
		fflush(logf);
	}

	/* Initialize the pseudo-random number generator. */
	urandom = fopen("/dev/urandom", "r");
	if (!urandom) {
		int err = errno;
		if (logf) {
			fprintf(logf, "fopen(/dev/urandom) failed: errno=%d (%s)\n", err, strerror(err));
			fprintf(logf, "About to call metal_log(METAL_LOG_ERROR, ...)\n");
			fflush(logf);
		}
		/* Preserve original behavior and log via metal_log as originally written. */
		metal_log(METAL_LOG_ERROR, "failed to open /dev/urandom (%s)\n", strerror(err));
		if (logf) {
			fprintf(logf, "Returned from metal_log(METAL_LOG_ERROR, ...)\n");
			fflush(logf);
			fclose(logf);
		}
		return -err;
	}
	if (logf) {
		fprintf(logf, "/dev/urandom opened\n");
		fflush(logf);
	}

	if (fread(&seed, 1, sizeof(seed), urandom) <= 0) {
		if (logf) {
			fprintf(logf, "fread(/dev/urandom) failed or returned 0\n");
			fprintf(logf, "About to call metal_log(METAL_LOG_DEBUG, ...)\n");
			fflush(logf);
		}
		metal_log(METAL_LOG_DEBUG, "Failed fread /dev/urandom\n");
	} else {
		if (logf) {
			fprintf(logf, "Seed read: %u\n", seed);
			fflush(logf);
		}
	}
	fclose(urandom);
	if (logf) {
		fprintf(logf, "/dev/urandom closed\n");
		fflush(logf);
	}

	srand(seed);
	if (logf) {
		fprintf(logf, "srand called (seed=%u)\n", seed);
		fflush(logf);
	}

	result = metal_init_page_sizes();
	if (logf) {
		fprintf(logf, "metal_init_page_sizes() returned %d\n", result);
		fflush(logf);
	}
	if (result < 0) {
		if (logf) fclose(logf);
		return result;
	}

	result = metal_linux_bus_init();
	if (logf) {
		fprintf(logf, "metal_linux_bus_init() returned %d\n", result);
		fflush(logf);
	}
	if (result < 0) {
		if (logf) fclose(logf);
		return result;
	}

	result = open("/proc/self/pagemap", O_RDONLY | O_CLOEXEC);
	if (result < 0) {
		int err = errno;
		if (logf) {
			fprintf(logf, "open(/proc/self/pagemap) failed: fd=%d errno=%d (%s)\n", result, err, strerror(err));
			fprintf(logf, "About to call metal_log(METAL_LOG_DEBUG, ...)\n");
			fflush(logf);
		}
		metal_log(METAL_LOG_DEBUG, "Failed pagemap open - %s\n", strerror(err));
	} else {
		if (logf) {
			fprintf(logf, "/proc/self/pagemap opened fd=%d\n", result);
			fflush(logf);
		}
	}
	_metal.pagemap_fd = result;

	metal_unused(params);
	if (logf) {
		fprintf(logf, "Calling metal_linux_irq_init()\n");
		fflush(logf);
	}

	/* Initialize IRQ handling */
	metal_linux_irq_init();

	if (logf) {
		fprintf(logf, "EXIT metal_sys_init (return 0)\n");
		fclose(logf);
	}
	return 0;
}
