int metal_sys_init(const struct metal_init_params *params)
{
    const char *tmp_path;
    unsigned int seed;
    FILE *urandom;
    int result;
    FILE *log = NULL;

    /* Ensure log directory exists and open log file for append. */
    /* Use system() as a best-effort to create the directory; if unavailable,
       fopen will fail and we will continue without log file. */
    (void)system("/bin/mkdir -p /tmp/logs");
    log = fopen("/tmp/logs/721.txt", "a");
    if (log) {
        fprintf(log, "--- metal_sys_init ENTER pid=%d tmp=%p\n", (int)getpid(), (void *)&tmp_path);
        fflush(log);
    }

    /* Find the temporary directory location. */
    tmp_path = getenv("TMPDIR");
    if (!tmp_path)
        tmp_path = "/tmp";
    _metal.tmp_path = tmp_path;

    if (log) {
        fprintf(log, "tmp_path resolved='%s' tmp_path_addr=%p _metal.tmp_path=%p\n",
                tmp_path, (void *)tmp_path, (void *)_metal.tmp_path);
        fflush(log);
    }

    /* Initialize the pseudo-random number generator. */
    urandom = fopen("/dev/urandom", "r");
    if (!urandom) {
        if (log) {
            fprintf(log, "open /dev/urandom FAILED errno=%d (%s)\n", errno, strerror(errno));
            fflush(log);
        }
        metal_log(METAL_LOG_ERROR, "failed to open /dev/urandom (%s)\n",
                  strerror(errno));
        if (log) {
            fprintf(log, "--- metal_sys_init LEAVE (urandom open failed)\n\n");
            fclose(log);
        }
        return -errno;
    }

    if (log) {
        fprintf(log, "/dev/urandom opened ok (FILE=%p)\n", (void *)urandom);
        fflush(log);
    }

    if (fread(&seed, 1, sizeof(seed), urandom) <= 0) {
        if (log) {
            fprintf(log, "Failed fread /dev/urandom (read zero bytes). seed (uninitialized) addr=%p\n",
                    (void *)&seed);
            fflush(log);
        }
        metal_log(METAL_LOG_DEBUG, "Failed fread /dev/urandom\n");
    } else {
        if (log) {
            fprintf(log, "Read seed from /dev/urandom: 0x%08x (%u)\n", seed, seed);
            fflush(log);
        }
    }
    fclose(urandom);
    srand(seed);

    if (log) {
        fprintf(log, "After srand() seed=0x%08x\n", seed);
        fflush(log);
    }

    result = metal_init_page_sizes();
    if (log) {
        fprintf(log, "metal_init_page_sizes() returned %d\n", result);
        fflush(log);
    }
    if (result < 0) {
        if (log) {
            fprintf(log, "--- metal_sys_init LEAVE (page sizes init failed) ret=%d\n\n", result);
            fclose(log);
        }
        return result;
    }

    if (log) {
        /* Log the address of the function we are about to call to help trace calls
           in a post-mortem (shows that the call was attempted). */
        fprintf(log, "About to call metal_linux_bus_init() at %p\n", (void *)metal_linux_bus_init);
        fflush(log);
    }

    result = metal_linux_bus_init();

    if (log) {
        fprintf(log, "metal_linux_bus_init() returned %d\n", result);
        fflush(log);
    }

    if (result < 0) {
        if (log) {
            fprintf(log, "--- metal_sys_init LEAVE (linux bus init failed) ret=%d\n\n", result);
            fclose(log);
        }
        return result;
    }

    result = open("/proc/self/pagemap", O_RDONLY | O_CLOEXEC);
    if (result < 0) {
        metal_log(METAL_LOG_DEBUG, "Failed pagemap open - %s\n",
                  strerror(errno));
        if (log) {
            fprintf(log, "open(/proc/self/pagemap) FAILED errno=%d (%s)\n", errno, strerror(errno));
            fflush(log);
        }
    }
    _metal.pagemap_fd = result;

    if (log) {
        fprintf(log, "pagemap_fd=%d assigned to _metal.pagemap_fd\n", _metal.pagemap_fd);
        fflush(log);
    }

    metal_unused(params);

    /* Initialize IRQ handling */
    if (log) {
        fprintf(log, "About to call metal_linux_irq_init() at %p\n", (void *)metal_linux_irq_init);
        fflush(log);
    }
    metal_linux_irq_init();

    if (log) {
        fprintf(log, "metal_linux_irq_init() completed. Exiting metal_sys_init with 0\n");
        fprintf(log, "--- metal_sys_init LEAVE pid=%d\n\n", (int)getpid());
        fclose(log);
    }
    return 0;
}
