int metal_sys_init(const struct metal_init_params *params)
{
    const char *tmp_path;
    unsigned int seed;
    FILE *urandom;
    int result;
    FILE *logf = NULL;
    size_t urandom_r = 0;

    /* Try to open a log file to record control flow/state for debugging. */
    /* Attempt to create directory if possible (harmless if already exists). */
    /* Note: the surrounding compilation unit already includes needed headers. */
    (void)mkdir("/tmp/logs", 0777);
    logf = fopen("/tmp/logs/722.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER metal_sys_init: params_ptr=%p address_of_params=%p is_null=%d\n",
                (void *)params, (void *)&params, (params == NULL));
        fflush(logf);
    }

    /* Find the temporary directory location. */
    tmp_path = getenv("TMPDIR");
    if (!tmp_path)
        tmp_path = "/tmp";
    _metal.tmp_path = tmp_path;

    if (logf) {
        fprintf(logf, "tmp_path chosen='%s'\n", tmp_path);
        fflush(logf);
    }

    /* Initialize the pseudo-random number generator. */
    urandom = fopen("/dev/urandom", "r");
    if (!urandom) {
        metal_log(METAL_LOG_ERROR, "failed to open /dev/urandom (%s)\n",
                  strerror(errno));
        if (logf) {
            fprintf(logf, "failed to open /dev/urandom: errno=%d (%s)\n",
                    errno, strerror(errno));
            fflush(logf);
            fclose(logf);
        }
        return -errno;
    }

    urandom_r = fread(&seed, 1, sizeof(seed), urandom);
    if (urandom_r <= 0) {
        metal_log(METAL_LOG_DEBUG, "Failed fread /dev/urandom\n");
        if (logf) {
            fprintf(logf, "fread /dev/urandom read_bytes=%zu (<=0)\n", urandom_r);
            fflush(logf);
        }
    } else {
        if (logf) {
            fprintf(logf, "fread /dev/urandom read_bytes=%zu seed=0x%08x\n", urandom_r, seed);
            fflush(logf);
        }
    }
    fclose(urandom);
    srand(seed);

    result = metal_init_page_sizes();
    if (logf) {
        fprintf(logf, "metal_init_page_sizes returned=%d\n", result);
        fflush(logf);
    }
    if (result < 0) {
        if (logf) fclose(logf);
        return result;
    }

    result = metal_linux_bus_init();
    if (logf) {
        fprintf(logf, "metal_linux_bus_init returned=%d\n", result);
        fflush(logf);
    }
    if (result < 0) {
        if (logf) fclose(logf);
        return result;
    }

    result = open("/proc/self/pagemap", O_RDONLY | O_CLOEXEC);
    if (result < 0) {
        metal_log(METAL_LOG_DEBUG, "Failed pagemap open - %s\n",
                  strerror(errno));
        if (logf) {
            fprintf(logf, "open /proc/self/pagemap failed: errno=%d (%s)\n",
                    errno, strerror(errno));
            fflush(logf);
        }
    }
    _metal.pagemap_fd = result;

    /* Probe right before the potentially violating macro expansion. */
    if (logf) {
        fprintf(logf, "BEFORE metal_unused: params_ptr=%p address_of_params=%p is_null=%d\n",
                (void *)params, (void *)&params, (params == NULL));
        fflush(logf);
    }

    /* This is the original call that expands to an assignment to the
     * function parameter (e.g., metal_unused(x) -> (x) = (x);).
     * The assignment modifies the parameter variable itself (the pointer),
     * which is the scenario under investigation for MISRA C Rule 17.8.
     */
    metal_unused(params);

    if (logf) {
        fprintf(logf, "AFTER  metal_unused: params_ptr=%p address_of_params=%p is_null=%d\n",
                (void *)params, (void *)&params, (params == NULL));
        fflush(logf);
    }

    /* Initialize IRQ handling */
    metal_linux_irq_init();

    if (logf) {
        fprintf(logf, "EXIT metal_sys_init: returning=0 pagemap_fd=%d\n", _metal.pagemap_fd);
        fflush(logf);
        fclose(logf);
    }

    return 0;
}
