int metal_sys_init(const struct metal_init_params *params)
{
	const char *tmp_path;
	unsigned int seed;
	FILE *urandom;
	int result;
	FILE *logf = NULL;

	/* Open probe log file early so we can record control flow and state. */
	logf = fopen("/tmp/logs/722.txt", "a");
	if (logf) {
		fprintf(logf, "ENTER metal_sys_init: params=%p\n", (const void *)params);
	}

	/* Find the temporary directory location. */
	tmp_path = getenv("TMPDIR");
	if (!tmp_path)
		tmp_path = "/tmp";
	_metal.tmp_path = tmp_path;
	if (logf) {
		fprintf(logf, "tmp_path=%s\n", tmp_path);
	}

	/* Initialize the pseudo-random number generator. */
	urandom = fopen("/dev/urandom", "r");
	if (!urandom) {
		if (logf) {
			fprintf(logf, "failed to open /dev/urandom: errno=%d (%s)\n", errno, strerror(errno));
			fclose(logf);
		}
		metal_log(METAL_LOG_ERROR, "failed to open /dev/urandom (%s)\n",
			  strerror(errno));
		return -errno;
	}
	if (fread(&seed, 1, sizeof(seed), urandom) <= 0) {
		metal_log(METAL_LOG_DEBUG, "Failed fread /dev/urandom\n");
		if (logf) {
			fprintf(logf, "fread /dev/urandom failed or returned 0\n");
		}
	}
	fclose(urandom);
	srand(seed);
	if (logf) {
		fprintf(logf, "seed initialized: %u\n", seed);
	}

	result = metal_init_page_sizes();
	if (result < 0) {
		if (logf) {
			fprintf(logf, "metal_init_page_sizes failed: %d\n", result);
			fclose(logf);
		}
		return result;
	}

	result = metal_linux_bus_init();
	if (result < 0) {
		if (logf) {
			fprintf(logf, "metal_linux_bus_init failed: %d\n", result);
			fclose(logf);
		}
		return result;
	}

	result = open("/proc/self/pagemap", O_RDONLY | O_CLOEXEC);
	if (result < 0) {
		metal_log(METAL_LOG_DEBUG, "Failed pagemap open - %s\n",
			  strerror(errno));
	}
	_metal.pagemap_fd = result;
	if (logf) {
		fprintf(logf, "pagemap_fd=%d\n", result);
	}

	/* Probe the parameter state immediately before the macro that triggers
	 * the suspected MISRA violation. The macro metal_unused(params) expands
	 * to an assignment which writes to the parameter itself (params = params;).
	 * We log the pointer value before and after to provide evidence of the
	 * control flow and state surrounding the operation.
	 */
	if (logf) {
		fprintf(logf, "about to call metal_unused(params): params(before)=%p\n", (const void *)params);
	}

	metal_unused(params);

	if (logf) {
		fprintf(logf, "after call metal_unused(params): params(after)=%p\n", (const void *)params);
	}

	/* Initialize IRQ handling */
	metal_linux_irq_init();

	if (logf) {
		fprintf(logf, "EXIT metal_sys_init: return=0\n");
		fclose(logf);
	}
	return 0;
}
