int metal_sys_init(const struct metal_init_params *params)
{
	const char *tmp_path;
	unsigned int seed;
	FILE *urandom;
	int result;
	FILE *logf = NULL;
	unsigned char before_snap[sizeof(void *) * 3];
	unsigned char after_snap[sizeof(void *) * 3];
	size_t snap_size = sizeof(void *) * 3;
	size_t i;
	void *params_addr = (void *)&params;

	/* Try to open log file for append; create dir if necessary. */
	if (mkdir("/tmp/logs", 0700) && errno != EEXIST) {
		/* ignore mkdir error, we will try to open anyway */
	}
	logf = fopen("/tmp/logs/722.txt", "a");
	if (!logf)
		logf = stderr; /* fallback so logs are never empty */

	fprintf(logf, "ENTER metal_sys_init: params value=%p, params_addr=%p\n",
		(const void *)params, params_addr);

	/* Find the temporary directory location. */
	tmp_path = getenv("TMPDIR");
	if (!tmp_path)
		tmp_path = "/tmp";
	_metal.tmp_path = tmp_path;
	fprintf(logf, "tmp_path set to '%s'\n", _metal.tmp_path);

	/* Initialize the pseudo-random number generator. */
	urandom = fopen("/dev/urandom", "r");
	if (!urandom) {
		metal_log(METAL_LOG_ERROR, "failed to open /dev/urandom (%s)\n",
			  strerror(errno));
		fprintf(logf, "FAILED open /dev/urandom: %s (errno=%d)\n",
			strerror(errno), errno);
		if (logf && logf != stderr)
			fclose(logf);
		return -errno;
	}
	fprintf(logf, "/dev/urandom opened\n");
	if (fread(&seed, 1, sizeof(seed), urandom) <= 0) {
		metal_log(METAL_LOG_DEBUG, "Failed fread /dev/urandom\n");
		fprintf(logf, "fread from /dev/urandom failed or returned 0\n");
	} else {
		fprintf(logf, "seed read from /dev/urandom = %u\n", seed);
	}
	fclose(urandom);
	srand(seed);
	fprintf(logf, "srand(seed) called\n");

	/* Preserve a snapshot of the stack bytes around the params local before call */
	if (snap_size > sizeof(before_snap))
		snap_size = sizeof(before_snap);
	/* Safely attempt to copy a small region around the params variable. */
	memcpy(before_snap, (unsigned char *)params_addr - sizeof(void *), snap_size);
	fprintf(logf, "stack snapshot BEFORE metal_unused (hex):");
	for (i = 0; i < snap_size; ++i)
		fprintf(logf, " %02x", before_snap[i]);
	fprintf(logf, "\n");
	fflush(logf);

	result = metal_init_page_sizes();
	fprintf(logf, "metal_init_page_sizes returned %d\n", result);
	if (result < 0) {
		if (logf && logf != stderr)
			fclose(logf);
		return result;
	}

	result = metal_linux_bus_init();
	fprintf(logf, "metal_linux_bus_init returned %d\n", result);
	if (result < 0) {
		if (logf && logf != stderr)
			fclose(logf);
		return result;
	}

	result = open("/proc/self/pagemap", O_RDONLY | O_CLOEXEC);
	if (result < 0) {
		metal_log(METAL_LOG_DEBUG, "Failed pagemap open - %s\n",
			  strerror(errno));
		fprintf(logf, "Failed pagemap open: %s (errno=%d)\n",
			strerror(errno), errno);
	} else {
		fprintf(logf, "pagemap fd = %d\n", result);
	}
	_metal.pagemap_fd = result;

	/* Evidence point: log the params value/address before and after metal_unused */
	fprintf(logf, "About to call metal_unused(params). params=%p, params_addr=%p\n",
		(const void *)params, params_addr);

	/* Call the macro that is reported to cause an assignment to the parameter. */
	metal_unused(params);

	/* Snapshot after the call */
	memcpy(after_snap, (unsigned char *)params_addr - sizeof(void *), snap_size);
	fprintf(logf, "stack snapshot AFTER metal_unused (hex):");
	for (i = 0; i < snap_size; ++i)
		fprintf(logf, " %02x", after_snap[i]);
	fprintf(logf, "\n");

	/* Compare snapshots to detect any byte differences in the observed region. */
	if (memcmp(before_snap, after_snap, snap_size) == 0) {
		fprintf(logf, "No byte changes detected in observed region around params after metal_unused\n");
	} else {
		fprintf(logf, "Detected byte changes in observed region around params after metal_unused. Changed offsets:\n");
		for (i = 0; i < snap_size; ++i) {
			if (before_snap[i] != after_snap[i])
				fprintf(logf, " offset %zu: before=0x%02x after=0x%02x\n",
					i, before_snap[i], after_snap[i]);
		}
	}
	fflush(logf);

	/* Initialize IRQ handling */
	metal_linux_irq_init();
	fprintf(logf, "metal_linux_irq_init done, returning 0\n");
	if (logf && logf != stderr)
		fclose(logf);
	return 0;
}
