int metal_sys_init(const struct metal_init_params *params)
{
	const char *tmp_path;
	unsigned int seed;
	FILE *urandom;
	int result;
	FILE *logf = NULL;

	/* Open or create the log file for probes. Try to ensure the directory exists. */
	logf = fopen("/tmp/logs/728.txt", "a");
	if (!logf) {
		/* Best-effort directory creation; ignore errors if it fails. */
		system("mkdir -p /tmp/logs");
		logf = fopen("/tmp/logs/728.txt", "a");
	}
	if (logf) {
		fprintf(logf, "metal_sys_init: entered (params=%p)\n", (const void *)params);
		fflush(logf);
	}

	/* Find the temporary directory location. */
	tmp_path = getenv("TMPDIR");
	if (!tmp_path)
		tmp_path = "/tmp";
	_metal.tmp_path = tmp_path;
	if (logf) {
		fprintf(logf, "metal_sys_init: tmp_path='%s'\n", tmp_path);
		fflush(logf);
	}

	/* Initialize the pseudo-random number generator. */
	urandom = fopen("/dev/urandom", "r");
	if (!urandom) {
		metal_log(METAL_LOG_ERROR, "failed to open /dev/urandom (%s)\n",
			  strerror(errno));
		if (logf) {
			fprintf(logf, "metal_sys_init: fopen(/dev/urandom) failed: %s\n", strerror(errno));
			fflush(logf);
		}
		return -errno;
	}
	if (fread(&seed, 1, sizeof(seed), urandom) <= 0) {
		metal_log(METAL_LOG_DEBUG, "Failed fread /dev/urandom\n");
		if (logf) {
			fprintf(logf, "metal_sys_init: fread /dev/urandom failed or returned 0 bytes\n");
			fflush(logf);
		}
	} else {
		if (logf) {
			fprintf(logf, "metal_sys_init: seed read from /dev/urandom = %u\n", seed);
			fflush(logf);
		}
	}
	fclose(urandom);
	srand(seed);

	result = metal_init_page_sizes();
	if (logf) {
		fprintf(logf, "metal_sys_init: metal_init_page_sizes() -> %d\n", result);
		fflush(logf);
	}
	if (result < 0) {
		if (logf) fclose(logf);
		return result;
	}

	result = metal_linux_bus_init();
	if (logf) {
		fprintf(logf, "metal_sys_init: metal_linux_bus_init() -> %d\n", result);
		fflush(logf);
	}
	if (result < 0) {
		if (logf) fclose(logf);
		return result;
	}

	result = open("/proc/self/pagemap", O_RDONLY | O_CLOEXEC);
	if (result < 0) {
		metal_log(METAL_LOG_DEBUG, "Failed pagemap open - %s\n",
			  strerror(errno));
		if (logf) {
			fprintf(logf, "metal_sys_init: open(/proc/self/pagemap) failed: %s\n", strerror(errno));
			fflush(logf);
		}
	}
	_metal.pagemap_fd = result;
	if (logf) {
		fprintf(logf, "metal_sys_init: pagemap_fd=%d\n", _metal.pagemap_fd);
		fflush(logf);
	}

	/* Probe around the unused-parameter suppression to capture the violation scenario. */
	if (logf) {
		fprintf(logf, "metal_sys_init: before metal_unused(params) params=%p (is_null=%d)\n",
			(const void *)params, params == NULL);
		fflush(logf);
	}

	/* Original suppression of unused-parameter warning; kept to preserve behavior. */
	metal_unused(params);

	if (logf) {
		/* Log after the suppression to show the assignment (if the macro expands to one) was executed. */
		fprintf(logf, "metal_sys_init: after metal_unused(params) params=%p (is_null=%d)\n",
			(const void *)params, params == NULL);
		fflush(logf);
	}

	/* Initialize IRQ handling */
	metal_linux_irq_init();
	if (logf) {
		fprintf(logf, "metal_sys_init: completed, returning 0\n");
		fclose(logf);
	}
	return 0;
}
