int metal_sys_init(const struct metal_init_params *params)
{
	const char *tmp_path;
	unsigned int seed;
	FILE *urandom;
	int result;
	size_t read_bytes;
	FILE *logf = NULL;

	/* Try to ensure log directory exists for reproducible logging. */
	/* Use system() to create the directory if possible; ignore errors. */
	(void)system("mkdir -p /tmp/logs");
	logf = fopen("/tmp/logs/725.txt", "a");
	if (logf) {
		fprintf(logf, "metal_sys_init: entry params=%p\n", (void *)params);
		fflush(logf);
	}

	/* Find the temporary directory location. */
	tmp_path = getenv("TMPDIR");
	if (!tmp_path)
		tmp_path = "/tmp";
	if (logf) {
		fprintf(logf, "metal_sys_init: tmp_path resolved to '%s' (ptr=%p)\n",
			tmp_path ? tmp_path : "(null)", (const void *)tmp_path);
	}

	/* Assignment that may discard const qualifier if _metal.tmp_path is non-const. */
	_metal.tmp_path = tmp_path;
	if (logf) {
		/* Log the stored pointer and string to provide evidence of the assignment. */
		fprintf(logf, "metal_sys_init: _metal.tmp_path after assign = '%s' (ptr=%p)\n",
			_metal.tmp_path ? _metal.tmp_path : "(null)", (const void *)_metal.tmp_path);
		fflush(logf);
	}

	/* Initialize the pseudo-random number generator. */
	urandom = fopen("/dev/urandom", "r");
	if (!urandom) {
		if (logf) {
			fprintf(logf, "metal_sys_init: failed to open /dev/urandom (%s)\n", strerror(errno));
			fflush(logf);
		}
		metal_log(METAL_LOG_ERROR, "failed to open /dev/urandom (%s)\n", strerror(errno));
		if (logf)
			fclose(logf);
		return -errno;
	}

	read_bytes = fread(&seed, 1, sizeof(seed), urandom);
	if (logf) {
		fprintf(logf, "metal_sys_init: fread returned %zu bytes\n", read_bytes);
	}
	if (read_bytes <= 0) {
		if (logf) {
			fprintf(logf, "metal_sys_init: Failed fread /dev/urandom\n");
			fflush(logf);
		}
		metal_log(METAL_LOG_DEBUG, "Failed fread /dev/urandom\n");
	}
	if (logf) {
		fprintf(logf, "metal_sys_init: seed value read = %u\n", seed);
		fflush(logf);
	}
	fclose(urandom);
	srand(seed);

	result = metal_init_page_sizes();
	if (logf) {
		fprintf(logf, "metal_sys_init: metal_init_page_sizes result = %d\n", result);
		fflush(logf);
	}
	if (result < 0) {
		if (logf) fclose(logf);
		return result;
	}

	result = metal_linux_bus_init();
	if (logf) {
		fprintf(logf, "metal_sys_init: metal_linux_bus_init result = %d\n", result);
		fflush(logf);
	}
	if (result < 0) {
		if (logf) fclose(logf);
		return result;
	}

	result = open("/proc/self/pagemap", O_RDONLY | O_CLOEXEC);
	if (logf) {
		if (result < 0)
			fprintf(logf, "metal_sys_init: Failed pagemap open - %s (fd=%d)\n", strerror(errno), result);
		else
			fprintf(logf, "metal_sys_init: pagemap opened fd=%d\n", result);
		fflush(logf);
	}
	_metal.pagemap_fd = result;
	if (logf) {
		fprintf(logf, "metal_sys_init: _metal.pagemap_fd = %d\n", _metal.pagemap_fd);
		fflush(logf);
	}

	metal_unused(params);

	/* Initialize IRQ handling */
	metal_linux_irq_init();
	if (logf) {
		fprintf(logf, "metal_sys_init: metal_linux_irq_init completed\n");
		fclose(logf);
	}
	return 0;
}
