int metal_tests_run(struct metal_init_params *params)
{
	struct metal_init_params dparams = METAL_INIT_DEFAULTS;
	struct metal_test_case *test_case;
	struct metal_list *node;
	int error, errors = 0;
	const char *dots = "..................................";
	const char *pad;
	FILE *log = NULL;
	size_t dots_len;

	if (!params)
		params = &dparams;

	params->log_level = METAL_LOG_DEBUG;
	error = metal_init(params);
	if (error)
		return error;

	/* Open log file for append. This captures control flow and state for
	   reproducing the potential out-of-bounds scenario described. */
	log = fopen("/tmp/logs/433.txt", "a");
	dots_len = strlen(dots);
	if (log) {
		fprintf(log, "ENTER metal_tests_run: params=%p\n", (void *)params);
		fprintf(log, "dots='%s' dots_len=%zu\n", dots, dots_len);
		fflush(log);
	}

	metal_list_for_each(&test_cases, node) {
		test_case = metal_container_of(node, struct metal_test_case,
				       node);

		/* Compute name length exactly as in the original code. This mirrors
		   the same operations that can lead to the reported violation. */
		size_t name_len = strlen(test_case->name);
		pad = dots + name_len; /* potential OOB pointer if name_len > dots_len */

		/* Log the computed values and whether pad appears to be out-of-bounds.
		   Note: we intentionally do not dereference pad when it is out-of-bounds
		   to avoid creating a new undefined-behavior; we only log the pointer
		   value and the comparison that indicates a risky condition. */
		if (log) {
			fprintf(log, "TEST CASE: name='%s' name_len=%zu pad_ptr=%p pad_offset=%zu dots_len=%zu\n",
				    test_case->name, name_len, (void *)pad, name_len, dots_len);
			fprintf(log, "PAD within dots? %s\n", (name_len <= dots_len) ? "YES" : "NO (OUT OF BOUNDS)");
			fflush(log);
		}

		metal_log(METAL_LOG_INFO,"running [%s]\n", test_case->name);
		error = test_case->test();

		/* The original code passes 'pad' to metal_log using "%s". Keep the
		   original call to preserve program behavior (and thus reproduce the
		   scenario). The separate log file already records whether 'pad' was
		   computed to be out-of-bounds. */
		metal_log(METAL_LOG_INFO,"result [%s]%s %s%s%s\n",
		       test_case->name, pad,
		       error ? "fail" : "pass",
		       error ? " - error: " : "",
		       error ? strerror(-error) : "");

		if (log) {
			fprintf(log, "RESULT for '%s': error=%d\n", test_case->name, error);
			fflush(log);
		}

		if (error)
			errors++;
	}

	if (log) {
		fprintf(log, "LEAVE metal_tests_run: errors=%d\n", errors);
		fclose(log);
	}

	metal_finish();

	return errors;
}
