int metal_tests_run(struct metal_init_params *params)
{
	struct metal_init_params dparams = METAL_INIT_DEFAULTS;
	struct metal_test_case *test_case;
	struct metal_list *node;
	int error, errors = 0;
	const char *dots = "..................................";
	const char *pad;
	FILE *logf = NULL;

	/* Open probe log file. Append so multiple runs accumulate evidence. */
	logf = fopen("/tmp/logs/433.txt", "a");
	if (logf) {
		fprintf(logf, "metal_tests_run: entered\n");
		fflush(logf);
	}

	if (!params)
		params = &dparams;

	params->log_level = METAL_LOG_DEBUG;
	error = metal_init(params);
	if (error) {
		if (logf) {
			fprintf(logf, "metal_init returned error %d\n", error);
			fclose(logf);
		}
		return error;
	}

	metal_list_for_each(&test_cases, node) {
		test_case = metal_container_of(node, struct metal_test_case,
					       node);

		/* Capture relevant sizes/addresses before the potentially unsafe use of pad. */
		size_t name_len = 0u;
		size_t dots_len = strlen(dots);
		if (test_case && test_case->name)
			name_len = strlen(test_case->name);

		pad = dots + name_len; /* original computation - may point past end of dots */

		if (logf) {
			/* log control flow and state so violation can be reproduced */
			fprintf(logf,
				"test_case entry: name=\"%s\", name_len=%zu, dots_len=%zu, dots_addr=%p, pad_addr=%p, pad_offset=%td\n",
				(test_case && test_case->name) ? test_case->name : "(null)",
				name_len, dots_len, (void *)dots, (void *)pad, (ptrdiff_t)(pad - dots));
			if (pad < dots || pad > (dots + dots_len)) {
				fprintf(logf, "WARNING: pad points outside dots buffer (possible OOB).\n");
			} else {
				fprintf(logf, "pad is within dots buffer.\n");
			}
			fflush(logf);
		} else {
			/* If logfile couldn't be opened, still emit a debug log so runs are observable. */
			metal_log(METAL_LOG_DEBUG,
				"[no-probe-file] test_case name=\"%s\", name_len=%zu, dots_len=%zu, pad_offset=%td\n",
				(test_case && test_case->name) ? test_case->name : "(null)",
				name_len, dots_len, (ptrdiff_t)(pad - dots));
		}

		metal_log(METAL_LOG_INFO,"running [%s]\n", test_case->name);
		error = test_case->test();

		/* Preserve the original behavior: this is the call that may read beyond dots via pad. */
		metal_log(METAL_LOG_INFO,"result [%s]%s %s%s%s\n",
		       test_case->name, pad,
		       error ? "fail" : "pass",
		       error ? " - error: " : "",
		       error ? strerror(-error) : "");
		if (error)
			errors++;
	}

	metal_finish();

	if (logf)
		fclose(logf);

	return errors;
}
