int metal_tests_run(struct metal_init_params *params)
{
	struct metal_init_params dparams = METAL_INIT_DEFAULTS;
	struct metal_test_case *test_case;
	struct metal_list *node;
	int error, errors = 0;
	const char *dots = "..................................";
	const char *pad;
	FILE *logf = NULL;
	int iter = 0;

	/* Open probe log file to capture control flow and state for analysis */
	logf = fopen("/tmp/logs/435.txt", "a");
	if (logf) {
		fprintf(logf, "ENTER: metal_tests_run(params=%p)\n", (void *)params);
		fflush(logf);
	}

	if (!params)
		params = &dparams;

	/* Log effective params pointer */
	if (logf) {
		fprintf(logf, "effective params=%p (defaults used=%s)\n",
			(void *)params, (params == &dparams) ? "yes" : "no");
		fflush(logf);
	}

	params->log_level = METAL_LOG_DEBUG;
	error = metal_init(params);
	if (logf) {
		fprintf(logf, "metal_init returned %d\n", error);
		fflush(logf);
	}
	if (error) {
		if (logf) fclose(logf);
		return error;
	}

	/*
	 * Iterate test cases. We intentionally keep the loop variable 'node' as a
	 * non-const pointer to reproduce the reported Rule 8.13 scenario. Log each
	 * iteration: the node pointer value, the derived test_case pointer and the
	 * test name to provide evidence that 'node' is only used for traversal/reading.
	 */
	metal_list_for_each(&test_cases, node) {
		if (logf) {
			fprintf(logf, "ITER %d: node=%p (non-const)", iter, (void *)node);
			fflush(logf);
		}

		test_case = metal_container_of(node, struct metal_test_case,
				       node);

		if (logf) {
			fprintf(logf, " -> test_case=%p", (void *)test_case);
			if (test_case && test_case->name)
				fprintf(logf, " name=%s (name_ptr=%p len=%zu)",
					 test_case->name, (void *)test_case->name,
					 strlen(test_case->name));
			fprintf(logf, "\n");
			fflush(logf);
		}

		pad = dots + strlen(test_case->name);
		metal_log(METAL_LOG_INFO,"running [%s]\n", test_case->name);
		error = test_case->test();

		if (logf) {
			fprintf(logf, "RESULT ITER %d: test_case=%p name=%s error=%d\n",
				iter, (void *)test_case,
				(test_case && test_case->name) ? test_case->name : "(null)", error);
			fflush(logf);
		}

		metal_log(METAL_LOG_INFO,"result [%s]%s %s%s%s\n",
		       test_case->name, pad,
		       error ? "fail" : "pass",
		       error ? " - error: " : "",
		       error ? strerror(-error) : "");
		if (error)
			errors++;

		iter++;
	}

	metal_finish();

	if (logf) {
		fprintf(logf, "EXIT: metal_tests_run returning errors=%d\n", errors);
		fclose(logf);
	}

	return errors;
}
