int metal_tests_run(struct metal_init_params *params)
{
	struct metal_init_params dparams = METAL_INIT_DEFAULTS;
	struct metal_test_case *test_case;
	struct metal_list *node;
	int error, errors = 0;
	const char *dots = "..................................";
	const char *pad;
	FILE *probe = NULL;

	/* Open probe log. Append so multiple runs accumulate evidence. */
	probe = fopen("/tmp/logs/435.txt", "a");
	if (probe) {
		fprintf(probe, "-- enter metal_tests_run --\n");
		fflush(probe);
	} else {
		/* If probe can't be opened, emit a runtime log so there is still trace. */
		metal_log(METAL_LOG_WARN, "warning: could not open probe log /tmp/logs/435.txt\n");
	}

	if (!params)
		params = &dparams;

	/* Log incoming params pointer and initial log_level if probe available. */
	if (probe) {
		fprintf(probe, "params=%p, params->log_level=%d\n", (void *)params, (int)params->log_level);
		fflush(probe);
	}

	params->log_level = METAL_LOG_DEBUG;
	error = metal_init(params);

	/* Log result of metal_init and early return if any. */
	if (probe) {
		fprintf(probe, "metal_init returned %d\n", error);
		fflush(probe);
	}
	if (error) {
		if (probe) {
			fprintf(probe, "-- exit metal_tests_run (early return due to init error) error=%d --\n", error);
			fclose(probe);
		}
		return error;
	}

	/* Iterate through test cases. Record the iterator pointer (node) and test_case details.
	   This captures the control flow and the addresses involved in traversal which can be
	   used as evidence for static analysis observations about the iterator's usage. */
	metal_list_for_each(&test_cases, node) {
		test_case = metal_container_of(node, struct metal_test_case,
				       node);

		/* Log node and test_case pointers and name. */
		if (probe) {
			fprintf(probe, "iter: node=%p, test_case=%p, name='%s'\n",
				(node), (void *)test_case,
				(test_case && test_case->name) ? test_case->name : "(null)");
			fflush(probe);
		}

		pad = dots + strlen(test_case->name);
		metal_log(METAL_LOG_INFO,"running [%s]\n", test_case->name);

		/* Log that we are about to call the test function and the function pointer. */
		if (probe) {
			fprintf(probe, "calling test() at %p for '%s'\n",
				(void *)(uintptr_t)test_case->test,
				(test_case->name ? test_case->name : "(null)"));
			fflush(probe);
		}

		error = test_case->test();

		/* Log result of the test. */
		if (probe) {
			fprintf(probe, "result for '%s': %s (error=%d)\n",
				(test_case->name ? test_case->name : "(null)"),
				error ? "fail" : "pass",
				error);
			if (error)
				fprintf(probe, "strerror: %s\n", strerror(-error));
			fflush(probe);
		}

		metal_log(METAL_LOG_INFO,"result [%s]%s %s%s%s\n",
		       test_case->name, pad,
		       error ? "fail" : "pass",
		       error ? " - error: " : "",
		       error ? strerror(-error) : "");
		if (error)
			errors++;
	}

	metal_finish();

	if (probe) {
		fprintf(probe, "metal_finish called, total errors=%d\n", errors);
		fprintf(probe, "-- exit metal_tests_run --\n");
		fclose(probe);
	}

	return errors;
}
