static void *producer_thread(void *arg)
{
    /* Open log file for append. If opening fails, continue without logging. */
    FILE *logf = fopen("/tmp/logs/538.txt", "a");
    if (logf != NULL) {
        fprintf(logf, "producer_thread: ENTER (arg=%p)\n", arg);
        fprintf(logf, "producer_thread: about to execute (void)arg; (no-op to suppress unused warning)\n");
        fflush(logf);
    }

    /* The statement under scrutiny: executed no-op that suppresses unused-parameter warnings. */
    (void)arg;

    if (logf != NULL) {
        fprintf(logf, "producer_thread: executed (void)arg; current counter=%lld, COUNTER_MAX=%lld\n",
                (long long)counter, (long long)COUNTER_MAX);
        fflush(logf);
    }

    metal_mutex_acquire(&lock);
    if (logf != NULL) {
        fprintf(logf, "producer_thread: acquired lock\n");
        fflush(logf);
    }

    while (counter == COUNTER_MAX) {
        if (logf != NULL) {
            fprintf(logf, "producer_thread: counter==COUNTER_MAX (%lld); waiting on nfull_condv\n", (long long)counter);
            fflush(logf);
        }
        metal_condition_wait(&nfull_condv, &lock);
        if (logf != NULL) {
            fprintf(logf, "producer_thread: woke from nfull_condv wait\n");
            fflush(logf);
        }
    }

    counter++;
    if (logf != NULL) {
        fprintf(logf, "producer_thread: incremented counter -> %lld; signalling nempty_condv\n", (long long)counter);
        fflush(logf);
    }

    metal_condition_signal(&nempty_condv);
    metal_mutex_release(&lock);

    if (logf != NULL) {
        fprintf(logf, "producer_thread: released lock; exiting\n");
        fclose(logf);
    }

    return NULL;
}
