static void *shmem_child(void *arg)
{
	const char *name = arg;
	struct {
		metal_mutex_t	mutex;
		int			counter;
	} *virt;
	struct metal_io_region *io;
	unsigned long phys;
	size_t size = 1 * 1024 * 1024;
	int error;
	FILE *logf = fopen("/tmp/logs/7.txt", "a");

	if (logf) {
		fprintf(logf, "ENTER shmem_child: arg=%p name=%s size=%zu\n", arg, name ? name : "(null)", size);
	} else {
		/* If log file can't be opened, emit a runtime log so test harness still sees something. */
		metal_log(METAL_LOG_INFO, "shmem_child: could not open log file /tmp/logs/7.txt\n");
	}

	error = metal_shmem_open(name, size, &io);
	if (logf) fprintf(logf, "metal_shmem_open returned: %d io=%p\n", error, (void *)io);
	if (error) {
		metal_log(METAL_LOG_ERROR, "Failed shmem_open: %d.\n", error);
		if (logf) fprintf(logf, "ERROR: Failed shmem_open: %d - incrementing nb_err and returning\n", error);
		atomic_fetch_add(&nb_err, 1);
		if (logf) {
			fclose(logf);
		}
		return NULL;
	}

	virt = metal_io_virt(io, 0);
	phys = metal_io_phys(io, 0);
	if (logf) fprintf(logf, "After mapping: virt=%p phys=%lu\n", (void *)virt, phys);
	if (phys != METAL_BAD_OFFSET) {
		if (virt != metal_io_phys_to_virt(io, phys)) {
			atomic_fetch_add(&nb_err, 1);
			metal_log(METAL_LOG_ERROR, "Failed virt != phys.\n");
			if (logf) fprintf(logf, "CHECK FAILED: virt != metal_io_phys_to_virt(io, phys) (virt=%p phys_to_virt=%p)\n", (void *)virt, (void *)metal_io_phys_to_virt(io, phys));
		}
		if (phys != metal_io_virt_to_phys(io, virt)) {
			atomic_fetch_add(&nb_err, 1);
			metal_log(METAL_LOG_ERROR, "Failed phys != virt.\n");
			if (logf) fprintf(logf, "CHECK FAILED: phys != metal_io_virt_to_phys(io, virt) (phys=%lu virt_to_phys=%lu)\n", phys, metal_io_virt_to_phys(io, virt));
		}
	} else {
		if (logf) fprintf(logf, "phys == METAL_BAD_OFFSET (no physical mapping)\n");
	}

	metal_io_finish(io);
	if (logf) {
		fprintf(logf, "metal_io_finish called, exiting shmem_child\n");
		fclose(logf);
	}
	return NULL;
}
