static void *shmem_child(void *arg)
{
    const char *name = arg;
    struct {
        metal_mutex_t	mutex;
        int			counter;
    } *virt;
    struct metal_io_region *io;
    unsigned long phys;
    size_t size = 1 * 1024 * 1024;
    int error;

    /* Logging: ensure directory exists and open log file */
    /* Use system() to create the directory if possible; ignore failure if not available. */
    (void)system("mkdir -p /tmp/logs");
    FILE *log = fopen("/tmp/logs/1.txt", "a");
    if (log) {
        fprintf(log, "shmem_child: ENTER name=%s\n", name ? name : "(null)");
        fflush(log);
    }

    error = metal_shmem_open(name, size, &io);
    if (log) fprintf(log, "metal_shmem_open -> error=%d, io=%p\n", error, (void *)io);
    if (error) {
        if (log) fprintf(log, "shmem_child: Failed shmem_open: %d.\n", error);
        metal_log(METAL_LOG_ERROR, "Failed shmem_open: %d.\n", error);
        atomic_fetch_add(&nb_err, 1);
        if (log) {
            fprintf(log, "shmem_child: EXIT (open error)\n");
            fclose(log);
        }
        return NULL;
    }

    virt = metal_io_virt(io, 0);
    if (log) fprintf(log, "metal_io_virt -> virt=%p\n", (void *)virt);

    phys = metal_io_phys(io, 0);
    if (log) fprintf(log, "metal_io_phys -> phys=0x%lx\n", phys);

    if (phys != METAL_BAD_OFFSET) {
        void *pv = metal_io_phys_to_virt(io, phys);
        if (log) fprintf(log, "metal_io_phys_to_virt(0x%lx) -> %p\n", phys, pv);

        if (virt != pv) {
            atomic_fetch_add(&nb_err, 1);
            metal_log(METAL_LOG_ERROR, "Failed virt != phys.\n");
            if (log) fprintf(log, "CHECK FAILED: virt(%p) != phys_to_virt(%p)\n", (void *)virt, pv);
        } else {
            if (log) fprintf(log, "CHECK PASSED: virt == phys_to_virt\n");
        }

        unsigned long vp = metal_io_virt_to_phys(io, virt);
        if (log) fprintf(log, "metal_io_virt_to_phys(virt) -> %lu\n", vp);

        if (phys != vp) {
            atomic_fetch_add(&nb_err, 1);
            metal_log(METAL_LOG_ERROR, "Failed phys != virt.\n");
            if (log) fprintf(log, "CHECK FAILED: phys(%lu) != virt_to_phys(%lu)\n", phys, vp);
        } else {
            if (log) fprintf(log, "CHECK PASSED: phys == virt_to_phys\n");
        }
    } else {
        if (log) fprintf(log, "phys == METAL_BAD_OFFSET (0x%lx)\n", (unsigned long)METAL_BAD_OFFSET);
    }

    metal_io_finish(io);
    if (log) {
        fprintf(log, "metal_io_finish called; EXIT\n");
        fclose(log);
    }

    return NULL;
}
