static void *shmem_child(void *arg)
{
	const char *name = arg;
	struct {
		metal_mutex_t	mutex;
		int			counter;
	} *virt;
	struct metal_io_region *io;
	unsigned long phys;
	size_t size = 1 * 1024 * 1024;
	int error;
	FILE *fp;

	/* Probe: log entry and initial state */
	fp = fopen("/tmp/logs/2.txt", "a");
	if (fp) {
		fprintf(fp, "ENTER shmem_child: name=%s, initial_size=%zu, &size=%p\n",
			name ? name : "(null)", (size_t)size, (void *)&size);
		fclose(fp);
	}

	error = metal_shmem_open(name, size, &io);

	/* Probe: log after metal_shmem_open call to capture control flow and state */
	fp = fopen("/tmp/logs/2.txt", "a");
	if (fp) {
		fprintf(fp, "AFTER metal_shmem_open: error=%d, size=%zu, io=%p\n",
			error, (size_t)size, (void *)io);
		fclose(fp);
	}

	if (error) {
		metal_log(METAL_LOG_ERROR, "Failed shmem_open: %d.\n", error);
		atomic_fetch_add(&nb_err, 1);
		/* Log early exit */
		fp = fopen("/tmp/logs/2.txt", "a");
		if (fp) {
			fprintf(fp, "EXIT shmem_child: shmem_open failed, error=%d\n", error);
			fclose(fp);
		}
		return NULL;
	}

	virt = metal_io_virt(io, 0);
	phys = metal_io_phys(io, 0);

	/* Log results of virt/phys mapping and subsequent checks */
	fp = fopen("/tmp/logs/2.txt", "a");
	if (fp) {
		fprintf(fp, "virt=%p, phys=%lu\n", (void *)virt, (unsigned long)phys);
		if (phys != METAL_BAD_OFFSET) {
			void *p2 = metal_io_phys_to_virt(io, phys);
			unsigned long p3 = metal_io_virt_to_phys(io, virt);
			fprintf(fp, "phys != BAD_OFFSET: phys=%lu, phys_to_virt=%p, virt_to_phys=%lu\n",
				(unsigned long)phys, p2, p3);

			if (virt != p2) {
				fprintf(fp, "CHECK FAILED: virt (%p) != metal_io_phys_to_virt(io, phys) (%p)\n",
					(void *)virt, p2);
			}
			if (phys != p3) {
				fprintf(fp, "CHECK FAILED: phys (%lu) != metal_io_virt_to_phys(io, virt) (%lu)\n",
					(unsigned long)phys, p3);
			}
		}
		fclose(fp);
	}

	/* Preserve original finishing behavior */
	metal_io_finish(io);

	/* Log normal exit */
	fp = fopen("/tmp/logs/2.txt", "a");
	if (fp) {
		fprintf(fp, "EXIT shmem_child: completed normally, name=%s\n", name ? name : "(null)");
		fclose(fp);
	}

	return NULL;
}
