static void *spinlock_thread(void *arg)
{
	struct metal_spinlock *l = arg;
	int i;
	FILE *f = fopen("/tmp/logs/388.txt", "a");
	if (!f) {
		/* fallback if directory doesn't exist */
		f = fopen("/tmp/388.txt", "a");
	}
	if (f) {
		fprintf(f, "spinlock_thread ENTRY: arg=%p, spinlock_test_count=%d\n", arg, spinlock_test_count);
		fflush(f);
	}

	for (i = 0; i < spinlock_test_count; i++) {
		metal_spinlock_acquire(l);

		if (f) {
			fprintf(f, "thread %p: acquired lock %p, iteration=%d\n", arg, (void *)l, i);
			fflush(f);
		}

		/* Original operation preserved */
		total++;

		if (f) {
			fprintf(f, "thread %p: performed total++ (iteration=%d)\n", arg, i);
			fflush(f);
		}

		metal_spinlock_release(l);

		if (f) {
			fprintf(f, "thread %p: released lock %p, iteration=%d\n", arg, (void *)l, i);
			fflush(f);
		}
	}

	if (f) {
		fprintf(f, "spinlock_thread EXIT: arg=%p\n", arg);
		fclose(f);
	}

	return NULL;
}
