void
MHD_MD5_update (struct Md5Ctx *ctx,
                const uint8_t *data,
                size_t length)
{
  unsigned int bytes_have; /**< Number of bytes in the context buffer */
  FILE *logf = NULL;

  /* Try to open log file. If opening fails, just skip logging. */
  logf = fopen("/tmp/logs/312.txt", "a");
  if (logf != NULL)
  {
    fprintf(logf, "MHD_MD5_update: entered\n");
    fprintf(logf, "  ctx=%p, data=%p, length=%zu\n", (void *)ctx, (const void *)data, length);
    /* Attempt to print current count if available */
    fprintf(logf, "  ctx->count (before) = %zu\n", (size_t)ctx->count);
    fflush(logf);
  }

  mhd_assert ((data != NULL) || (length == 0));

#ifndef MHD_FAVOR_SMALL_CODE
  if (0 == length)
  {
    if (logf)
    {
      fprintf(logf, "  early return: length == 0\n");
      fclose(logf);
      logf = NULL;
    }
    return; /* Shortcut, do nothing */
  }
#endif /* MHD_FAVOR_SMALL_CODE */

  /* Note: (count & (MD5_BLOCK_SIZE-1))
           equals (count % MD5_BLOCK_SIZE) for this block size. */
  bytes_have = (unsigned int) (ctx->count & (MD5_BLOCK_SIZE - 1));

  if (logf)
  {
    fprintf(logf, "  bytes_have = %u\n", bytes_have);
    fflush(logf);
  }

  ctx->count += length;

  if (logf)
  {
    fprintf(logf, "  ctx->count (after += length) = %zu\n", (size_t)ctx->count);
    fflush(logf);
  }

  if (0 != bytes_have)
  {
    unsigned int bytes_left = MD5_BLOCK_SIZE - bytes_have;
    if (logf)
    {
      fprintf(logf, "  bytes_left = %u\n", bytes_left);
      fprintf(logf, "  branch: bytes_have != 0, length (before) = %zu\n", length);
      fflush(logf);
    }
    if (length >= bytes_left)
    {     /* Combine new data with data in the buffer and
             process the full block. */
      if (logf)
      {
        fprintf(logf, "  entering combine-with-buffer branch: length (%zu) >= bytes_left (%u)\n", length, bytes_left);
        fflush(logf);
      }
      memcpy (((uint8_t *) ctx->buffer) + bytes_have,
              data,
              bytes_left);
      data += bytes_left;

      if (logf)
      {
        fprintf(logf, "  data advanced by bytes_left -> data=%p\n", (const void *)data);
        fflush(logf);
      }

      /* Here the original code modifies the function parameter 'length'. */
      if (logf)
      {
        fprintf(logf, "  length (before -= bytes_left) = %zu\n", length);
        fflush(logf);
      }
      length -= bytes_left;
      if (logf)
      {
        fprintf(logf, "  length (after -= bytes_left) = %zu\n", length);
        fflush(logf);
      }

      md5_transform (ctx->H, ctx->buffer);
      bytes_have = 0;

      if (logf)
      {
        fprintf(logf, "  completed md5_transform on combined block; bytes_have reset to 0\n");
        fflush(logf);
      }
    }
    else
    {
      if (logf)
      {
        fprintf(logf, "  not enough new data to fill buffer; length (%zu) < bytes_left (%u)\n", length, bytes_left);
        fflush(logf);
      }
    }
  }

  while (MD5_BLOCK_SIZE <= length)
  {   /* Process any full blocks of new data directly,
         without copying to the buffer. */
    if (logf)
    {
      fprintf(logf, "  while-loop: processing full block: length (before) = %zu\n", length);
      fflush(logf);
    }
    md5_transform (ctx->H, data);
    data += MD5_BLOCK_SIZE;

    if (logf)
    {
      fprintf(logf, "  while-loop: data advanced by MD5_BLOCK_SIZE -> data=%p\n", (const void *)data);
      fflush(logf);
    }

    /* Here the original code modifies the function parameter 'length'. */
    if (logf)
    {
      fprintf(logf, "  length (before -= MD5_BLOCK_SIZE) = %zu\n", length);
      fflush(logf);
    }
    length -= MD5_BLOCK_SIZE;
    if (logf)
    {
      fprintf(logf, "  length (after -= MD5_BLOCK_SIZE) = %zu\n", length);
      fflush(logf);
    }
  }

  if (0 != length)
  {   /* Copy incomplete block of new data (if any)
         to the buffer. */
    if (logf)
    {
      fprintf(logf, "  final copy: remaining length = %zu, bytes_have = %u\n", length, bytes_have);
      fflush(logf);
    }
    memcpy (((uint8_t *) ctx->buffer) + bytes_have, data, length);
  }

  if (logf)
  {
    fprintf(logf, "MHD_MD5_update: exit (final length=%zu)\n", length);
    fprintf(logf, "  ctx=%p, ctx->count=%zu\n", (void *)ctx, (size_t)ctx->count);
    fclose(logf);
    logf = NULL;
  }
}
