void
MHD_MD5_update (struct Md5Ctx *ctx,
                const uint8_t *data,
                size_t length)
{
#include <stdio.h>
  unsigned int bytes_have; /**< Number of bytes in the context buffer */
  /* Log file for capturing control flow and state to demonstrate the
     pointer-parameter modification (violation scenario). */
  FILE *log = fopen("/tmp/logs/312.txt", "a");

  if (log) {
    fprintf(log, "MHD_MD5_update: entry ctx=%p data=%p length=%llu count_before=%llu\n",
            (void *) ctx,
            (void *) data,
            (unsigned long long) length,
            (unsigned long long) ctx->count);
  }

  mhd_assert ((data != NULL) || (length == 0));

#ifndef MHD_FAVOR_SMALL_CODE
  if (0 == length)
  {
    if (log)
      fprintf(log, "MHD_MD5_update: early return because length == 0\n");
    if (log)
      fclose(log);
    return; /* Shortcut, do nothing */
  }
#endif /* MHD_FAVOR_SMALL_CODE */

  /* Note: (count & (MD5_BLOCK_SIZE-1))
           equals (count % MD5_BLOCK_SIZE) for this block size. */
  bytes_have = (unsigned int) (ctx->count & (MD5_BLOCK_SIZE - 1));
  if (log)
    fprintf(log, "bytes_have=%u\n", bytes_have);

  ctx->count += length;
  if (log)
    fprintf(log, "count_after_add=%llu\n", (unsigned long long) ctx->count);

  if (0 != bytes_have)
  {
    unsigned int bytes_left = MD5_BLOCK_SIZE - bytes_have;
    if (log)
      fprintf(log, "bytes_left=%u length=%llu\n", bytes_left, (unsigned long long) length);
    if (length >= bytes_left)
    {     /* Combine new data with data in the buffer and
             process the full block. */
      if (log)
        fprintf(log, "memcpy to buffer at offset %u from data=%p bytes_left=%u\n",
                bytes_have, (void *) data, bytes_left);
      memcpy (((uint8_t *) ctx->buffer) + bytes_have,
              data,
              bytes_left);
      if (log)
        fprintf(log, "About to modify pointer parameter 'data' (violation): before=%p\n",
                (void *) data);
      data += bytes_left;
      if (log)
        fprintf(log, "Pointer parameter 'data' modified: after=%p\n",
                (void *) data);
      length -= bytes_left;
      if (log)
        fprintf(log, "length after subtract=%llu\n", (unsigned long long) length);
      md5_transform (ctx->H, ctx->buffer);
      bytes_have = 0;
    }
  }

  while (MD5_BLOCK_SIZE <= length)
  {   /* Process any full blocks of new data directly,
         without copying to the buffer. */
    if (log)
      fprintf(log, "md5_transform on data=%p length=%llu (processing full block)\n",
              (void *) data,
              (unsigned long long) length);
    md5_transform (ctx->H, data);
    if (log)
      fprintf(log, "About to modify pointer parameter 'data' by MD5_BLOCK_SIZE: before=%p\n",
              (void *) data);
    data += MD5_BLOCK_SIZE;
    if (log)
      fprintf(log, "Pointer parameter 'data' modified: after=%p\n",
              (void *) data);
    length -= MD5_BLOCK_SIZE;
    if (log)
      fprintf(log, "length after subtracting MD5_BLOCK_SIZE=%llu\n", (unsigned long long) length);
  }

  if (0 != length)
  {   /* Copy incomplete block of new data (if any)
         to the buffer. */
    if (log)
      fprintf(log, "Copy remaining partial block: length=%llu bytes_have=%u data=%p\n",
              (unsigned long long) length, bytes_have, (void *) data);
    memcpy (((uint8_t *) ctx->buffer) + bytes_have, data, length);
  }

  if (log)
    fclose(log);
}
