void
MHD_MD5_update (struct Md5Ctx *ctx,
                const uint8_t *data,
                size_t length)
{
  unsigned int bytes_have; /**< Number of bytes in the context buffer */
  FILE *___mhd_log = fopen("/tmp/logs/312.txt", "a");

  if (___mhd_log)
    fprintf(___mhd_log, "ENTER MHD_MD5_update: ctx=%p data=%p length=%zu\n", (void *) ctx, (void *) data, length);

  mhd_assert ((data != NULL) || (length == 0));

#ifndef MHD_FAVOR_SMALL_CODE
  if (0 == length)
  {
    if (___mhd_log)
      fprintf(___mhd_log, "early return: length == 0\n");
    if (___mhd_log)
      fclose(___mhd_log);
    return; /* Shortcut, do nothing */
  }
#endif /* MHD_FAVOR_SMALL_CODE */

  /* Note: (count & (MD5_BLOCK_SIZE-1))
           equals (count % MD5_BLOCK_SIZE) for this block size. */
  bytes_have = (unsigned int) (ctx->count & (MD5_BLOCK_SIZE - 1));
  if (___mhd_log)
    fprintf(___mhd_log, "bytes_have=%u ctx->count(before)=%zu\n", bytes_have, ctx->count);

  ctx->count += length;
  if (___mhd_log)
    fprintf(___mhd_log, "ctx->count(after)=%zu (added length=%zu)\n", ctx->count, length);

  if (0 != bytes_have)
  {
    unsigned int bytes_left = MD5_BLOCK_SIZE - bytes_have;
    if (___mhd_log)
      fprintf(___mhd_log, "incomplete buffer: bytes_left=%u length=%zu\n", bytes_left, length);

    if (length >= bytes_left)
    {     /* Combine new data with data in the buffer and
             process the full block. */
      if (___mhd_log)
        fprintf(___mhd_log, "filling buffer: will copy %u bytes from data=%p to buffer+%u\n", bytes_left, (void *) data, bytes_have);

      memcpy (((uint8_t *) ctx->buffer) + bytes_have,
              data,
              bytes_left);

      data += bytes_left;
      if (___mhd_log)
        fprintf(___mhd_log, "data pointer advanced by %u, new data=%p\n", bytes_left, (void *) data);

      length -= bytes_left; /* <-- modification of the by-value parameter 'length' (violation scenario) */
      if (___mhd_log)
        fprintf(___mhd_log, "length decreased by %u, new length=%zu\n", bytes_left, length);

      md5_transform (ctx->H, ctx->buffer);
      if (___mhd_log)
        fprintf(___mhd_log, "md5_transform on buffer done\n");

      bytes_have = 0;
      if (___mhd_log)
        fprintf(___mhd_log, "bytes_have reset to %u\n", bytes_have);
    }
    else
    {
      if (___mhd_log)
        fprintf(___mhd_log, "not enough to fill buffer: need %u, have %zu\n", bytes_left, length);
    }
  }

  while (MD5_BLOCK_SIZE <= length)
  {   /* Process any full blocks of new data directly,
         without copying to the buffer. */
    if (___mhd_log)
      fprintf(___mhd_log, "processing full block: data=%p length=%zu\n", (void *) data, length);

    md5_transform (ctx->H, data);

    data += MD5_BLOCK_SIZE;
    if (___mhd_log)
      fprintf(___mhd_log, "advanced data by %d, new data=%p\n", MD5_BLOCK_SIZE, (void *) data);

    length -= MD5_BLOCK_SIZE;
    if (___mhd_log)
      fprintf(___mhd_log, "decreased length by %d, new length=%zu\n", MD5_BLOCK_SIZE, length);
  }

  if (0 != length)
  {   /* Copy incomplete block of new data (if any)
         to the buffer. */
    if (___mhd_log)
      fprintf(___mhd_log, "copying incomplete tail: bytes=%zu to buffer+%u\n", length, bytes_have);

    memcpy (((uint8_t *) ctx->buffer) + bytes_have, data, length);

    if (___mhd_log)
      fprintf(___mhd_log, "copied %zu bytes to buffer; final bytes_have=%u\n", length, bytes_have);
  }

  if (___mhd_log)
  {
    fprintf(___mhd_log, "EXIT MHD_MD5_update: final ctx->count=%zu\n", ctx->count);
    fclose(___mhd_log);
  }
}
