void
MHD_SHA256_update (struct Sha256Ctx *ctx,
                   const uint8_t *data,
                   size_t length)
{
  unsigned bytes_have; /**< Number of bytes in buffer */

  /* Minimal logging to /tmp/logs/532.txt for reproducing pointer-modification traces */
  {
    FILE *log = fopen("/tmp/logs/532.txt", "a");
    if (log)
    {
      fprintf(log, "ENTER MHD_SHA256_update: ctx=%p data=%p length=%zu count(before)=%llu\n",
              (void *)ctx, (void *)data, length, (unsigned long long)ctx->count);
      fclose(log);
    }
  }

  mhd_assert ((data != NULL) || (length == 0));

#ifndef MHD_FAVOR_SMALL_CODE
  if (0 == length)
  {
    FILE *log = fopen("/tmp/logs/532.txt", "a");
    if (log)
    {
      fprintf(log, "EARLY RETURN: length == 0, ctx=%p data=%p\n", (void *)ctx, (void *)data);
      fclose(log);
    }
    return; /* Shortcut, do nothing */
  }
#endif /* MHD_FAVOR_SMALL_CODE */

  /* Note: (count & (SHA256_BLOCK_SIZE-1))
           equals (count % SHA256_BLOCK_SIZE) for this block size. */
  bytes_have = (unsigned) (ctx->count & (SHA256_BLOCK_SIZE - 1));
  ctx->count += length;

  {
    FILE *log = fopen("/tmp/logs/532.txt", "a");
    if (log)
    {
      fprintf(log, "AFTER count update: ctx=%p count(after)=%llu bytes_have=%u length=%zu data=%p\n",
              (void *)ctx, (unsigned long long)ctx->count, bytes_have, length, (void *)data);
      fclose(log);
    }
  }

  if (0 != bytes_have)
  {
    unsigned bytes_left = SHA256_BLOCK_SIZE - bytes_have;
    if (length >= bytes_left)
    {     /* Combine new data with data in the buffer and
             process full block. */
      {
        FILE *log = fopen("/tmp/logs/532.txt", "a");
        if (log)
        {
          fprintf(log, "COMBINE BLOCK: ctx=%p bytes_have=%u bytes_left=%u length(before)=%zu data(before)=%p\n",
                  (void *)ctx, bytes_have, bytes_left, length, (void *)data);
          fclose(log);
        }
      }

      memcpy (((uint8_t *) ctx->buffer) + bytes_have,
              data,
              bytes_left);

      /* --- Violation point: parameter 'data' is modified below --- */
      data += bytes_left;
      length -= bytes_left;

      {
        FILE *log = fopen("/tmp/logs/532.txt", "a");
        if (log)
        {
          fprintf(log, "AFTER ADJUST: data=%p length=%zu (moved by bytes_left=%u)\n",
                  (void *)data, length, bytes_left);
          fclose(log);
        }
      }

      sha256_transform (ctx->H, ctx->buffer);
      bytes_have = 0;
    }
  }

  while (SHA256_BLOCK_SIZE <= length)
  {   /* Process any full blocks of new data directly,
         without copying to the buffer. */
    {
      FILE *log = fopen("/tmp/logs/532.txt", "a");
      if (log)
      {
        fprintf(log, "PROCESS FULL BLOCK: ctx=%p data(before)=%p length(before)=%zu\n",
                (void *)ctx, (void *)data, length);
        fclose(log);
      }
    }

    sha256_transform (ctx->H, data);

    /* --- Violation point: parameter 'data' is modified below --- */
    data += SHA256_BLOCK_SIZE;
    length -= SHA256_BLOCK_SIZE;

    {
      FILE *log = fopen("/tmp/logs/532.txt", "a");
      if (log)
      {
        fprintf(log, "AFTER BLOCK ADVANCE: data=%p length=%zu (adv by %d)\n",
                (void *)data, length, SHA256_BLOCK_SIZE);
        fclose(log);
      }
    }
  }

  if (0 != length)
  {   /* Copy incomplete block of new data (if any)
         to the buffer. */
    {
      FILE *log = fopen("/tmp/logs/532.txt", "a");
      if (log)
      {
        fprintf(log, "COPY INCOMPLETE: ctx=%p bytes_have=%u remaining_length=%zu data=%p\n",
                (void *)ctx, bytes_have, length, (void *)data);
        fclose(log);
      }
    }

    memcpy (((uint8_t *) ctx->buffer) + bytes_have, data, length);
  }

  {
    FILE *log = fopen("/tmp/logs/532.txt", "a");
    if (log)
    {
      fprintf(log, "EXIT MHD_SHA256_update: ctx=%p final_count=%llu final_data_pointer=%p final_length=%zu\n",
              (void *)ctx, (unsigned long long)ctx->count, (void *)data, length);
      fclose(log);
    }
  }
}
