void
MHD_SHA256_update (struct Sha256Ctx *ctx,
                   const uint8_t *data,
                   size_t length)
{
  unsigned bytes_have; /**< Number of bytes in buffer */
  /* Keep a copy of the original pointer so logs can show the parameter was modified */
  const uint8_t *orig_data = data;
  FILE *log = NULL;

  mhd_assert ((data != NULL) || (length == 0));

#ifndef MHD_FAVOR_SMALL_CODE
  if (0 == length)
  {
    /* Log entry and early return */
    log = fopen("/tmp/logs/532.txt", "a");
    if (log)
    {
      fprintf(log, "MHD_SHA256_update entered: ctx=%p orig_data=%p data=%p length=%zu (early return)\n",
              (void *) ctx, (const void *) orig_data, (const void *) data, length);
      fclose(log);
    }
    return; /* Shortcut, do nothing */
  }
#endif /* MHD_FAVOR_SMALL_CODE */

  /* Note: (count & (SHA256_BLOCK_SIZE-1))
           equals (count % SHA256_BLOCK_SIZE) for this block size. */
  bytes_have = (unsigned) (ctx->count & (SHA256_BLOCK_SIZE - 1));
  ctx->count += length;

  /* Log initial state after computing bytes_have */
  log = fopen("/tmp/logs/532.txt", "a");
  if (log)
  {
    fprintf(log, "MHD_SHA256_update start: ctx=%p orig_data=%p data=%p length=%zu bytes_have=%u count(after)=%zu\n",
            (void *) ctx, (const void *) orig_data, (const void *) data, length, bytes_have, (size_t) ctx->count);
    fclose(log);
  }

  if (0 != bytes_have)
  {
    unsigned bytes_left = SHA256_BLOCK_SIZE - bytes_have;
    if (length >= bytes_left)
    {     /* Combine new data with data in the buffer and
             process full block. */
      /* Log before memcpy and pointer modification */
      log = fopen("/tmp/logs/532.txt", "a");
      if (log)
      {
        fprintf(log, "Combining with buffer: before memcpy: data=%p length=%zu bytes_left=%u bytes_have=%u\n",
                (const void *) data, length, bytes_left, bytes_have);
        fclose(log);
      }

      memcpy (((uint8_t *) ctx->buffer) + bytes_have,
              data,
              bytes_left);

      /* This is the parameter-modifying statement reported as violating MISRA Rule 17.8 */
      data += bytes_left;
      length -= bytes_left;

      /* Log after pointer arithmetic */
      log = fopen("/tmp/logs/532.txt", "a");
      if (log)
      {
        fprintf(log, "After combining: data advanced: orig_data=%p current_data=%p length=%zu bytes_have(before transform)=%u\n",
                (const void *) orig_data, (const void *) data, length, bytes_have);
        fclose(log);
      }

      sha256_transform (ctx->H, ctx->buffer);
      bytes_have = 0;

      /* Log after transform */
      log = fopen("/tmp/logs/532.txt", "a");
      if (log)
      {
        fprintf(log, "After sha256_transform (combined block): data=%p length=%zu bytes_have=%u\n",
                (const void *) data, length, bytes_have);
        fclose(log);
      }
    }
  }

  while (SHA256_BLOCK_SIZE <= length)
  {   /* Process any full blocks of new data directly,
         without copying to the buffer. */
    /* Log each full-block processing iteration */
    log = fopen("/tmp/logs/532.txt", "a");
    if (log)
    {
      fprintf(log, "Processing full block: data=%p length=%zu\n", (const void *) data, length);
      fclose(log);
    }

    sha256_transform (ctx->H, data);

    /* This also modifies the function parameter 'data' */
    data += SHA256_BLOCK_SIZE;
    length -= SHA256_BLOCK_SIZE;

    /* Log after advancing pointer */
    log = fopen("/tmp/logs/532.txt", "a");
    if (log)
    {
      fprintf(log, "After processing full block: advanced data=%p remaining length=%zu\n", (const void *) data, length);
      fclose(log);
    }
  }

  if (0 != length)
  {   /* Copy incomplete block of new data (if any)
         to the buffer. */
    /* Log final copy of incomplete block */
    log = fopen("/tmp/logs/532.txt", "a");
    if (log)
    {
      fprintf(log, "Copying remaining data to buffer: data=%p length=%zu bytes_have=%u\n",
              (const void *) data, length, bytes_have);
      fclose(log);
    }

    memcpy (((uint8_t *) ctx->buffer) + bytes_have, data, length);

    /* Log final state */
    log = fopen("/tmp/logs/532.txt", "a");
    if (log)
    {
      fprintf(log, "MHD_SHA256_update exit: ctx=%p orig_data=%p final_data=%p final_length=%zu final_bytes_have=%u\n",
              (void *) ctx, (const void *) orig_data, (const void *) data, length, bytes_have);
      fclose(log);
    }
  }
  else
  {
    /* Nothing left to copy; log final state */
    log = fopen("/tmp/logs/532.txt", "a");
    if (log)
    {
      fprintf(log, "MHD_SHA256_update exit: ctx=%p orig_data=%p final_data=%p final_length=%zu final_bytes_have=%u\n",
              (void *) ctx, (const void *) orig_data, (const void *) data, length, bytes_have);
      fclose(log);
    }
  }
}
