void
MHD_SHA256_update (struct Sha256Ctx *ctx,
                   const uint8_t *data,
                   size_t length)
{
  unsigned bytes_have; /**< Number of bytes in buffer */
  FILE *mhd_log = fopen("/tmp/logs/532.txt", "a");

  if (mhd_log)
  {
    fprintf(mhd_log, "ENTER MHD_SHA256_update: ctx=%p data=%p length=%zu\n",
            (void *) ctx, (void *) data, length);
  }

  mhd_assert ((data != NULL) || (length == 0));

#ifndef MHD_FAVOR_SMALL_CODE
  if (0 == length)
  {
    if (mhd_log)
      fprintf(mhd_log, "EARLY RETURN: length == 0\n");
    if (mhd_log) fclose(mhd_log);
    return; /* Shortcut, do nothing */
  }
#endif /* MHD_FAVOR_SMALL_CODE */

  /* Note: (count & (SHA256_BLOCK_SIZE-1))
           equals (count % SHA256_BLOCK_SIZE) for this block size. */
  bytes_have = (unsigned) (ctx->count & (SHA256_BLOCK_SIZE - 1));
  if (mhd_log)
    fprintf(mhd_log, "before: ctx->count=%llu bytes_have=%u length=%zu\n",
            (unsigned long long) ctx->count, bytes_have, length);

  ctx->count += length;
  if (mhd_log)
    fprintf(mhd_log, "after:  ctx->count=%llu\n", (unsigned long long) ctx->count);

  if (0 != bytes_have)
  {
    unsigned bytes_left = SHA256_BLOCK_SIZE - bytes_have;
    if (mhd_log)
      fprintf(mhd_log, "bytes_have!=0 branch: bytes_have=%u bytes_left=%u length=%zu\n",
              bytes_have, bytes_left, length);

    if (length >= bytes_left)
    {     /* Combine new data with data in the buffer and
             process full block. */
      if (mhd_log)
        fprintf(mhd_log, "will combine buffer: copy %u bytes from data to buffer\n",
                bytes_left);

      memcpy (((uint8_t *) ctx->buffer) + bytes_have,
              data,
              bytes_left);
      data += bytes_left;
      if (mhd_log)
        fprintf(mhd_log, "before modifying length: old_length=%zu bytes_left=%u\n",
                length, bytes_left);

      length -= bytes_left; /* <-- Modification of function parameter (intentional) */

      if (mhd_log)
        fprintf(mhd_log, "after  modifying length: new_length=%zu\n", length);

      sha256_transform (ctx->H, ctx->buffer);
      bytes_have = 0;
    }
    else
    {
      if (mhd_log)
        fprintf(mhd_log, "not enough new data to fill buffer: length=%zu bytes_left=%u\n",
                length, bytes_left);
    }
  }

  while (SHA256_BLOCK_SIZE <= length)
  {   /* Process any full blocks of new data directly,
         without copying to the buffer. */
    if (mhd_log)
      fprintf(mhd_log, "processing full block: data=%p length=%zu\n",
              (void *) data, length);

    sha256_transform (ctx->H, data);
    data += SHA256_BLOCK_SIZE;

    if (mhd_log)
      fprintf(mhd_log, "about to subtract SHA256_BLOCK_SIZE from length: old_length=%zu\n",
              length);

    length -= SHA256_BLOCK_SIZE; /* <-- Modification of function parameter (intentional) */

    if (mhd_log)
      fprintf(mhd_log, "after subtraction: new_length=%zu\n", length);
  }

  if (0 != length)
  {   /* Copy incomplete block of new data (if any)
         to the buffer. */
    if (mhd_log)
      fprintf(mhd_log, "copy incomplete block: bytes=%zu to buffer offset=%u data=%p\n",
              length, bytes_have, (void *) data);

    memcpy (((uint8_t *) ctx->buffer) + bytes_have, data, length);
  }

  if (mhd_log)
  {
    fprintf(mhd_log, "EXIT MHD_SHA256_update: final ctx->count=%llu remaining length=%zu\n",
            (unsigned long long) ctx->count, length);
    fclose(mhd_log);
  }
}
