void
MHD_SHA512_256_update (struct Sha512_256Ctx *ctx,
                       const uint8_t *data,
                       size_t length)
{
  /* Local includes to ensure logging helpers are available in the
     replacement function. These are allowed here to keep the
     function self-contained for the test harness. */
  #include <stdio.h>
  #include <sys/stat.h>
  #include <sys/types.h>
  #include <stdint.h>
  #include <inttypes.h>
  #include <errno.h>

  unsigned int bytes_have; /**< Number of bytes in the context buffer */
  uint64_t count_hi; /**< The high part to be moved to another variable */

  /* Attempt to open log file and write diagnostics for reproducing the
     violation scenario where the pointer parameter 'data' is modified. */
  {
    /* Ensure log directory exists (ignore errors if it already exists). */
    (void) mkdir("/tmp/logs", 0755);
    FILE *log = fopen("/tmp/logs/482.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "ENTER MHD_SHA512_256_update: ctx=%p data=%p length=%zu count(before)=%llu\n",
              (void *) ctx, (void *) data, length, (unsigned long long) ctx->count);
      fclose(log);
    }
  }

  mhd_assert ((data != NULL) || (length == 0));

#ifndef MHD_FAVOR_SMALL_CODE
  if (0 == length)
  {
    FILE *log = fopen("/tmp/logs/482.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "EARLY RETURN (length==0)\n");
      fclose(log);
    }
    return; /* Shortcut, do nothing */
  }
#endif /* ! MHD_FAVOR_SMALL_CODE */

  /* Note: (count & (SHA512_256_BLOCK_SIZE-1))
           equals (count % SHA512_256_BLOCK_SIZE) for this block size. */
  bytes_have = (unsigned int) (ctx->count & (SHA512_256_BLOCK_SIZE - 1));
  ctx->count += length;
#if SIZEOF_SIZE_T > 7
  if (length > ctx->count)
    ctx->count_bits_hi += 1U << 3; /* Value wrap */
#endif /* SIZEOF_SIZE_T > 7 */
  count_hi = ctx->count >> 61;
  if (0 != count_hi)
  {
    ctx->count_bits_hi += count_hi;
    ctx->count &= UINT64_C (0x1FFFFFFFFFFFFFFF);
  }

  {
    FILE *log = fopen("/tmp/logs/482.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "computed bytes_have=%u count(after)=%llu count_bits_hi=%llu\n",
              bytes_have, (unsigned long long) ctx->count, (unsigned long long) ctx->count_bits_hi);
      fclose(log);
    }
  }

  if (0 != bytes_have)
  {
    unsigned int bytes_left = SHA512_256_BLOCK_SIZE - bytes_have;
    if (length >= bytes_left)
    {     /* Combine new data with data in the buffer and
             process the full block. */
      /* Log state before the memcpy and pointer adjustment. */
      {
        FILE *log = fopen("/tmp/logs/482.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "CASE: bytes_have!=0 && length>=bytes_left: bytes_have=%u bytes_left=%u data(before)=%p length(before)=%zu\n",
                  bytes_have, bytes_left, (void *) data, length);
          fclose(log);
        }
      }

      memcpy (((uint8_t *) ctx->buffer) + bytes_have,
              data,
              bytes_left);

      /* This is the modification of the pointer parameter reported as the
         MISRA violation: data is incremented here. We log before and
         after so the trace proves that the parameter itself was changed. */
      {
        FILE *log = fopen("/tmp/logs/482.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "modifying parameter 'data': data(before)=%p \n", (void *) data);
          fclose(log);
        }
      }

      data += bytes_left;
      length -= bytes_left;

      {
        FILE *log = fopen("/tmp/logs/482.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "modified parameter 'data': data(after)=%p length(after)=%zu\n",
                  (void *) data, length);
          fclose(log);
        }
      }

      sha512_256_transform (ctx->H, ctx->buffer);
      bytes_have = 0;
    }
  }

  while (SHA512_256_BLOCK_SIZE <= length)
  {   /* Process any full blocks of new data directly,
         without copying to the buffer. */
    /* Log each iteration of the block-processing loop showing the
       pointer before modification, the transform call, and the pointer
       after modification. */
    {
      FILE *log = fopen("/tmp/logs/482.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "LOOP: before transform data=%p length=%zu\n", (void *) data, length);
        fclose(log);
      }
    }

    sha512_256_transform (ctx->H, data);

    {
      FILE *log = fopen("/tmp/logs/482.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "LOOP: transform done data(before increment)=%p\n", (void *) data);
        fclose(log);
      }
    }

    data += SHA512_256_BLOCK_SIZE;
    length -= SHA512_256_BLOCK_SIZE;

    {
      FILE *log = fopen("/tmp/logs/482.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "LOOP: data(after increment)=%p length=%zu\n", (void *) data, length);
        fclose(log);
      }
    }
  }

  if (0 != length)
  {   /* Copy incomplete block of new data (if any)
         to the buffer. */
    {
      FILE *log = fopen("/tmp/logs/482.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "FINAL COPY: bytes_have=%u remaining length=%zu data=%p -> copying to buffer at offset\n",
                bytes_have, length, (void *) data);
        fclose(log);
      }
    }

    memcpy (((uint8_t *) ctx->buffer) + bytes_have, data, length);
  }

  {
    FILE *log = fopen("/tmp/logs/482.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "EXIT MHD_SHA512_256_update: ctx=%p data(final)=%p length(final)=%zu count(after)=%llu\n",
              (void *) ctx, (void *) data, length, (unsigned long long) ctx->count);
      fclose(log);
    }
  }
}
