void
MHD_SHA512_256_update (struct Sha512_256Ctx *ctx,
                       const uint8_t *data,
                       size_t length)
{
  unsigned int bytes_have; /**< Number of bytes in the context buffer */
  uint64_t count_hi; /**< The high part to be moved to another variable */
  FILE *fp = NULL;

  /* Open log file for append. We don't abort on failure to avoid affecting
     normal behavior; just skip logging if fopen fails. */
  fp = fopen("/tmp/logs/482.txt", "a");
  if (fp != NULL)
  {
    fprintf(fp, "ENTER MHD_SHA512_256_update: ctx=%p data=%p initial_length=%zu\n",
            (void *)ctx, (void *)data, length);
    fflush(fp);
  }

  mhd_assert ((data != NULL) || (length == 0));

#ifndef MHD_FAVOR_SMALL_CODE
  if (0 == length)
  {
    if (fp != NULL)
    {
      fprintf(fp, "EARLY RETURN: length == 0\n");
      fclose(fp);
    }
    return; /* Shortcut, do nothing */
  }
#endif /* ! MHD_FAVOR_SMALL_CODE */

  /* Note: (count & (SHA512_256_BLOCK_SIZE-1))
           equals (count % SHA512_256_BLOCK_SIZE) for this block size. */
  bytes_have = (unsigned int) (ctx->count & (SHA512_256_BLOCK_SIZE - 1));
  if (fp != NULL)
  {
    fprintf(fp, "bytes_have=%u ctx->count(before)=%llu\n",
            bytes_have, (unsigned long long)ctx->count);
    fflush(fp);
  }

  ctx->count += length;
  if (fp != NULL)
  {
    fprintf(fp, "ctx->count(after add length)=%llu added_length=%zu\n",
            (unsigned long long)ctx->count, length);
    fflush(fp);
  }

#if SIZEOF_SIZE_T > 7
  if (length > ctx->count)
    ctx->count_bits_hi += 1U << 3; /* Value wrap */
#endif /* SIZEOF_SIZE_T > 7 */
  count_hi = ctx->count >> 61;
  if (0 != count_hi)
  {
    ctx->count_bits_hi += count_hi;
    ctx->count &= UINT64_C (0x1FFFFFFFFFFFFFFF);
    if (fp != NULL)
    {
      fprintf(fp, "count_hi nonzero: count_hi=%llu ctx->count(after mask)=%llu ctx->count_bits_hi=%llu\n",
              (unsigned long long)count_hi,
              (unsigned long long)ctx->count,
              (unsigned long long)ctx->count_bits_hi);
      fflush(fp);
    }
  }

  if (0 != bytes_have)
  {
    unsigned int bytes_left = SHA512_256_BLOCK_SIZE - bytes_have;
    if (fp != NULL)
    {
      fprintf(fp, "bytes_have branch: bytes_have=%u bytes_left=%u length_before=%zu data=%p\n",
              bytes_have, bytes_left, length, (void *)data);
      fflush(fp);
    }
    if (length >= bytes_left)
    {     /* Combine new data with data in the buffer and
             process the full block. */
      memcpy (((uint8_t *) ctx->buffer) + bytes_have,
              data,
              bytes_left);
      data += bytes_left;
      length -= bytes_left;
      if (fp != NULL)
      {
        fprintf(fp, "  after combining: length=%zu data=%p\n",
                length, (void *)data);
        fflush(fp);
      }
      sha512_256_transform (ctx->H, ctx->buffer);
      bytes_have = 0;
      if (fp != NULL)
      {
        fprintf(fp, "  transformed buffer; bytes_have reset to 0\n");
        fflush(fp);
      }
    }
    else
    {
      if (fp != NULL)
      {
        fprintf(fp, "  not enough to fill buffer: length=%zu bytes_left=%u -> copy deferred\n",
                length, bytes_left);
        fflush(fp);
      }
    }
  }

  while (SHA512_256_BLOCK_SIZE <= length)
  {   /* Process any full blocks of new data directly,
         without copying to the buffer. */
    if (fp != NULL)
    {
      fprintf(fp, "WHILE LOOP: processing block: length_before=%zu data=%p\n",
              length, (void *)data);
      fflush(fp);
    }
    sha512_256_transform (ctx->H, data);
    data += SHA512_256_BLOCK_SIZE;
    length -= SHA512_256_BLOCK_SIZE;
    if (fp != NULL)
    {
      fprintf(fp, "  after transform: length_after=%zu data=%p\n",
              length, (void *)data);
      fflush(fp);
    }
  }

  if (0 != length)
  {   /* Copy incomplete block of new data (if any)
         to the buffer. */
    if (fp != NULL)
    {
      fprintf(fp, "FINAL COPY: remaining length=%zu bytes_have=%u data=%p\n",
              length, bytes_have, (void *)data);
      fflush(fp);
    }
    memcpy (((uint8_t *) ctx->buffer) + bytes_have, data, length);
  }

  if (fp != NULL)
  {
    fprintf(fp, "EXIT MHD_SHA512_256_update: final ctx->count=%llu final_length=%zu\n",
            (unsigned long long)ctx->count, length);
    fclose(fp);
  }
}
