void
MHD_SHA512_256_update (struct Sha512_256Ctx *ctx,
                       const uint8_t *data,
                       size_t length)
{
  unsigned int bytes_have; /**< Number of bytes in the context buffer */
  uint64_t count_hi; /**< The high part to be moved to another variable */
  FILE *logf = NULL;

  /* Try to open append log file; if this fails, continue without logging. */
  logf = fopen("/tmp/logs/485.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER MHD_SHA512_256_update: ctx=%p data=%p length=%zu\n",
            (void *)ctx, (void *)data, length);
    fflush(logf);
  }

  mhd_assert ((data != NULL) || (length == 0));

#ifndef MHD_FAVOR_SMALL_CODE
  if (0 == length)
  {
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: length==0\n");
      fclose(logf);
      logf = NULL;
    }
    return; /* Shortcut, do nothing */
  }
#endif /* ! MHD_FAVOR_SMALL_CODE */

  /* Note: (count & (SHA512_256_BLOCK_SIZE-1))
           equals (count % SHA512_256_BLOCK_SIZE) for this block size. */
  bytes_have = (unsigned int) (ctx->count & (SHA512_256_BLOCK_SIZE - 1));
  if (logf)
    fprintf(logf, "bytes_have(before)=%u ctx->count(before)=%llu\n",
            bytes_have, (unsigned long long)ctx->count);

  ctx->count += length;
#if SIZEOF_SIZE_T > 7
  if (length > ctx->count)
    ctx->count_bits_hi += 1U << 3; /* Value wrap */
#endif /* SIZEOF_SIZE_T > 7 */
  count_hi = ctx->count >> 61;
  if (0 != count_hi)
  {
    ctx->count_bits_hi += count_hi;
    ctx->count &= UINT64_C (0x1FFFFFFFFFFFFFFF);
  }

  if (logf)
  {
    fprintf(logf, "ctx->count(after)=%llu ctx->count_bits_hi=%llu count_hi=%llu\n",
            (unsigned long long)ctx->count,
            (unsigned long long)ctx->count_bits_hi,
            (unsigned long long)count_hi);
    fflush(logf);
  }

  if (0 != bytes_have)
  {
    unsigned int bytes_left = SHA512_256_BLOCK_SIZE - bytes_have;
    if (logf)
      fprintf(logf, "INCOMPLETE buffer path: bytes_have=%u bytes_left=%u length=%zu\n",
              bytes_have, bytes_left, length);
    if (length >= bytes_left)
    {     /* Combine new data with data in the buffer and
             process the full block. */
      if (logf)
      {
        fprintf(logf, "ABOUT TO memcpy to ctx->buffer+%u from data=%p bytes=%u\n",
                bytes_have, (void *)data, bytes_left);
        fflush(logf);
      }
      memcpy (((uint8_t *) ctx->buffer) + bytes_have,
              data,
              bytes_left);
      data += bytes_left;
      length -= bytes_left;

      if (logf)
      {
        /* Log that we are about to call sha512_256_transform with ctx->buffer */
        fprintf(logf, "CALL sha512_256_transform(ctx->H=%p, ctx->buffer=%p) -- combined block\n",
                (void *)ctx->H, (void *)ctx->buffer);
        /* Also log that the original data pointer was const uint8_t * and is being passed (address) */
        fprintf(logf, "NOTE: passing data pointer (originally const uint8_t *) to transform: data_after_advance=%p\n",
                (void *)data);
        fflush(logf);
      }

      sha512_256_transform (ctx->H, ctx->buffer);
      bytes_have = 0;

      if (logf)
      {
        fprintf(logf, "AFTER transform (combined block). length now=%zu bytes_have=%u\n",
                length, bytes_have);
        fflush(logf);
      }
    }
  }

  while (SHA512_256_BLOCK_SIZE <= length)
  {   /* Process any full blocks of new data directly,
         without copying to the buffer. */
    if (logf)
    {
      fprintf(logf, "CALL sha512_256_transform(ctx->H=%p, data=%p) -- full-block direct, block_size=%d\n",
              (void *)ctx->H, (void *)data, SHA512_256_BLOCK_SIZE);
      /* Warn about the potential const-discard scenario: we log that data is const uint8_t * */
      fprintf(logf, "POTENTIAL CONST-DISCARD: data (const uint8_t *) address=%p passed to sha512_256_transform\n",
              (void *)data);
      fflush(logf);
    }

    sha512_256_transform (ctx->H, data);
    data += SHA512_256_BLOCK_SIZE;
    length -= SHA512_256_BLOCK_SIZE;

    if (logf)
    {
      fprintf(logf, "AFTER transform (direct). data now=%p length=%zu\n",
              (void *)data, length);
      fflush(logf);
    }
  }

  if (0 != length)
  {   /* Copy incomplete block of new data (if any)
         to the buffer. */
    if (logf)
    {
      fprintf(logf, "FINAL memcpy to ctx->buffer+%u from data=%p bytes=%zu\n",
              bytes_have, (void *)data, length);
      fflush(logf);
    }
    memcpy (((uint8_t *) ctx->buffer) + bytes_have, data, length);
  }

  if (logf)
  {
    fprintf(logf, "EXIT MHD_SHA512_256_update: ctx=%p final_count=%llu final_count_bits_hi=%llu\n",
            (void *)ctx, (unsigned long long)ctx->count, (unsigned long long)ctx->count_bits_hi);
    fclose(logf);
  }
}
