static enum MHD_Result
MHD_accept_connection (struct MHD_Daemon *daemon)
{
  struct sockaddr_storage addrstorage;
  socklen_t addrlen;
  MHD_socket s;
  MHD_socket fd;
  bool sk_nonbl;
  bool sk_spipe_supprs;
  bool sk_cloexec;
  enum MHD_tristate sk_non_ip;
#if defined(_DEBUG) && defined (USE_ACCEPT4)
  const bool use_accept4 = ! daemon->avoid_accept4;
#elif defined (USE_ACCEPT4)
  static const bool use_accept4 = true;
#else  /* ! USE_ACCEPT4 && ! _DEBUG */
  static const bool use_accept4 = false;
#endif /* ! USE_ACCEPT4 && ! _DEBUG */

  enum MHD_Result ret = MHD_NO;
  FILE *logf = NULL;

#ifdef MHD_USE_THREADS
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
  mhd_assert (NULL == daemon->worker_pool);
#endif /* MHD_USE_THREADS */

  /* Open log early so we can trace entry and subsequent decisions. */
  logf = fopen("/tmp/logs/4425.txt", "a");
  if (NULL != logf)
  {
    fprintf(logf, "ENTER MHD_accept_connection: daemon=%p listen_fd=%lld was_quiesced=%d\n",
            (void *) daemon,
            (long long) daemon->listen_fd,
            (int) daemon->was_quiesced);
    fflush(logf);
  }

  if ( (MHD_INVALID_SOCKET == (fd = daemon->listen_fd)) ||
       (daemon->was_quiesced) )
  {
    if (NULL != logf)
    {
      fprintf(logf, "EARLY RETURN: invalid listen_fd or quiesced (listen_fd=%lld was_quiesced=%d)\n",
              (long long) fd,
              (int) daemon->was_quiesced);
      fflush(logf);
    }
    ret = MHD_NO;
    goto done;
  }

  addrlen = (socklen_t) sizeof (addrstorage);
  memset (&addrstorage,
          0,
          (size_t) addrlen);
#ifdef HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN
  addrstorage.ss_len = addrlen;
#endif /* HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN */

  /* Initialise with default values to avoid compiler warnings */
  sk_nonbl = false;
  sk_spipe_supprs = false;
  sk_cloexec = false;
  s = MHD_INVALID_SOCKET;

#ifdef USE_ACCEPT4
  if (use_accept4 &&
      (MHD_INVALID_SOCKET !=
       (s = accept4 (fd,
                     (struct sockaddr *) &addrstorage,
                     &addrlen,
                     SOCK_CLOEXEC_OR_ZERO | SOCK_NONBLOCK_OR_ZERO
                     | SOCK_NOSIGPIPE_OR_ZERO))))
  {
    sk_nonbl = (SOCK_NONBLOCK_OR_ZERO != 0);
#ifndef MHD_WINSOCK_SOCKETS
    sk_spipe_supprs = (SOCK_NOSIGPIPE_OR_ZERO != 0);
#else  /* MHD_WINSOCK_SOCKETS */
    sk_spipe_supprs = true; /* Nothing to suppress on W32 */
#endif /* MHD_WINSOCK_SOCKETS */
    sk_cloexec = (SOCK_CLOEXEC_OR_ZERO != 0);
    if (NULL != logf)
    {
      fprintf(logf, "accept4() succeeded: s=%lld sk_nonbl=%d sk_spipe_supprs=%d sk_cloexec=%d addrlen=%u\n",
              (long long) s,
              (int) sk_nonbl,
              (int) sk_spipe_supprs,
              (int) sk_cloexec,
              (unsigned) addrlen);
      fflush(logf);
    }
  }
#endif /* USE_ACCEPT4 */
#if defined(_DEBUG) || ! defined(USE_ACCEPT4)
  if (! use_accept4 &&
      (MHD_INVALID_SOCKET !=
       (s = accept (fd,
                    (struct sockaddr *) &addrstorage,
                    &addrlen))))
  {
#ifdef MHD_ACCEPT_INHERIT_NONBLOCK
    sk_nonbl = daemon->listen_nonblk;
#else  /* ! MHD_ACCEPT_INHERIT_NONBLOCK */
    sk_nonbl = false;
#endif /* ! MHD_ACCEPT_INHERIT_NONBLOCK */
#ifndef MHD_WINSOCK_SOCKETS
    sk_spipe_supprs = false;
#else  /* MHD_WINSOCK_SOCKETS */
    sk_spipe_supprs = true; /* Nothing to suppress on W32 */
#endif /* MHD_WINSOCK_SOCKETS */
    sk_cloexec = false;
    if (NULL != logf)
    {
      fprintf(logf, "accept() succeeded: s=%lld sk_nonbl=%d sk_spipe_supprs=%d sk_cloexec=%d addrlen=%u\n",
              (long long) s,
              (int) sk_nonbl,
              (int) sk_spipe_supprs,
              (int) sk_cloexec,
              (unsigned) addrlen);
      fflush(logf);
    }
  }
#endif /* _DEBUG || !USE_ACCEPT4 */

  if (MHD_INVALID_SOCKET == s)
  {
    const int err = MHD_socket_get_error_ ();

    if (NULL != logf)
    {
      fprintf(logf, "accept failed: err=%d\n", err);
      fflush(logf);
    }

    /* This could be a common occurrence with multiple worker threads */
    if (MHD_SCKT_ERR_IS_ (err,
                          MHD_SCKT_EINVAL_))
    {
      if (NULL != logf)
      {
        fprintf(logf, "RETURN MHD_NO due to EINVAL on accept (likely shutdown)\n");
        fflush(logf);
      }
      ret = MHD_NO;   /* can happen during shutdown */
      goto done;
    }
    if (MHD_SCKT_ERR_IS_DISCNN_BEFORE_ACCEPT_ (err))
    {
      if (NULL != logf)
      {
        fprintf(logf, "RETURN MHD_NO due to client disconnected before accept\n");
        fflush(logf);
      }
      ret = MHD_NO;   /* do not print error if client just disconnected early */
      goto done;
    }
#ifdef HAVE_MESSAGES
    if (! MHD_SCKT_ERR_IS_EAGAIN_ (err) )
    {
      const char *serr = MHD_socket_strerr_ (err);
      if (NULL != logf)
      {
        fprintf(logf, "Error accepting connection (non-EAGAIN): err=%d strerr_ptr=%p str=""%s""\n",
                err,
                (const void *) serr,
                serr ? serr : "(null)");
        fflush(logf);
      }

      MHD_DLOG (daemon,
                _ ("Error accepting connection: %s\n"),
                serr);
    }
#endif
    if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err) )
    {
      /* system/process out of resources */
      if (0 == daemon->connections)
      {
#ifdef HAVE_MESSAGES
        /* Not setting 'at_limit' flag, as there is no way it
           would ever be cleared.  Instead trying to produce
           bit fat ugly warning. */
        MHD_DLOG (daemon,
                  _ ("Hit process or system resource limit at FIRST " \
                     "connection. This is really bad as there is no sane " \
                     "way to proceed. Will try busy waiting for system " \
                     "resources to become magically available.\n"));
#endif
      }
      else
      {
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
        MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
#endif
        daemon->at_limit = true;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
        MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#endif
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("Hit process or system resource limit at %u " \
                     "connections, temporarily suspending accept(). " \
                     "Consider setting a lower MHD_OPTION_CONNECTION_LIMIT.\n"),
                  (unsigned int) daemon->connections);
#endif
        if (NULL != logf)
        {
          fprintf(logf, "Marked daemon->at_limit = true at connections=%u\n",
                  (unsigned) daemon->connections);
          fflush(logf);
        }
      }
    }
    ret = MHD_NO;
    goto done;
  }

  sk_non_ip = daemon->listen_is_unix;
  if (0 >= addrlen)
  {
#ifdef HAVE_MESSAGES
    if (_MHD_NO != daemon->listen_is_unix)
      MHD_DLOG (daemon,
                _ ("Accepted socket has zero-length address. "
                   "Processing the new socket as a socket with " \
                   "unknown type.\n"));
#endif
    addrlen = 0;
    sk_non_ip = _MHD_YES; /* IP-type addresses have non-zero length */
    if (NULL != logf)
    {
      fprintf(logf, "Accepted socket with zero-length address; treating as non-IP.\n");
      fflush(logf);
    }
  }
  if (((socklen_t) sizeof (addrstorage)) < addrlen)
  {
    /* Should not happen as 'sockaddr_storage' must be large enough to
     * store any address supported by the system. */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Accepted socket address is larger than expected by " \
                 "system headers. Processing the new socket as a socket with " \
                 "unknown type.\n"));
#endif
    addrlen = 0;
    sk_non_ip = _MHD_YES; /* IP-type addresses must fit */
    if (NULL != logf)
    {
      fprintf(logf, "Accepted socket address larger than expected; treating as non-IP.\n");
      fflush(logf);
    }
  }

  if (! sk_nonbl && ! MHD_socket_nonblocking_ (s))
  {
#ifdef HAVE_MESSAGES
    const char *last = MHD_socket_last_strerr_ ();
    if (NULL != logf)
    {
      fprintf(logf, "Failed to set nonblocking on s=%lld; last_strerr_ptr=%p last_strerr=\"%s\"\n",
              (long long) s,
              (const void *) last,
              last ? last : "(null)");
      fflush(logf);
    }
    MHD_DLOG (daemon,
              _ ("Failed to set nonblocking mode on incoming connection " \
                 "socket: %s\n"),
              last);
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
  }
  else
    sk_nonbl = true;

  if (! sk_cloexec && ! MHD_socket_noninheritable_ (s))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set noninheritable mode on incoming connection " \
                 "socket.\n"));
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
  }

#if defined(MHD_socket_nosignal_)
  if (! sk_spipe_supprs && ! MHD_socket_nosignal_ (s))
  {
#ifdef HAVE_MESSAGES
    const char *last2 = MHD_socket_last_strerr_ ();
    if (NULL != logf)
    {
      fprintf(logf, "Failed to suppress SIGPIPE on s=%lld; last_strerr_ptr=%p last_strerr=\"%s\"\n",
              (long long) s,
              (const void *) last2,
              last2 ? last2 : "(null)");
      fflush(logf);
    }
    MHD_DLOG (daemon,
              _ ("Failed to suppress SIGPIPE on incoming connection " \
                 "socket: %s\n"),
              last2);
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
#ifndef MSG_NOSIGNAL
    /* Application expects that SIGPIPE will be suppressed,
     * but suppression failed and SIGPIPE cannot be suppressed with send(). */
    if (! daemon->sigpipe_blocked)
    {
      (void) MHD_socket_close_ (s);
      if (NULL != logf)
      {
        fprintf(logf, "Closing socket %lld due to unsuppressed SIGPIPE and sigpipe not blocked.\n",
                (long long) s);
        fflush(logf);
      }
      ret = MHD_NO;
      goto done;
    }
#endif /* MSG_NOSIGNAL */
  }
  else
    sk_spipe_supprs = true;
#endif /* MHD_socket_nosignal_ */
#ifdef HAVE_MESSAGES
#if _MHD_DEBUG_CONNECT
  MHD_DLOG (daemon,
            _ ("Accepted connection on socket %d\n"),
            s);
#endif
#endif
  if (NULL != logf)
  {
    fprintf(logf, "About to add connection: s=%lld addrlen=%u sk_nonbl=%d sk_spipe_supprs=%d sk_non_ip=%d\n",
            (long long) s,
            (unsigned) addrlen,
            (int) sk_nonbl,
            (int) sk_spipe_supprs,
            (int) sk_non_ip);
    fflush(logf);
  }
  (void) internal_add_connection (daemon,
                                  s,
                                  &addrstorage,
                                  addrlen,
                                  false,
                                  sk_nonbl,
                                  sk_spipe_supprs,
                                  sk_non_ip);
  ret = MHD_YES;

done:
  if (NULL != logf)
  {
    fprintf(logf, "EXIT MHD_accept_connection: returning %d\n", (int) ret);
    fclose(logf);
  }
  return ret;
}
