_MHD_EXTERN enum MHD_Result
MHD_add_connection (struct MHD_Daemon *daemon,
                    MHD_socket client_socket,
                    const struct sockaddr *addr,
                    socklen_t addrlen)
{
  bool sk_nonbl;
  bool sk_spipe_supprs;
  struct sockaddr_storage addrstorage;
  FILE *log = fopen("/tmp/logs/3579.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER MHD_add_connection: daemon=%p client_socket=%lld addr=%p addrlen=%lu\n",
            (void *) daemon, (long long) client_socket, (void *) addr, (unsigned long) addrlen);
    fflush(log);
  }

  /* TODO: fix atomic value reading */
  if ((! MHD_D_IS_THREAD_SAFE_ (daemon)) &&
      (daemon->connection_limit <= daemon->connections))
    MHD_cleanup_connections (daemon);

#ifdef HAVE_MESSAGES
  if (MHD_D_IS_USING_THREADS_ (daemon) &&
      (0 == (daemon->options & MHD_USE_ITC)))
  {
    MHD_DLOG (daemon,
              _ ("MHD_add_connection() has been called for daemon started"
                 " without MHD_USE_ITC flag.\nDaemon will not process newly"
                 " added connection until any activity occurs in already"
                 " added sockets.\n"));
  }
#endif /* HAVE_MESSAGES */
  if (0 != addrlen)
  {
    if (AF_INET == addr->sa_family)
    {
      if (sizeof(struct sockaddr_in) > (size_t) addrlen)
      {
        if (log)
        {
          fprintf(log, "EARLY RETURN: invalid IPv4 addrlen %lu (sizeof(sockaddr_in)=%zu)\n",
                  (unsigned long) addrlen, sizeof(struct sockaddr_in));
          fclose(log);
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in) > (size_t) addr->sa_len) )
      {
        if (log)
        {
          fprintf(log, "EARLY RETURN: IPv4 sa_len inconsistent sa_len=%u sizeof(sockaddr_in)=%zu\n",
                  (unsigned) addr->sa_len, sizeof(struct sockaddr_in));
          fclose(log);
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_INET6
    if (AF_INET6 == addr->sa_family)
    {
      if (sizeof(struct sockaddr_in6) > (size_t) addrlen)
      {
        if (log)
        {
          fprintf(log, "EARLY RETURN: invalid IPv6 addrlen %lu (sizeof(sockaddr_in6)=%zu)\n",
                  (unsigned long) addrlen, sizeof(struct sockaddr_in6));
          fclose(log);
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in6) > (size_t) addr->sa_len) )
      {
        if (log)
        {
          fprintf(log, "EARLY RETURN: IPv6 sa_len inconsistent sa_len=%u sizeof(sockaddr_in6)=%zu\n",
                  (unsigned) addr->sa_len, sizeof(struct sockaddr_in6));
          fclose(log);
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
    if ((0 != addr->sa_len) &&
        (addrlen > addr->sa_len))
      addrlen = (socklen_t) addr->sa_len;   /* Use safest value */
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
#endif /* HAVE_INET6 */
  }

  if (! MHD_socket_nonblocking_ (client_socket))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set nonblocking mode on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
#endif
    sk_nonbl = false;
  }
  else
    sk_nonbl = true;

  if (log)
  {
    fprintf(log, "socket nonblocking set: sk_nonbl=%d\n", (int) sk_nonbl);
    fflush(log);
  }

#ifndef MHD_WINSOCK_SOCKETS
  sk_spipe_supprs = false;
  if (log)
  {
    fprintf(log, "MHD_WINSOCK_SOCKETS NOT defined -> sk_spipe_supprs=%d\n", (int) sk_spipe_supprs);
    fflush(log);
  }
#else  /* MHD_WINSOCK_SOCKETS */
  sk_spipe_supprs = true; /* Nothing to suppress on W32 */
  if (log)
  {
    fprintf(log, "MHD_WINSOCK_SOCKETS defined -> sk_spipe_supprs=%d\n", (int) sk_spipe_supprs);
    fflush(log);
  }
#endif /* MHD_WINSOCK_SOCKETS */
#if defined(MHD_socket_nosignal_)
  if (log)
  {
    fprintf(log, "Entering MHD_socket_nosignal_ block: initial sk_spipe_supprs=%d\n", (int) sk_spipe_supprs);
    fflush(log);
  }
  if (! sk_spipe_supprs)
    sk_spipe_supprs = MHD_socket_nosignal_ (client_socket);
  if (log)
  {
    fprintf(log, "After possible MHD_socket_nosignal_ call: sk_spipe_supprs=%d\n", (int) sk_spipe_supprs);
    fflush(log);
  }
  if (! sk_spipe_supprs)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to suppress SIGPIPE on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
    if (log)
    {
      fprintf(log, "SIGPIPE suppression failed after checks: sk_spipe_supprs=%d\n", (int) sk_spipe_supprs);
      fflush(log);
    }
#ifndef MSG_NOSIGNAL
    /* Application expects that SIGPIPE will be suppressed,
     * but suppression failed and SIGPIPE cannot be suppressed with send(). */
    if (! daemon->sigpipe_blocked)
    {
      if (log)
      {
        int err_local = MHD_socket_get_error_ ();
        fprintf(log, "SIGPIPE cannot be suppressed and daemon->sigpipe_blocked=false, err=%d -> closing socket and returning MHD_NO\n", err_local);
        fclose(log);
      }
      {
        int err = MHD_socket_get_error_ ();
        MHD_socket_close_ (client_socket);
        MHD_socket_fset_error_ (err);
        return MHD_NO;
      }
    }
#endif /* MSG_NOSIGNAL */
  }
#endif /* MHD_socket_nosignal_ */

  if ( (0 != (daemon->options & MHD_USE_TURBO)) &&
       (! MHD_socket_noninheritable_ (client_socket)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set noninheritable mode on new client socket.\n"));
#endif
  }

  /* Copy to sockaddr_storage structure to avoid alignment problems */
  if (0 < addrlen)
    memcpy (&addrstorage, addr, (size_t) addrlen);
#ifdef HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN
  addrstorage.ss_len = addrlen; /* Force set the right length */
#endif /* HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN */

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (NULL != daemon->worker_pool)
  {
    unsigned int i;
    /* have a pool, try to find a pool with capacity; we use the
       socket as the initial offset into the pool for load
       balancing */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      struct MHD_Daemon *const worker =
        &daemon->worker_pool[(i + (unsigned int) client_socket)
                             % daemon->worker_pool_size];
      if (worker->connections < worker->connection_limit)
      {
        if (log)
        {
          fprintf(log, "Dispatching to worker pool: worker=%p index=%u worker->connections=%u worker->connection_limit=%u -> calling internal_add_connection\n",
                  (void *) worker, i,
                  (unsigned) worker->connections, (unsigned) worker->connection_limit);
          fclose(log);
        }
        return internal_add_connection (worker,
                                        client_socket,
                                        &addrstorage,
                                        addrlen,
                                        true,
                                        sk_nonbl,
                                        sk_spipe_supprs,
                                        _MHD_UNKNOWN);
      }
    }
    /* all pools are at their connection limit, must refuse */
    if (log)
    {
      fprintf(log, "All worker pools full -> closing socket and returning MHD_NO\n");
      fclose(log);
    }
    MHD_socket_close_chk_ (client_socket);
#if defined(ENFILE) && (ENFILE + 0 != 0)
    errno = ENFILE;
#endif
    return MHD_NO;
  }
#endif /* MHD_USE_POSIX_THREADS || MHD_USE_W32_THREADS */

  if (log)
  {
    fprintf(log, "Proceeding to call internal_add_connection with daemon=%p client_socket=%lld sk_nonbl=%d sk_spipe_supprs=%d\n",
            (void *) daemon, (long long) client_socket, (int) sk_nonbl, (int) sk_spipe_supprs);
    fclose(log);
  }

  return internal_add_connection (daemon,
                                  client_socket,
                                  &addrstorage,
                                  addrlen,
                                  true,
                                  sk_nonbl,
                                  sk_spipe_supprs,
                                  _MHD_UNKNOWN);
}
