_MHD_EXTERN enum MHD_Result
MHD_add_connection (struct MHD_Daemon *daemon,
                    MHD_socket client_socket,
                    const struct sockaddr *addr,
                    socklen_t addrlen)
{
  bool sk_nonbl;
  bool sk_spipe_supprs;
  struct sockaddr_storage addrstorage;

  /* Minimal runtime tracing to /tmp/logs/3578.txt for control flow and state. */
  {
    FILE *log = fopen("/tmp/logs/3578.txt", "a");
    int saved_errno = errno;
    if (log)
    {
      fprintf(log, "MHD_add_connection ENTRY: daemon=%p client_socket=%lu addr=%p addrlen=%u\n",
              (void *) daemon,
              (unsigned long) client_socket,
              (void *) addr,
              (unsigned) addrlen);
#ifdef HAVE_MESSAGES
      fprintf(log, "  compile-time: HAVE_MESSAGES defined\n");
#else
      fprintf(log, "  compile-time: HAVE_MESSAGES NOT defined\n");
#endif
      if (addr)
        fprintf(log, "  addr->sa_family=%d\n", (int) addr->sa_family);
      fclose(log);
    }
    errno = saved_errno;
  }

  /* TODO: fix atomic value reading */
  if ((! MHD_D_IS_THREAD_SAFE_ (daemon)) &&
      (daemon->connection_limit <= daemon->connections))
    MHD_cleanup_connections (daemon);

#ifdef HAVE_MESSAGES
  if (MHD_D_IS_USING_THREADS_ (daemon) &&
      (0 == (daemon->options & MHD_USE_ITC)))
  {
    MHD_DLOG (daemon,
              _ ("MHD_add_connection() has been called for daemon started"
                 " without MHD_USE_ITC flag.\nDaemon will not process newly"
                 " added connection until any activity occurs in already"
                 " added sockets.\n"));
  }
#endif /* HAVE_MESSAGES */
  if (0 != addrlen)
  {
    if (AF_INET == addr->sa_family)
    {
      if (sizeof(struct sockaddr_in) > (size_t) addrlen)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        /* Log the invalid addrlen return */
        {
          FILE *log = fopen("/tmp/logs/3578.txt", "a");
          int saved_errno = errno;
          if (log)
          {
            fprintf(log, "MHD_add_connection: invalid addrlen for AF_INET: addrlen=%u -> returning MHD_NO\n", (unsigned) addrlen);
            fclose(log);
          }
          errno = saved_errno;
        }
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in) > (size_t) addr->sa_len) )
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        /* Log the invalid sa_len return */
        {
          FILE *log = fopen("/tmp/logs/3578.txt", "a");
          int saved_errno = errno;
          if (log)
          {
            fprintf(log, "MHD_add_connection: inconsistent sa_len for AF_INET: sa_len=%u -> returning MHD_NO\n", (unsigned) addr->sa_len);
            fclose(log);
          }
          errno = saved_errno;
        }
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_INET6
    if (AF_INET6 == addr->sa_family)
    {
      if (sizeof(struct sockaddr_in6) > (size_t) addrlen)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        {
          FILE *log = fopen("/tmp/logs/3578.txt", "a");
          int saved_errno = errno;
          if (log)
          {
            fprintf(log, "MHD_add_connection: invalid addrlen for AF_INET6: addrlen=%u -> returning MHD_NO\n", (unsigned) addrlen);
            fclose(log);
          }
          errno = saved_errno;
        }
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in6) > (size_t) addr->sa_len) )
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        {
          FILE *log = fopen("/tmp/logs/3578.txt", "a");
          int saved_errno = errno;
          if (log)
          {
            fprintf(log, "MHD_add_connection: inconsistent sa_len for AF_INET6: sa_len=%u -> returning MHD_NO\n", (unsigned) addr->sa_len);
            fclose(log);
          }
          errno = saved_errno;
        }
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
    if ((0 != addr->sa_len) &&
        (addrlen > addr->sa_len))
      addrlen = (socklen_t) addr->sa_len;   /* Use safest value */
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
#endif /* HAVE_INET6 */
  }

  if (! MHD_socket_nonblocking_ (client_socket))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set nonblocking mode on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
#endif
    sk_nonbl = false;
  }
  else
    sk_nonbl = true;

#ifndef MHD_WINSOCK_SOCKETS
  sk_spipe_supprs = false;
#else  /* MHD_WINSOCK_SOCKETS */
  sk_spipe_supprs = true; /* Nothing to suppress on W32 */
#endif /* MHD_WINSOCK_SOCKETS */
#if defined(MHD_socket_nosignal_)
  if (! sk_spipe_supprs)
    sk_spipe_supprs = MHD_socket_nosignal_ (client_socket);
  if (! sk_spipe_supprs)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to suppress SIGPIPE on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
#else  /* ! HAVE_MESSAGES */
    /* When HAVE_MESSAGES is not defined original code had a no-op here. */
    /* Add logging so this runtime path is observable for diagnostics. */
    {
      FILE *log = fopen("/tmp/logs/3578.txt", "a");
      int saved_errno = errno;
      if (log)
      {
        fprintf(log, "MHD_add_connection: reached no-op branch (HAVE_MESSAGES not defined) while sk_spipe_supprs == false\n");
        fclose(log);
      }
      errno = saved_errno;
    }
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
#ifndef MSG_NOSIGNAL
    /* Application expects that SIGPIPE will be suppressed,
     * but suppression failed and SIGPIPE cannot be suppressed with send(). */
    if (! daemon->sigpipe_blocked)
    {
      int err = MHD_socket_get_error_ ();

      /* Log the fatal close case before closing */
      {
        FILE *log = fopen("/tmp/logs/3578.txt", "a");
        int saved_errno = errno;
        if (log)
        {
          fprintf(log, "MHD_add_connection: sigpipe suppression failed and sigpipe not blocked -> closing socket %lu and returning MHD_NO (err=%d)\n",
                  (unsigned long) client_socket, err);
          fclose(log);
        }
        errno = saved_errno;
      }

      MHD_socket_close_ (client_socket);
      MHD_socket_fset_error_ (err);
      return MHD_NO;
    }
#endif /* MSG_NOSIGNAL */
  }
#endif /* MHD_socket_nosignal_ */

  if ( (0 != (daemon->options & MHD_USE_TURBO)) &&
       (! MHD_socket_noninheritable_ (client_socket)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set noninheritable mode on new client socket.\n"));
#endif
    /* Also log this non-fatal condition so it's visible in the trace */
    {
      FILE *log = fopen("/tmp/logs/3578.txt", "a");
      int saved_errno = errno;
      if (log)
      {
        fprintf(log, "MHD_add_connection: failed to set noninheritable on socket %lu\n", (unsigned long) client_socket);
        fclose(log);
      }
      errno = saved_errno;
    }
  }

  /* Copy to sockaddr_storage structure to avoid alignment problems */
  if (0 < addrlen)
    memcpy (&addrstorage, addr, (size_t) addrlen);
#ifdef HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN
  addrstorage.ss_len = addrlen; /* Force set the right length */
#endif /* HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN */

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (NULL != daemon->worker_pool)
  {
    unsigned int i;
    /* have a pool, try to find a pool with capacity; we use the
       socket as the initial offset into the pool for load
       balancing */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      struct MHD_Daemon *const worker =
        &daemon->worker_pool[(i + (unsigned int) client_socket)
                             % daemon->worker_pool_size];
      if (worker->connections < worker->connection_limit)
        return internal_add_connection (worker,
                                        client_socket,
                                        &addrstorage,
                                        addrlen,
                                        true,
                                        sk_nonbl,
                                        sk_spipe_supprs,
                                        _MHD_UNKNOWN);
    }
    /* all pools are at their connection limit, must refuse */
    MHD_socket_close_chk_ (client_socket);
#if defined(ENFILE) && (ENFILE + 0 != 0)
    errno = ENFILE;
#endif
    /* Log the refused connection due to worker pool limits */
    {
      FILE *log = fopen("/tmp/logs/3578.txt", "a");
      int saved_errno = errno;
      if (log)
      {
        fprintf(log, "MHD_add_connection: all worker pools are full -> closing socket %lu and returning MHD_NO\n", (unsigned long) client_socket);
        fclose(log);
      }
      errno = saved_errno;
    }
    return MHD_NO;
  }
#endif /* MHD_USE_POSIX_THREADS || MHD_USE_W32_THREADS */

  /* Log the final call to internal_add_connection so we can trace normal path */
  {
    FILE *log = fopen("/tmp/logs/3578.txt", "a");
    int saved_errno = errno;
    if (log)
    {
      fprintf(log, "MHD_add_connection: calling internal_add_connection(daemon=%p, socket=%lu, addrlen=%u, sk_nonbl=%d, sk_spipe_supprs=%d)\n",
              (void *) daemon,
              (unsigned long) client_socket,
              (unsigned) addrlen,
              (int) sk_nonbl,
              (int) sk_spipe_supprs);
      fclose(log);
    }
    errno = saved_errno;
  }

  return internal_add_connection (daemon,
                                  client_socket,
                                  &addrstorage,
                                  addrlen,
                                  true,
                                  sk_nonbl,
                                  sk_spipe_supprs,
                                  _MHD_UNKNOWN);
}
