_MHD_EXTERN enum MHD_Result
MHD_add_connection (struct MHD_Daemon *daemon,
                    MHD_socket client_socket,
                    const struct sockaddr *addr,
                    socklen_t addrlen)
{
  bool sk_nonbl;
  bool sk_spipe_supprs;
  struct sockaddr_storage addrstorage;
  FILE *logf = NULL;

  /* Open log file for append. Keep it best-effort: failures do not change
     control flow or behavior of the original code. */
  logf = fopen("/tmp/logs/3573.txt", "a");
  if (NULL != logf)
  {
    fprintf(logf, "MHD_add_connection ENTRY: daemon=%p client_socket=%u addr=%p addrlen=%lu\n",
            (void *)daemon,
            (unsigned int) client_socket,
            (const void *) addr,
            (unsigned long) addrlen);
    fflush(logf);
  }

  /* TODO: fix atomic value reading */
  if ((! MHD_D_IS_THREAD_SAFE_ (daemon)) &&
      (daemon->connection_limit <= daemon->connections))
    MHD_cleanup_connections (daemon);

#ifdef HAVE_MESSAGES
  if (MHD_D_IS_USING_THREADS_ (daemon) &&
      (0 == (daemon->options & MHD_USE_ITC)))
  {
    MHD_DLOG (daemon,
              _ ("MHD_add_connection() has been called for daemon started"
                 " without MHD_USE_ITC flag.\nDaemon will not process newly"
                 " added connection until any activity occurs in already"
                 " added sockets.\n"));
  }
#endif /* HAVE_MESSAGES */
  if (0 != addrlen)
  {
    if (AF_INET == addr->sa_family)
    {
      if (sizeof(struct sockaddr_in) > (size_t) addrlen)
      {
        if (NULL != logf)
        {
          fprintf(logf, "MHD_add_connection: AF_INET but addrlen (%lu) < sizeof(sockaddr_in) (%zu) -> return MHD_NO\n",
                  (unsigned long) addrlen,
                  (size_t) sizeof(struct sockaddr_in));
          fflush(logf);
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        if (NULL != logf) fclose(logf);
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in) > (size_t) addr->sa_len) )
      {
        if (NULL != logf)
        {
          fprintf(logf, "MHD_add_connection: AF_INET but sa_len (%u) is smaller than sizeof(sockaddr_in) (%zu) -> return MHD_NO\n",
                  (unsigned int) addr->sa_len,
                  (size_t) sizeof(struct sockaddr_in));
          fflush(logf);
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        if (NULL != logf) fclose(logf);
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_INET6
    if (AF_INET6 == addr->sa_family)
    {
      if (sizeof(struct sockaddr_in6) > (size_t) addrlen)
      {
        if (NULL != logf)
        {
          fprintf(logf, "MHD_add_connection: AF_INET6 but addrlen (%lu) < sizeof(sockaddr_in6) (%zu) -> return MHD_NO\n",
                  (unsigned long) addrlen,
                  (size_t) sizeof(struct sockaddr_in6));
          fflush(logf);
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        if (NULL != logf) fclose(logf);
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in6) > (size_t) addr->sa_len) )
      {
        if (NULL != logf)
        {
          fprintf(logf, "MHD_add_connection: AF_INET6 but sa_len (%u) is smaller than sizeof(sockaddr_in6) (%zu) -> return MHD_NO\n",
                  (unsigned int) addr->sa_len,
                  (size_t) sizeof(struct sockaddr_in6));
          fflush(logf);
        }
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        if (NULL != logf) fclose(logf);
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
    if ((0 != addr->sa_len) &&
        (addrlen > addr->sa_len))
      addrlen = (socklen_t) addr->sa_len;   /* Use safest value */
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
#endif /* HAVE_INET6 */
  }

  if (! MHD_socket_nonblocking_ (client_socket))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set nonblocking mode on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
#endif
    sk_nonbl = false;
  }
  else
    sk_nonbl = true;

#ifndef MHD_WINSOCK_SOCKETS
  sk_spipe_supprs = false;
#else  /* MHD_WINSOCK_SOCKETS */
  sk_spipe_supprs = true; /* Nothing to suppress on W32 */
#endif /* MHD_WINSOCK_SOCKETS */
#if defined(MHD_socket_nosignal_)
  if (! sk_spipe_supprs)
    sk_spipe_supprs = MHD_socket_nosignal_ (client_socket);
  if (! sk_spipe_supprs)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to suppress SIGPIPE on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
#ifndef MSG_NOSIGNAL
    /* Application expects that SIGPIPE will be suppressed,
     * but suppression failed and SIGPIPE cannot be suppressed with send(). */
    if (! daemon->sigpipe_blocked)
    {
      int err = MHD_socket_get_error_ ();
      MHD_socket_close_ (client_socket);
      MHD_socket_fset_error_ (err);
      if (NULL != logf)
      {
        fprintf(logf, "MHD_add_connection: SIGPIPE suppression failed and not blocked by daemon -> closing socket and return MHD_NO\n");
        fflush(logf);
        fclose(logf);
      }
      return MHD_NO;
    }
#endif /* MSG_NOSIGNAL */
  }
#endif /* MHD_socket_nosignal_ */

  if ( (0 != (daemon->options & MHD_USE_TURBO)) &&
       (! MHD_socket_noninheritable_ (client_socket)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set noninheritable mode on new client socket.\n"));
#endif
  }

  /* Copy to sockaddr_storage structure to avoid alignment problems */
  if (0 < addrlen)
  {
    if (NULL != logf)
    {
      fprintf(logf, "MHD_add_connection: preparing memcpy -> addr=%p addrlen=%lu sizeof(addrstorage)=%zu sa_family=%d",
              (const void *) addr,
              (unsigned long) addrlen,
              (size_t) sizeof(addrstorage),
              (int) addr->sa_family);
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      fprintf(logf, " sa_len=%u", (unsigned int) addr->sa_len);
#endif
      fprintf(logf, "\n");
      fflush(logf);
    }
    memcpy (&addrstorage, addr, (size_t) addrlen);
    if (NULL != logf)
    {
      fprintf(logf, "MHD_add_connection: memcpy completed -> addrstorage at %p (copied %lu bytes)\n",
              (void *) &addrstorage,
              (unsigned long) addrlen);
      fflush(logf);
    }
  }
#ifdef HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN
  addrstorage.ss_len = addrlen; /* Force set the right length */
#endif /* HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN */

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (NULL != daemon->worker_pool)
  {
    unsigned int i;
    /* have a pool, try to find a pool with capacity; we use the
       socket as the initial offset into the pool for load
       balancing */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      struct MHD_Daemon *const worker =
        &daemon->worker_pool[(i + (unsigned int) client_socket)
                             % daemon->worker_pool_size];
      if (worker->connections < worker->connection_limit)
      {
        if (NULL != logf)
        {
          fprintf(logf, "MHD_add_connection: dispatching to worker %p (index=%u) worker->connections=%u worker->connection_limit=%u sk_nonbl=%d sk_spipe_supprs=%d\n",
                  (void *) worker,
                  i,
                  (unsigned int) worker->connections,
                  (unsigned int) worker->connection_limit,
                  sk_nonbl ? 1 : 0,
                  sk_spipe_supprs ? 1 : 0);
          fflush(logf);
          fclose(logf);
          logf = NULL;
        }
        return internal_add_connection (worker,
                                        client_socket,
                                        &addrstorage,
                                        addrlen,
                                        true,
                                        sk_nonbl,
                                        sk_spipe_supprs,
                                        _MHD_UNKNOWN);
      }
    }
    /* all pools are at their connection limit, must refuse */
    MHD_socket_close_chk_ (client_socket);
#if defined(ENFILE) && (ENFILE + 0 != 0)
    errno = ENFILE;
#endif
    if (NULL != logf)
    {
      fprintf(logf, "MHD_add_connection: all worker pools busy -> close socket and return MHD_NO\n");
      fflush(logf);
      fclose(logf);
    }
    return MHD_NO;
  }
#endif /* MHD_USE_POSIX_THREADS || MHD_USE_W32_THREADS */

  if (NULL != logf)
  {
    fprintf(logf, "MHD_add_connection: calling internal_add_connection on daemon %p sk_nonbl=%d sk_spipe_supprs=%d addrlen=%lu\n",
            (void *) daemon,
            sk_nonbl ? 1 : 0,
            sk_spipe_supprs ? 1 : 0,
            (unsigned long) addrlen);
    fflush(logf);
    fclose(logf);
  }

  return internal_add_connection (daemon,
                                  client_socket,
                                  &addrstorage,
                                  addrlen,
                                  true,
                                  sk_nonbl,
                                  sk_spipe_supprs,
                                  _MHD_UNKNOWN);
}
