_MHD_EXTERN enum MHD_Result
MHD_add_connection (struct MHD_Daemon *daemon,
                    MHD_socket client_socket,
                    const struct sockaddr *addr,
                    socklen_t addrlen)
{
  bool sk_nonbl;
  bool sk_spipe_supprs;
  struct sockaddr_storage addrstorage;
  FILE *logf = NULL;

  /* Open log for tracing control flow/state for Rule 21.17 reproduction */
  logf = fopen("/tmp/logs/3573.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER MHD_add_connection: daemon=%p client_socket=%llu addr=%p addrlen=%lu\n",
            (void *) daemon,
            (unsigned long long) client_socket,
            (void *) addr,
            (unsigned long) addrlen);
  }

  /* TODO: fix atomic value reading */
  if ((! MHD_D_IS_THREAD_SAFE_ (daemon)) &&
      (daemon->connection_limit <= daemon->connections))
    MHD_cleanup_connections (daemon);

#ifdef HAVE_MESSAGES
  if (MHD_D_IS_USING_THREADS_ (daemon) &&
      (0 == (daemon->options & MHD_USE_ITC)))
  {
    MHD_DLOG (daemon,
              _ ("MHD_add_connection() has been called for daemon started"
                 " without MHD_USE_ITC flag.\nDaemon will not process newly"
                 " added connection until any activity occurs in already"
                 " added sockets.\n"));
  }
#endif /* HAVE_MESSAGES */
  if (0 != addrlen)
  {
    if (addr != NULL)
    {
      if (logf)
        fprintf(logf, "addr present: sa_family=%d\n", (int) addr->sa_family);
    }

    if (AF_INET == addr->sa_family)
    {
      if (logf)
        fprintf(logf, "AF_INET branch: sizeof(sockaddr_in)=%zu addrlen=%lu\n",
                sizeof(struct sockaddr_in), (unsigned long) addrlen);

      if (sizeof(struct sockaddr_in) > (size_t) addrlen)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        if (logf)
        {
          fprintf(logf, "RETURN MHD_NO: AF_INET addrlen too small (addrlen=%lu, need=%zu)\n",
                  (unsigned long) addrlen, sizeof(struct sockaddr_in));
          fclose(logf);
        }
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in) > (size_t) addr->sa_len) )
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        if (logf)
        {
          fprintf(logf, "RETURN MHD_NO: AF_INET sa_len mismatch (sa_len=%u, need=%zu)\n",
                  (unsigned int) addr->sa_len, sizeof(struct sockaddr_in));
          fclose(logf);
        }
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_INET6
    if (AF_INET6 == addr->sa_family)
    {
      if (logf)
        fprintf(logf, "AF_INET6 branch: sizeof(sockaddr_in6)=%zu addrlen=%lu\n",
                sizeof(struct sockaddr_in6), (unsigned long) addrlen);

      if (sizeof(struct sockaddr_in6) > (size_t) addrlen)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        if (logf)
        {
          fprintf(logf, "RETURN MHD_NO: AF_INET6 addrlen too small (addrlen=%lu, need=%zu)\n",
                  (unsigned long) addrlen, sizeof(struct sockaddr_in6));
          fclose(logf);
        }
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in6) > (size_t) addr->sa_len) )
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        if (logf)
        {
          fprintf(logf, "RETURN MHD_NO: AF_INET6 sa_len mismatch (sa_len=%u, need=%zu)\n",
                  (unsigned int) addr->sa_len, sizeof(struct sockaddr_in6));
          fclose(logf);
        }
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
    if ((0 != addr->sa_len) &&
        (addrlen > addr->sa_len))
    {
      if (logf)
        fprintf(logf, "Adjusting addrlen from %lu to sa_len %u to be safe\n",
                (unsigned long) addrlen, (unsigned int) addr->sa_len);
      addrlen = (socklen_t) addr->sa_len;   /* Use safest value */
    }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
#endif /* HAVE_INET6 */
  }

  if (! MHD_socket_nonblocking_ (client_socket))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set nonblocking mode on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
#endif
    sk_nonbl = false;
    if (logf)
      fprintf(logf, "socket nonblocking: FAILED\n");
  }
  else
  {
    sk_nonbl = true;
    if (logf)
      fprintf(logf, "socket nonblocking: OK\n");
  }

#ifndef MHD_WINSOCK_SOCKETS
  sk_spipe_supprs = false;
#else  /* MHD_WINSOCK_SOCKETS */
  sk_spipe_supprs = true; /* Nothing to suppress on W32 */
#endif /* MHD_WINSOCK_SOCKETS */
#if defined(MHD_socket_nosignal_)
  if (! sk_spipe_supprs)
    sk_spipe_supprs = MHD_socket_nosignal_ (client_socket);
  if (! sk_spipe_supprs)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to suppress SIGPIPE on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
#ifndef MSG_NOSIGNAL
    /* Application expects that SIGPIPE will be suppressed,
     * but suppression failed and SIGPIPE cannot be suppressed with send(). */
    if (! daemon->sigpipe_blocked)
    {
      int err = MHD_socket_get_error_ ();
      MHD_socket_close_ (client_socket);
      MHD_socket_fset_error_ (err);
      if (logf)
      {
        fprintf(logf, "RETURN MHD_NO: SIGPIPE suppression failed and sigpipe not blocked; socket closed\n");
        fclose(logf);
      }
      return MHD_NO;
    }
#endif /* MSG_NOSIGNAL */
  }
#endif /* MHD_socket_nosignal_ */

  if ( (0 != (daemon->options & MHD_USE_TURBO)) &&
       (! MHD_socket_noninheritable_ (client_socket)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set noninheritable mode on new client socket.\n"));
#endif
    if (logf)
      fprintf(logf, "Failed to set noninheritable mode on new client socket.\n");
  }

  /* Copy to sockaddr_storage structure to avoid alignment problems */
  if (0 < addrlen)
  {
    if (logf)
    {
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if (addr)
        fprintf(logf, "Before memcpy: addr=%p addrlen=%lu sa_len=%u\n",
                (void *) addr, (unsigned long) addrlen, (unsigned int) addr->sa_len);
      else
        fprintf(logf, "Before memcpy: addr is NULL but addrlen=%lu (we will not dereference)\n",
                (unsigned long) addrlen);
#else
      fprintf(logf, "Before memcpy: addr=%p addrlen=%lu (no sa_len available)\n",
              (void *) addr, (unsigned long) addrlen);
#endif
    }
    memcpy (&addrstorage, addr, (size_t) addrlen);
    if (logf)
      fprintf(logf, "Performed memcpy(&addrstorage, addr, %lu)\n", (unsigned long) addrlen);
  }
  else
  {
    if (logf)
      fprintf(logf, "Skipping memcpy because addrlen=%lu\n", (unsigned long) addrlen);
  }
#ifdef HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN
  addrstorage.ss_len = addrlen; /* Force set the right length */
#endif /* HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN */

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (NULL != daemon->worker_pool)
  {
    unsigned int i;
    /* have a pool, try to find a pool with capacity; we use the
       socket as the initial offset into the pool for load
       balancing */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      struct MHD_Daemon *const worker =
        &daemon->worker_pool[(i + (unsigned int) client_socket)
                             % daemon->worker_pool_size];
      if (worker->connections < worker->connection_limit)
      {
        if (logf)
        {
          fprintf(logf, "Forwarding to worker internal_add_connection: worker=%p client_socket=%llu addrstorage=%p addrlen=%lu sk_nonbl=%d sk_spipe_supprs=%d\n",
                  (void *) worker,
                  (unsigned long long) client_socket,
                  (void *) &addrstorage,
                  (unsigned long) addrlen,
                  sk_nonbl ? 1 : 0,
                  sk_spipe_supprs ? 1 : 0);
          fclose(logf);
        }
        return internal_add_connection (worker,
                                        client_socket,
                                        &addrstorage,
                                        addrlen,
                                        true,
                                        sk_nonbl,
                                        sk_spipe_supprs,
                                        _MHD_UNKNOWN);
      }
    }
    /* all pools are at their connection limit, must refuse */
    MHD_socket_close_chk_ (client_socket);
#if defined(ENFILE) && (ENFILE + 0 != 0)
    errno = ENFILE;
#endif
    if (logf)
    {
      fprintf(logf, "RETURN MHD_NO: all worker pools full; closed socket\n");
      fclose(logf);
    }
    return MHD_NO;
  }
#endif /* MHD_USE_POSIX_THREADS || MHD_USE_W32_THREADS */

  if (logf)
  {
    fprintf(logf, "Forwarding to daemon internal_add_connection: daemon=%p client_socket=%llu addrstorage=%p addrlen=%lu sk_nonbl=%d sk_spipe_supprs=%d\n",
            (void *) daemon,
            (unsigned long long) client_socket,
            (void *) &addrstorage,
            (unsigned long) addrlen,
            sk_nonbl ? 1 : 0,
            sk_spipe_supprs ? 1 : 0);
    fclose(logf);
  }

  return internal_add_connection (daemon,
                                  client_socket,
                                  &addrstorage,
                                  addrlen,
                                  true,
                                  sk_nonbl,
                                  sk_spipe_supprs,
                                  _MHD_UNKNOWN);
}
