bool
MHD_add_response_entry_no_alloc_ (struct MHD_Response *response,
                                  enum MHD_ValueKind kind,
                                  char *header,
                                  size_t header_len,
                                  char *content,
                                  size_t content_len)
{
  struct MHD_HTTP_Res_Header *hdr;

  /* Log entry with parameters to help reproduce the violation scenario. */
  {
    FILE *lf = fopen("/tmp/logs/665.txt", "a");
    if (NULL != lf)
      {
        fprintf(lf, "ENTER: MHD_add_response_entry_no_alloc_: response=%p kind=%d header=%p header_len=%zu content=%p content_len=%zu\n",
                (void *) response, (int) kind, (void *) header, header_len, (void *) content, content_len);
        fclose(lf);
      }
  }

  mhd_assert (0 != header_len);
  mhd_assert (0 != content_len);
  if (NULL == (hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header))))
    {
      /* Log allocation failure so callers can see that path. */
      FILE *lf = fopen("/tmp/logs/665.txt", "a");
      if (NULL != lf)
        {
          fprintf(lf, "ALLOC_FAIL: MHD_calloc_ returned NULL (requested=%zu bytes)\n",
                  sizeof (struct MHD_HTTP_Res_Header));
          fclose(lf);
        }
      return false;
    }

  /* Log the newly allocated header pointer. */
  {
    FILE *lf = fopen("/tmp/logs/665.txt", "a");
    if (NULL != lf)
      {
        fprintf(lf, "ALLOC_OK: hdr=%p (struct MHD_HTTP_Res_Header)\n", (void *) hdr);
        fclose(lf);
      }
  }

  hdr->header = header;
  hdr->header_size = header_len;
  hdr->value = content; /* Potential MISRA 8.13 scenario: storing non-const char * into header->value */
  hdr->value_size = content_len;
  hdr->kind = kind;

  /* Log assignments to capture the state that demonstrates the violation scenario. */
  {
    FILE *lf = fopen("/tmp/logs/665.txt", "a");
    if (NULL != lf)
      {
        fprintf(lf, "ASSIGNED: hdr=%p header=%p header_size=%zu value=%p value_size=%zu kind=%d\n",
                (void *) hdr,
                (void *) hdr->header, hdr->header_size,
                (void *) hdr->value, hdr->value_size,
                (int) hdr->kind);
        /* For additional visibility, attempt to log a small, safe prefix of header/content if printable. */
        if (header != NULL && header_len > 0)
          fprintf(lf, "ASSIGNED_HEADER_PREFIX: '%.*s' (len=%zu)\n", (int) (header_len < 64 ? header_len : 64), header, header_len);
        if (content != NULL && content_len > 0)
          fprintf(lf, "ASSIGNED_CONTENT_PREFIX: '%.*s' (len=%zu)\n", (int) (content_len < 64 ? content_len : 64), content, content_len);
        fclose(lf);
      }
  }

  _MHD_insert_header_last (response, hdr);

  /* Log successful insertion and exit. */
  {
    FILE *lf = fopen("/tmp/logs/665.txt", "a");
    if (NULL != lf)
      {
        fprintf(lf, "EXIT_SUCCESS: header entry inserted, hdr=%p\n", (void *) hdr);
        fclose(lf);
      }
  }

  return true; /* Success exit point */
}
