bool
MHD_add_response_entry_no_alloc_ (struct MHD_Response *response,
                                  enum MHD_ValueKind kind,
                                  char *header,
                                  size_t header_len,
                                  char *content,
                                  size_t content_len)
{
  struct MHD_HTTP_Res_Header *hdr;
  FILE *log = fopen("/tmp/logs/665.txt", "a");

  if (log)
    fprintf(log, "ENTER: MHD_add_response_entry_no_alloc_ (response=%p, kind=%d, header=%p, header_len=%zu, content=%p, content_len=%zu)\n",
            (void *) response, (int) kind, (void *) header, header_len, (void *) content, content_len);

  mhd_assert (0 != header_len);
  mhd_assert (0 != content_len);
  if (NULL == (hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header))))
  {
    if (log)
      fprintf(log, "ALLOC_FAIL: MHD_calloc_ returned NULL\n");
    if (log)
      fclose(log);
    return false;
  }

  if (log)
    fprintf(log, "ALLOC_OK: hdr=%p (allocated %zu bytes)\n", (void *) hdr, sizeof (struct MHD_HTTP_Res_Header));

  /* Record state before assignments */
  if (log)
    fprintf(log, "BEFORE_ASSIGN: hdr->header=%p, hdr->header_size=%zu, hdr->value=%p, hdr->value_size=%zu, hdr->kind=%d\n",
            (void *) hdr->header, (size_t) hdr->header_size, (void *) hdr->value, (size_t) hdr->value_size, (int) hdr->kind);

  hdr->header = header;
  hdr->header_size = header_len;
  hdr->value = content;
  hdr->value_size = content_len;
  hdr->kind = kind;

  /* Record state after assignments to capture the potential violation scenario where non-const pointers are stored without modification */
  if (log)
  {
    fprintf(log, "AFTER_ASSIGN: hdr=%p, hdr->header=%p, hdr->header_size=%zu, hdr->value=%p, hdr->value_size=%zu, hdr->kind=%d\n",
            (void *) hdr, (void *) hdr->header, hdr->header_size, (void *) hdr->value, hdr->value_size, (int) hdr->kind);
  }

  _MHD_insert_header_last (response, hdr);

  if (log)
  {
    fprintf(log, "INSERTED: header node %p inserted into response %p\n", (void *) hdr, (void *) response);
    fclose(log);
  }

  return true; /* Success exit point */
}
