bool
MHD_add_response_entry_no_alloc_ (struct MHD_Response *response,
                                  enum MHD_ValueKind kind,
                                  char *header,
                                  size_t header_len,
                                  char *content,
                                  size_t content_len)
{
  struct MHD_HTTP_Res_Header *hdr;
  FILE *logf = fopen("/tmp/logs/665.txt", "a");

  if (logf)
  {
    fprintf(logf, "ENTER: MHD_add_response_entry_no_alloc_: response=%p, kind=%d, header=%p, header_len=%zu, content=%p, content_len=%zu\n",
            (void *) response, (int) kind, (void *) header, header_len, (void *) content, content_len);
    fflush(logf);
  }

  mhd_assert (0 != header_len);
  mhd_assert (0 != content_len);
  if (NULL == (hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header))))
  {
    if (logf)
    {
      fprintf(logf, "ALLOC FAIL: could not allocate MHD_HTTP_Res_Header\n");
      fclose(logf);
    }
    return false;
  }

  if (logf)
    fprintf(logf, "ALLOC OK: hdr=%p\n", (void *) hdr);

  hdr->header = header;
  hdr->header_size = header_len;
  hdr->value = content;
  hdr->value_size = content_len;
  hdr->kind = kind;

  if (logf)
  {
    fprintf(logf, "ASSIGN: hdr->header=%p hdr->header_size=%zu hdr->value=%p hdr->value_size=%zu hdr->kind=%d\n",
            (void *) hdr->header, hdr->header_size, (void *) hdr->value, hdr->value_size, (int) hdr->kind);
    fflush(logf);
  }

  _MHD_insert_header_last (response, hdr);

  if (logf)
  {
    fprintf(logf, "INSERTED: header inserted into response (response=%p). Exiting true\n", (void *) response);
    fclose(logf);
  }

  return true; /* Success exit point */
}
