bool
MHD_add_response_entry_no_check_ (struct MHD_Response *response,
                                  enum MHD_ValueKind kind,
                                  const char *header,
                                  size_t header_len,
                                  const char *content,
                                  size_t content_len)
{
  char *header_malloced;
  char *value_malloced;
  FILE *log = fopen("/tmp/logs/1478.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER MHD_add_response_entry_no_check_: response=%p kind=%d header=%p header_len=%zu content=%p content_len=%zu\n",
            (void *) response, (int) kind, (const void *) header, header_len, (const void *) content, content_len);
  }

  mhd_assert (0 != header_len);
  if (log)
    fprintf(log, "mhd_assert(header_len) executed; header_len=%zu\n", header_len);

  mhd_assert (0 != content_len);
  if (log)
    fprintf(log, "mhd_assert(content_len) executed; content_len=%zu\n", content_len);

  header_malloced = malloc (header_len + 1);
  if (log)
    fprintf(log, "malloc(header): requested=%zu -> returned=%p\n", header_len + 1, (void *) header_malloced);

  if (NULL == header_malloced)
  {
    if (log)
    {
      fprintf(log, "header_malloced == NULL -> returning false\n");
      fclose(log);
    }
    return false;
  }

  memcpy (header_malloced, header, header_len);
  header_malloced[header_len] = 0;
  if (log)
    fprintf(log, "copied header -> header_malloced=%p header_str=\"%s\" header_len=%zu\n",
            (void *) header_malloced, header_malloced, header_len);

  value_malloced = malloc (content_len + 1);
  if (log)
    fprintf(log, "malloc(value): requested=%zu -> returned=%p\n", content_len + 1, (void *) value_malloced);

  if (NULL != value_malloced)
  {
    memcpy (value_malloced, content, content_len);
    value_malloced[content_len] = 0;
    if (log)
      fprintf(log, "copied content -> value_malloced=%p value_str=\"%s\" content_len=%zu\n",
              (void *) value_malloced, value_malloced, content_len);

    if (log)
      fprintf(log, "calling MHD_add_response_entry_no_alloc_(...)\n");

    if (MHD_add_response_entry_no_alloc_ (response, kind,
                                          header_malloced, header_len,
                                          value_malloced, content_len))
    {
      if (log)
      {
        fprintf(log, "MHD_add_response_entry_no_alloc_ returned true -> returning true (success)\n");
        fclose(log);
      }
      return true; /* Success exit point */
    }

    if (log)
      fprintf(log, "MHD_add_response_entry_no_alloc_ returned false -> freeing value_malloced and continuing\n");

    free (value_malloced);
  }
  else
  {
    if (log)
      fprintf(log, "value_malloced == NULL -> will free header and return false\n");
  }

  free (header_malloced);
  if (log)
  {
    fprintf(log, "freed header_malloced=%p -> returning false (failure)\n", (void *) header_malloced);
    fclose(log);
  }

  return false; /* Failure exit point */
}
