bool
MHD_add_response_entry_no_check_ (struct MHD_Response *response,
                                  enum MHD_ValueKind kind,
                                  const char *header,
                                  size_t header_len,
                                  const char *content,
                                  size_t content_len)
{
  char *header_malloced;
  char *value_malloced;

  /* Log entry and the important state for reproducing the dead-code scenario. */
  {
    FILE *log = fopen("/tmp/logs/1478.txt", "a");
    if (log)
    {
      fprintf(log, "ENTER: MHD_add_response_entry_no_check_\n");
      fprintf(log, " state: header=%p, header_len=%zu, content=%p, content_len=%zu\n",
              (const void *)header, header_len, (const void *)content, content_len);
      fclose(log);
    }
  }

  mhd_assert (0 != header_len);
  /* Log that the header_len assertion point was reached. */
  {
    FILE *log = fopen("/tmp/logs/1478.txt", "a");
    if (log)
    {
      fprintf(log, "ASSERT_CHECKED: header_len != 0 (evaluated as %zu)\n", header_len);
      fclose(log);
    }
  }

  mhd_assert (0 != content_len);
  /* Log that the content_len assertion point was reached. This is the statement
     that may expand to nothing in production builds; we record that it was executed
     and the value observed, so other agents can reproduce the reported violation. */
  {
    FILE *log = fopen("/tmp/logs/1478.txt", "a");
    if (log)
    {
      fprintf(log, "ASSERT_CHECKED: content_len != 0 (evaluated as %zu)\n", content_len);
      fclose(log);
    }
  }

  header_malloced = malloc (header_len + 1);
  /* Log allocation result for header_malloced. */
  {
    FILE *log = fopen("/tmp/logs/1478.txt", "a");
    if (log)
    {
      fprintf(log, "ALLOC: header_malloced=%p (request=%zu)\n",
              (void *)header_malloced, header_len + 1);
      fclose(log);
    }
  }

  if (NULL == header_malloced)
    return false;

  memcpy (header_malloced, header, header_len);
  header_malloced[header_len] = 0;

  value_malloced = malloc (content_len + 1);
  /* Log allocation result for value_malloced. */
  {
    FILE *log = fopen("/tmp/logs/1478.txt", "a");
    if (log)
    {
      fprintf(log, "ALLOC: value_malloced=%p (request=%zu)\n",
              (void *)value_malloced, content_len + 1);
      fclose(log);
    }
  }

  if (NULL != value_malloced)
  {
    memcpy (value_malloced, content, content_len);
    value_malloced[content_len] = 0;

    /* Before calling the helper, log the parameters. */
    {
      FILE *log = fopen("/tmp/logs/1478.txt", "a");
      if (log)
      {
        fprintf(log, "CALL: MHD_add_response_entry_no_alloc_(response=%p, kind=%d, header_malloced=%p, header_len=%zu, value_malloced=%p, content_len=%zu)\n",
                (void *)response, (int)kind, (void *)header_malloced, header_len, (void *)value_malloced, content_len);
        fclose(log);
      }
    }

    if (MHD_add_response_entry_no_alloc_ (response, kind,
                                          header_malloced, header_len,
                                          value_malloced, content_len))
    {
      /* Log successful path before returning. */
      FILE *log = fopen("/tmp/logs/1478.txt", "a");
      if (log)
      {
        fprintf(log, "EXIT: Success (returned true)\n");
        fclose(log);
      }
      return true; /* Success exit point */
    }

    free (value_malloced);
    /* Log that value_malloced was freed after failure. */
    {
      FILE *log = fopen("/tmp/logs/1478.txt", "a");
      if (log)
      {
        fprintf(log, "FREE: value_malloced freed after failed add (address=%p)\n", (void *)value_malloced);
        fclose(log);
      }
    }
  }
  free (header_malloced);

  /* Log final failure exit. */
  {
    FILE *log = fopen("/tmp/logs/1478.txt", "a");
    if (log)
    {
      fprintf(log, "EXIT: Failure (returned false)\n");
      fclose(log);
    }
  }

  return false; /* Failure exit point */
}
