size_t
MHD_bin_to_hex_z (const void *bin,
                  size_t size,
                  char *hex)
{
  size_t res;
  FILE *logf = NULL;

  /* Log entry with input state */
  logf = fopen("/tmp/logs/633.txt", "a");
  if (logf)
    {
      fprintf(logf, "MHD_bin_to_hex_z ENTER: bin=%p size=%zu hex=%p\n",
              bin, size, (void *)hex);
      fclose(logf);
    }

  /* Preserve original behavior: call underlying converter */
  res = MHD_bin_to_hex (bin, size, hex);

  /* Log return value from MHD_bin_to_hex and intended NUL write index */
  logf = fopen("/tmp/logs/633.txt", "a");
  if (logf)
    {
      fprintf(logf, "MHD_bin_to_hex returned res=%zu; will write NUL at hex[%zu]\n",
              res, res);
      fclose(logf);
    }

  /* Original code: write terminating NUL (may be out-of-bounds if caller
     provided exactly res bytes). We keep this to reproduce the scenario. */
  hex[res] = 0;

  /* Log post-write state: pointer value written and surrounding bytes (if
     readable). We attempt to print the byte we just wrote. */
  logf = fopen("/tmp/logs/633.txt", "a");
  if (logf)
    {
      /* Print the written character as an integer to avoid issues with
         non-printables; also print the pointer and index for correlation. */
      fprintf(logf, "After NUL write: hex=%p written_index=%zu written_value=%d\n",
              (void *)hex, res, (int) hex[res]);
      fclose(logf);
    }

  return res;
}
