static void
MHD_cleanup_connections (struct MHD_Daemon *daemon)
{
  struct MHD_Connection *pos;
  FILE *logfp = NULL;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
  mhd_assert (NULL == daemon->worker_pool);

  MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
#endif
  /* Open a log file to capture control flow and state for debugging the
     potential dead-assignment scenario. Keep the file open for the
     duration of the cleanup to reduce overhead. */
  logfp = fopen("/tmp/logs/2768.txt", "a");
  if (logfp)
    fprintf (logfp, "MHD_cleanup_connections: entered daemon=%p\n", (void *) daemon);

  while (NULL != (pos = daemon->cleanup_tail))
  {
    if (logfp)
      fprintf (logfp, "Loop begin: pos=%p, pos->socket_fd=%d\n", (void *) pos, (int) pos->socket_fd);

    DLL_remove (daemon->cleanup_head,
                daemon->cleanup_tail,
                pos);
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
    if (MHD_D_IS_USING_THREAD_PER_CONN_ (daemon) &&
        (! pos->thread_joined) &&
        (! MHD_thread_handle_ID_join_thread_ (pos->tid)) )
      MHD_PANIC (_ ("Failed to join a thread.\n"));
#endif
#ifdef UPGRADE_SUPPORT
    cleanup_upgraded_connection (pos);
#endif /* UPGRADE_SUPPORT */
    MHD_pool_destroy (pos->pool);
#ifdef HTTPS_SUPPORT
    if (NULL != pos->tls_session)
      gnutls_deinit (pos->tls_session);
#endif /* HTTPS_SUPPORT */

    /* clean up the connection */
    if (NULL != daemon->notify_connection)
      daemon->notify_connection (daemon->notify_connection_cls,
                                 pos,
                                 &pos->socket_context,
                                 MHD_CONNECTION_NOTIFY_CLOSED);
    MHD_ip_limit_del (daemon,
                      pos->addr,
                      pos->addr_len);
#ifdef EPOLL_SUPPORT
    if (MHD_D_IS_USING_EPOLL_ (daemon))
    {
      if (0 != (pos->epoll_state & MHD_EPOLL_STATE_IN_EREADY_EDLL))
      {
        EDLL_remove (daemon->eready_head,
                     daemon->eready_tail,
                     pos);
        pos->epoll_state &=
          ~((enum MHD_EpollState) MHD_EPOLL_STATE_IN_EREADY_EDLL);
      }
      if ( (-1 != daemon->epoll_fd) &&
           (0 != (pos->epoll_state & MHD_EPOLL_STATE_IN_EPOLL_SET)) )
      {
        /* epoll documentation suggests that closing a FD
           automatically removes it from the epoll set; however,
           this is not true as if we fail to do manually remove it,
           we are still seeing an event for this fd in epoll,
           causing grief (use-after-free...) --- at least on my
           system. */
        if (0 != epoll_ctl (daemon->epoll_fd,
                            EPOLL_CTL_DEL,
                            pos->socket_fd,
                            NULL))
          MHD_PANIC (_ ("Failed to remove FD from epoll set.\n"));
        pos->epoll_state &=
          ~((enum MHD_EpollState)
            MHD_EPOLL_STATE_IN_EPOLL_SET);
      }
    }
#endif
    if (NULL != pos->rp.response)
    {
      if (logfp)
        fprintf (logfp, "pos=%p: response before destroy=%p\n", (void *) pos, (void *) pos->rp.response);
      MHD_destroy_response (pos->rp.response);
      if (logfp)
        fprintf (logfp, "pos=%p: after destroy response (raw read)=%p\n", (void *) pos, (void *) pos->rp.response);

      /* The following assignment is the subject of the reported violation
         (pos->rp.response = NULL). We log before and after the assignment
         to provide evidence whether the assignment is observable before
         the pos object is freed. */
      if (logfp)
        fprintf (logfp, "pos=%p: assigning pos->rp.response = NULL (about to do so)\n", (void *) pos);

      pos->rp.response = NULL;

      if (logfp)
        fprintf (logfp, "pos=%p: assigned pos->rp.response = NULL (now %p)\n", (void *) pos, (void *) pos->rp.response);
    }
    else
    {
      if (logfp)
        fprintf (logfp, "pos=%p: no response present (pos->rp.response == NULL)\n", (void *) pos);
    }
    if (MHD_INVALID_SOCKET != pos->socket_fd)
      MHD_socket_close_chk_ (pos->socket_fd);
    if (NULL != pos->addr)
      free (pos->addr);

    /* Save pointer value for logging after free (do not dereference). */
    if (logfp)
      fprintf (logfp, "pos=%p: about to free connection object\n", (void *) pos);
    {
      void *pos_ptr_for_log = (void *) pos;
      free (pos);
      if (logfp)
        fprintf (logfp, "pos freed: %p\n", pos_ptr_for_log);
    }

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
#endif
    daemon->connections--;
    daemon->at_limit = false;

    if (logfp)
      fflush (logfp);
  }
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#endif

  if (logfp)
  {
    fprintf (logfp, "MHD_cleanup_connections: exit daemon=%p, connections=%d\n", (void *) daemon, (int) daemon->connections);
    fclose (logfp);
  }
}
