static void
MHD_cleanup_connections (struct MHD_Daemon *daemon)
{
  struct MHD_Connection *pos;
  FILE *logf = NULL;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
  mhd_assert (NULL == daemon->worker_pool);

  MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
#endif
  /* open log file for appending control-flow / state info */
  logf = fopen("/tmp/logs/2765.txt", "a");
  if (logf)
  {
    fprintf (logf, "ENTER MHD_cleanup_connections: daemon=%p cleanup_head=%p cleanup_tail=%p connections=%d at_limit=%d\n",
             (void *) daemon,
             (void *) daemon->cleanup_head,
             (void *) daemon->cleanup_tail,
             (int) daemon->connections,
             (int) daemon->at_limit);
    fflush (logf);
  }

  while (NULL != (pos = daemon->cleanup_tail))
  {
    if (logf)
    {
      fprintf (logf, "LOOP: processing pos=%p pos->addr=%p pos->addr_len=%lu socket_fd=%ld rp.response=%p epoll_state=0x%x thread_joined=%d tid=%p\n",
               (void *) pos,
               (void *) pos->addr,
               (unsigned long) pos->addr_len,
               (long) pos->socket_fd,
               (void *) pos->rp.response,
               (unsigned int) pos->epoll_state,
               (int) pos->thread_joined,
               (void *) &pos->tid);
      fflush (logf);
    }

    DLL_remove (daemon->cleanup_head,
                daemon->cleanup_tail,
                pos);
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
    if (MHD_D_IS_USING_THREAD_PER_CONN_ (daemon) &&
        (! pos->thread_joined) &&
        (! MHD_thread_handle_ID_join_thread_ (pos->tid)) )
      MHD_PANIC (_ ("Failed to join a thread.\n"));
#endif
#ifdef UPGRADE_SUPPORT
    cleanup_upgraded_connection (pos);
#endif /* UPGRADE_SUPPORT */
    MHD_pool_destroy (pos->pool);
#ifdef HTTPS_SUPPORT
    if (NULL != pos->tls_session)
      gnutls_deinit (pos->tls_session);
#endif /* HTTPS_SUPPORT */

    /* clean up the connection */
    if (NULL != daemon->notify_connection)
      daemon->notify_connection (daemon->notify_connection_cls,
                                 pos,
                                 &pos->socket_context,
                                 MHD_CONNECTION_NOTIFY_CLOSED);

    /* Log immediately before calling MHD_ip_limit_del so the pointer
       value and length are captured for later inspection. This helps
       reproduce the scenario where a non-const pointer field
       (pos->addr) is passed to a function which only reads it. */
    if (logf)
    {
      fprintf (logf, "BEFORE MHD_ip_limit_del: daemon=%p pos=%p pos->addr=%p pos->addr_len=%lu\n",
               (void *) daemon,
               (void *) pos,
               (void *) pos->addr,
               (unsigned long) pos->addr_len);
      fflush (logf);
    }

    MHD_ip_limit_del (daemon,
                      pos->addr,
                      pos->addr_len);

    if (logf)
    {
      fprintf (logf, "AFTER MHD_ip_limit_del: daemon=%p pos=%p pos->addr=%p pos->addr_len=%lu\n",
               (void *) daemon,
               (void *) pos,
               (void *) pos->addr,
               (unsigned long) pos->addr_len);
      fflush (logf);
    }

#ifdef EPOLL_SUPPORT
    if (MHD_D_IS_USING_EPOLL_ (daemon))
    {
      if (0 != (pos->epoll_state & MHD_EPOLL_STATE_IN_EREADY_EDLL))
      {
        EDLL_remove (daemon->eready_head,
                     daemon->eready_tail,
                     pos);
        pos->epoll_state &=
          ~((enum MHD_EpollState) MHD_EPOLL_STATE_IN_EREADY_EDLL);
      }
      if ( (-1 != daemon->epoll_fd) &&
           (0 != (pos->epoll_state & MHD_EPOLL_STATE_IN_EPOLL_SET)) )
      {
        /* epoll documentation suggests that closing a FD
           automatically removes it from the epoll set; however,
           this is not true as if we fail to do manually remove it,
           we are still seeing an event for this fd in epoll,
           causing grief (use-after-free...) --- at least on my
           system. */
        if (0 != epoll_ctl (daemon->epoll_fd,
                            EPOLL_CTL_DEL,
                            pos->socket_fd,
                            NULL))
          MHD_PANIC (_ ("Failed to remove FD from epoll set.\n"));
        pos->epoll_state &=
          ~((enum MHD_EpollState)
            MHD_EPOLL_STATE_IN_EPOLL_SET);
      }
    }
#endif
    if (NULL != pos->rp.response)
    {
      MHD_destroy_response (pos->rp.response);
      pos->rp.response = NULL;
    }
    if (MHD_INVALID_SOCKET != pos->socket_fd)
      MHD_socket_close_chk_ (pos->socket_fd);

    if (NULL != pos->addr)
    {
      if (logf)
      {
        fprintf (logf, "FREEING pos->addr: pos=%p addr=%p addr_len=%lu\n",
                 (void *) pos,
                 (void *) pos->addr,
                 (unsigned long) pos->addr_len);
        fflush (logf);
      }
      free (pos->addr);
      if (logf)
      {
        fprintf (logf, "FREED pos->addr: pos=%p addr(now invalid)=%p\n",
                 (void *) pos,
                 (void *) pos->addr);
        fflush (logf);
      }
    }
    free (pos);

    if (logf)
    {
      fprintf (logf, "FREED pos: remaining connections before decrement=%d\n",
               (int) daemon->connections);
      fflush (logf);
    }

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
#endif
    daemon->connections--;
    daemon->at_limit = false;
  }
  if (logf)
  {
    fprintf (logf, "EXIT MHD_cleanup_connections: daemon=%p connections=%d at_limit=%d cleanup_head=%p cleanup_tail=%p\n",
             (void *) daemon,
             (int) daemon->connections,
             (int) daemon->at_limit,
             (void *) daemon->cleanup_head,
             (void *) daemon->cleanup_tail);
    fclose (logf);
  }
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#endif
}
