void
MHD_connection_handle_read (struct MHD_Connection *connection,
                            bool socket_error)
{
  ssize_t bytes_read;
  /* Log file probe: append-only so multiple calls are recorded. */
  {
    FILE *logf = fopen("/tmp/logs/1816.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "ENTER: MHD_connection_handle_read: connection=%p state=%d suspended=%d socket_error=%d\n",
              (void *)connection,
              (int)((connection) ? connection->state : -1),
              (int)((connection) ? connection->suspended : -1),
              (int)socket_error);
      fclose(logf);
    }
  }

  if ( (MHD_CONNECTION_CLOSED == connection->state) ||
       (connection->suspended) )
  {
    /* Log early return reason for reproducibility of unreachable-case finding */
    FILE *logf = fopen("/tmp/logs/1816.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "EARLY RETURN: connection->state=%d suspended=%d -> returning immediately\n",
              (int)connection->state, (int)connection->suspended);
      fclose(logf);
    }
    return;
  }
#ifdef HTTPS_SUPPORT
  if (MHD_TLS_CONN_NO_TLS != connection->tls_state)
  {   /* HTTPS connection. */
    if (MHD_TLS_CONN_CONNECTED > connection->tls_state)
    {
      if (! MHD_run_tls_handshake_ (connection))
      {
        FILE *logf = fopen("/tmp/logs/1816.txt", "a");
        if (NULL != logf)
        {
          fprintf(logf, "TLS HANDSHAKE: incomplete, returning (tls_state=%d)\n",
                  (int)connection->tls_state);
          fclose(logf);
        }
        return;
      }
    }
  }
#endif /* HTTPS_SUPPORT */

  mhd_assert (NULL != connection->read_buffer);
  if (connection->read_buffer_size == connection->read_buffer_offset)
  {
    FILE *logf = fopen("/tmp/logs/1816.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "NO SPACE: read_buffer_size=%zu read_buffer_offset=%zu -> returning\n",
              connection->read_buffer_size, connection->read_buffer_offset);
      fclose(logf);
    }
    return; /* No space for receiving data. */
  }

  bytes_read = connection->recv_cls (connection,
                                     &connection->read_buffer
                                     [connection->read_buffer_offset],
                                     connection->read_buffer_size
                                     - connection->read_buffer_offset);
  {
    FILE *logf = fopen("/tmp/logs/1816.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "RECV: bytes_read=%zd sk_nonblck=%d\n", bytes_read, (int)connection->sk_nonblck);
      fclose(logf);
    }
  }
  if ((bytes_read < 0) || socket_error)
  {
    if ((MHD_ERR_AGAIN_ == bytes_read) && ! socket_error)
    {
      FILE *logf = fopen("/tmp/logs/1816.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "ERR_AGAIN: no new data -> returning\n");
        fclose(logf);
      }
      return;     /* No new data to process. */
    }
    if ((bytes_read > 0) && connection->sk_nonblck)
    { /* Try to detect the socket error */
      int dummy;
      bytes_read = connection->recv_cls (connection, &dummy, sizeof (dummy));
      {
        FILE *logf = fopen("/tmp/logs/1816.txt", "a");
        if (NULL != logf)
        {
          fprintf(logf, "NONBLOCK RECV PROBE: probe bytes_read=%zd\n", bytes_read);
          fclose(logf);
        }
      }
    }
    if (MHD_ERR_CONNRESET_ == bytes_read)
    {
      if ( (MHD_CONNECTION_INIT < connection->state) &&
           (MHD_CONNECTION_FULL_REQ_RECEIVED > connection->state) )
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (connection->daemon,
                  _ ("Socket has been disconnected when reading request.\n"));
#endif
        connection->discard_request = true;
      }
      {
        FILE *logf = fopen("/tmp/logs/1816.txt", "a");
        if (NULL != logf)
        {
          fprintf(logf, "CONNRESET: closing connection with TERMINATED_READ_ERROR\n");
          fclose(logf);
        }
      }
      MHD_connection_close_ (connection,
                             MHD_REQUEST_TERMINATED_READ_ERROR);
      return;
    }

#ifdef HAVE_MESSAGES
    if (MHD_CONNECTION_INIT != connection->state)
      MHD_DLOG (connection->daemon,
                _ ("Connection socket is closed when reading " \
                   "request due to the error: %s\n"),
                (bytes_read < 0) ? str_conn_error_ (bytes_read) :
                "detected connection closure");
#endif
    CONNECTION_CLOSE_ERROR (connection,
                            NULL);
    {
      FILE *logf = fopen("/tmp/logs/1816.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "SOCKET ERROR: invoked CONNECTION_CLOSE_ERROR -> returning\n");
        fclose(logf);
      }
    }
    return;
  }

  if (0 == bytes_read)
  {   /* Remote side closed connection. */
    connection->read_closed = true;
    {
      FILE *logf = fopen("/tmp/logs/1816.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "REMOTE CLOSED: state=%d -> read_closed set, handling termination\n", (int)connection->state);
        fclose(logf);
      }
    }
    if ( (MHD_CONNECTION_INIT < connection->state) &&
         (MHD_CONNECTION_FULL_REQ_RECEIVED > connection->state) )
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (connection->daemon,
                _ ("Connection was closed by remote side with incomplete "
                   "request.\n"));
#endif
      connection->discard_request = true;
      MHD_connection_close_ (connection,
                             MHD_REQUEST_TERMINATED_CLIENT_ABORT);
    }
    else if (MHD_CONNECTION_INIT == connection->state)
      /* This termination code cannot be reported to the application
       * because application has not been informed yet about this request */
      MHD_connection_close_ (connection,
                             MHD_REQUEST_TERMINATED_COMPLETED_OK);
    else
      MHD_connection_close_ (connection,
                             MHD_REQUEST_TERMINATED_WITH_ERROR);
    return;
  }
  connection->read_buffer_offset += (size_t) bytes_read;
  MHD_update_last_activity_ (connection);
#if DEBUG_STATES
  MHD_DLOG (connection->daemon,
            _ ("In function %s handling connection at state: %s\n"),
            MHD_FUNC_,
            MHD_state_to_string (connection->state));
#endif
  /* TODO: check whether the next 'switch()' really needed */
  {
    FILE *logf = fopen("/tmp/logs/1816.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "BEFORE SWITCH: state=%d read_closed=%d read_buffer_offset=%zu\n",
              (int)connection->state, (int)connection->read_closed, connection->read_buffer_offset);
      fclose(logf);
    }
  }
  switch (connection->state)
  {
  case MHD_CONNECTION_INIT:
  case MHD_CONNECTION_REQ_LINE_RECEIVING:
  case MHD_CONNECTION_REQ_HEADERS_RECEIVING:
  case MHD_CONNECTION_BODY_RECEIVING:
  case MHD_CONNECTION_FOOTERS_RECEIVING:
  case MHD_CONNECTION_FULL_REQ_RECEIVED:
    /* nothing to do but default action */
    if (connection->read_closed)
    {
      /* TODO: check whether this really needed */
      MHD_connection_close_ (connection,
                             MHD_REQUEST_TERMINATED_READ_ERROR);
    }
    return;
  case MHD_CONNECTION_CLOSED:
    /* This case is likely unreachable because of the early return above. */
    {
      FILE *logf = fopen("/tmp/logs/1816.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "SWITCH CASE: MHD_CONNECTION_CLOSED reached inside switch -> returning\n");
        fclose(logf);
      }
    }
    return;
#ifdef UPGRADE_SUPPORT
  case MHD_CONNECTION_UPGRADE:
    mhd_assert (0);
    return;
#endif /* UPGRADE_SUPPORT */
  case MHD_CONNECTION_START_REPLY:
    /* shrink read buffer to how much is actually used */
    /* TODO: remove shrink as it handled in special function */
    if ((0 != connection->read_buffer_size) &&
        (connection->read_buffer_size != connection->read_buffer_offset))
    {
      mhd_assert (NULL != connection->read_buffer);
      connection->read_buffer =
        MHD_pool_reallocate (connection->pool,
                             connection->read_buffer,
                             connection->read_buffer_size,
                             connection->read_buffer_offset);
      connection->read_buffer_size = connection->read_buffer_offset;
    }
    break;
  case MHD_CONNECTION_REQ_LINE_RECEIVED:
  case MHD_CONNECTION_HEADERS_RECEIVED:
  case MHD_CONNECTION_HEADERS_PROCESSED:
  case MHD_CONNECTION_BODY_RECEIVED:
  case MHD_CONNECTION_FOOTERS_RECEIVED:
    /* Milestone state, no data should be read */
    mhd_assert (0); /* Should not be possible */
    break;
  case MHD_CONNECTION_CONTINUE_SENDING:
  case MHD_CONNECTION_HEADERS_SENDING:
  case MHD_CONNECTION_HEADERS_SENT:
  case MHD_CONNECTION_NORMAL_BODY_UNREADY:
  case MHD_CONNECTION_NORMAL_BODY_READY:
  case MHD_CONNECTION_CHUNKED_BODY_UNREADY:
  case MHD_CONNECTION_CHUNKED_BODY_READY:
  case MHD_CONNECTION_CHUNKED_BODY_SENT:
  case MHD_CONNECTION_FOOTERS_SENDING:
  case MHD_CONNECTION_FULL_REPLY_SENT:
  default:
    mhd_assert (0); /* Should not be possible */
    break;
  }
  {
    FILE *logf = fopen("/tmp/logs/1816.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "EXIT: MHD_connection_handle_read: connection=%p final_state=%d\n",
              (void *)connection, (int)connection->state);
      fclose(logf);
    }
  }
  return;
}
