void
MHD_connection_set_initial_state_ (struct MHD_Connection *c)
{
  size_t read_buf_size;
  FILE *log = fopen("/tmp/logs/3448.txt", "a");
  if (log)
    {
      fprintf(log, "ENTER MHD_connection_set_initial_state_: c=%p\n", (void *) c);
#ifdef HTTPS_SUPPORT
      fprintf(log, "  daemon->options=0x%lx, tls_state=%d\n",
              (unsigned long) c->daemon->options,
              (int) c->tls_state);
#else
      fprintf(log, "  daemon->options=0x%lx\n",
              (unsigned long) c->daemon->options);
#endif
      fprintf(log, "  initial state before: c->state=%d\n", (int) c->state);
    }

#ifdef HTTPS_SUPPORT
  mhd_assert ( (0 == (c->daemon->options & MHD_USE_TLS)) || \
               (MHD_TLS_CONN_INIT == c->tls_state) );
  mhd_assert ( (0 != (c->daemon->options & MHD_USE_TLS)) || \
               (MHD_TLS_CONN_NO_TLS == c->tls_state) );
#endif /* HTTPS_SUPPORT */
  mhd_assert (MHD_CONNECTION_INIT == c->state);

  c->keepalive = MHD_CONN_KEEPALIVE_UNKOWN;
  if (log)
    fprintf(log, "  keepalive set=%d\n", (int) c->keepalive);
  c->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
  if (log)
    fprintf(log, "  event_loop_info set=%d\n", (int) c->event_loop_info);

  memset (&c->rq, 0, sizeof(c->rq));
  if (log)
    fprintf(log, "  rq zeroed\n");
  memset (&c->rp, 0, sizeof(c->rp));
  if (log)
    fprintf(log, "  rp zeroed\n");

  c->write_buffer = NULL;
  if (log)
    fprintf(log, "  write_buffer=NULL\n");
  c->write_buffer_size = 0;
  if (log)
    fprintf(log, "  write_buffer_size=0\n");
  c->write_buffer_send_offset = 0;
  if (log)
    fprintf(log, "  write_buffer_send_offset=0\n");
  c->write_buffer_append_offset = 0;
  if (log)
    fprintf(log, "  write_buffer_append_offset=0\n");

  c->continue_message_write_offset = 0;
  if (log)
    fprintf(log, "  continue_message_write_offset=0\n");

  c->read_buffer_offset = 0;
  if (log)
    fprintf(log, "  read_buffer_offset=0\n");
  read_buf_size = c->daemon->pool_size / 2;
  if (log)
    fprintf(log, "  computed read_buf_size=%zu\n", read_buf_size);
  c->read_buffer
    = MHD_pool_allocate (c->pool,
                         read_buf_size,
                         false);
  c->read_buffer_size = read_buf_size;
  if (log)
    {
      fprintf(log, "  read_buffer=%p, read_buffer_size=%zu\n",
              (void *) c->read_buffer, c->read_buffer_size);
      fclose(log);
    }
}
