static void
MHD_connection_update_event_loop_info (struct MHD_Connection *connection)
{
  FILE *logfp = fopen("/tmp/logs/3678.txt", "a");
  if (logfp)
  {
    /* Minimal entry probe */
    fprintf (logfp,
             "ENTER: MHD_connection_update_event_loop_info conn=%p suspended=%d state=%d (%s) tls_state=%d read_off=%zu read_size=%zu\n",
             (void *) connection,
             connection ? (int) connection->suspended : -1,
             connection ? (int) connection->state : -1,
             connection ? MHD_state_to_string (connection->state) : "(nil)",
             connection ? (int) connection->tls_state : -1,
             connection ? connection->read_buffer_offset : 0U,
             connection ? connection->read_buffer_size : 0U);
    fflush (logfp);
  }

  /* Do not update states of suspended connection */
  if (connection->suspended)
  {
    if (logfp)
    {
      fprintf (logfp, "EXIT: suspended -> no update\n");
      fclose (logfp);
      logfp = NULL;
    }
    return; /* States will be updated after resume. */
  }
#ifdef HTTPS_SUPPORT
  if (MHD_TLS_CONN_NO_TLS != connection->tls_state)
  {   /* HTTPS connection. */
    switch (connection->tls_state)
    {
    case MHD_TLS_CONN_INIT:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      if (logfp)
      {
        fprintf (logfp, "TLS case INIT: set event_loop_info=READ\n");
        fclose (logfp);
        logfp = NULL;
      }
      return;
    case MHD_TLS_CONN_HANDSHAKING:
    case MHD_TLS_CONN_WR_CLOSING:
      if (0 == gnutls_record_get_direction (connection->tls_session))
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      else
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      if (logfp)
      {
        fprintf (logfp, "TLS case HANDSHAKING/WR_CLOSING: direction=%d event_loop_info=%d\n",
                 (int) gnutls_record_get_direction (connection->tls_session),
                 (int) connection->event_loop_info);
        fclose (logfp);
        logfp = NULL;
      }
      return;
    case MHD_TLS_CONN_CONNECTED:
      break; /* Do normal processing */
    case MHD_TLS_CONN_WR_CLOSED:
    case MHD_TLS_CONN_TLS_FAILED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      if (logfp)
      {
        fprintf (logfp, "TLS case WR_CLOSED/TLS_FAILED: set event_loop_info=CLEANUP\n");
        fclose (logfp);
        logfp = NULL;
      }
      return;
    case MHD_TLS_CONN_TLS_CLOSING:  /* Not implemented yet */
    case MHD_TLS_CONN_TLS_CLOSED:   /* Not implemented yet */
    case MHD_TLS_CONN_INVALID_STATE:
    case MHD_TLS_CONN_NO_TLS: /* Not possible */
    default:
      if (logfp)
      {
        fprintf (logfp, "TLS default/invalid state: tls_state=%d -> PANIC\n", (int) connection->tls_state);
        fclose (logfp);
        logfp = NULL;
      }
      MHD_PANIC (_ ("Invalid TLS state value.\n"));
    }
  }
#endif /* HTTPS_SUPPORT */
  while (1)
  {
#if DEBUG_STATES
    MHD_DLOG (connection->daemon,
              _ ("In function %s handling connection at state: %s\n"),
              MHD_FUNC_,
              MHD_state_to_string (connection->state));
#endif
    switch (connection->state)
    {
    case MHD_CONNECTION_INIT:
    case MHD_CONNECTION_REQ_LINE_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      if (logfp)
      {
        fprintf (logfp, "STATE: %s -> event_loop_info=READ\n",
                 MHD_state_to_string (connection->state));
        fflush (logfp);
      }
      break;
    case MHD_CONNECTION_REQ_LINE_RECEIVED:
      if (logfp)
      {
        fprintf (logfp, "STATE: REQ_LINE_RECEIVED -> ASSERT_ONLY path (mhd_assert(0))\n");
        fflush (logfp);
      }
      mhd_assert (0);
      break;
    case MHD_CONNECTION_REQ_HEADERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      if (logfp)
      {
        fprintf (logfp, "STATE: REQ_HEADERS_RECEIVING -> event_loop_info=READ\n");
        fflush (logfp);
      }
      break;
    case MHD_CONNECTION_HEADERS_RECEIVED:
    case MHD_CONNECTION_HEADERS_PROCESSED:
      if (logfp)
      {
        fprintf (logfp, "STATE: HEADERS_* -> ASSERT_ONLY path (mhd_assert(0)) state=%s\n",
                 MHD_state_to_string (connection->state));
        fflush (logfp);
      }
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CONTINUE_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      if (logfp)
      {
        fprintf (logfp, "STATE: CONTINUE_SENDING -> event_loop_info=WRITE\n");
        fflush (logfp);
      }
      break;
    case MHD_CONNECTION_BODY_RECEIVING:
      if ((connection->rq.some_payload_processed) &&
          has_unprocessed_upload_body_data_in_buffer (connection))
      {
        /* Some data was processed, the buffer must have some free space */
        mhd_assert (connection->read_buffer_offset < \
                    connection->read_buffer_size);
        if (! connection->rq.have_chunked_upload)
        {
          /* Not a chunked upload. Do not read more than necessary to
             process the current request. */
          if (connection->rq.remaining_upload_size >=
              connection->read_buffer_offset)
            connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
          else
            connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
        }
        else
        {
          /* Chunked upload. The size of the current request is unknown.
             Continue reading as the space in the read buffer is available. */
          connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
        }
        if (logfp)
        {
          fprintf (logfp, "STATE: BODY_RECEIVING (some_payload_processed=1) -> event_loop_info=%d read_off=%zu read_size=%zu remaining_upload_size=%zu have_chunked=%d\n",
                   (int) connection->event_loop_info,
                   connection->read_buffer_offset,
                   connection->read_buffer_size,
                   connection->rq.remaining_upload_size,
                   (int) connection->rq.have_chunked_upload);
          fflush (logfp);
        }
      }
      else
      {
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
        if (logfp)
        {
          fprintf (logfp, "STATE: BODY_RECEIVING (no payload processed) -> event_loop_info=READ\n");
          fflush (logfp);
        }
      }
      break;
    case MHD_CONNECTION_BODY_RECEIVED:
      if (logfp)
      {
        fprintf (logfp, "STATE: BODY_RECEIVED -> ASSERT_ONLY path (mhd_assert(0))\n");
        fflush (logfp);
      }
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      if (logfp)
      {
        fprintf (logfp, "STATE: FOOTERS_RECEIVING -> event_loop_info=READ\n");
        fflush (logfp);
      }
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVED:
      if (logfp)
      {
        fprintf (logfp, "STATE: FOOTERS_RECEIVED -> ASSERT_ONLY path (mhd_assert(0))\n");
        fflush (logfp);
      }
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FULL_REQ_RECEIVED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      if (logfp)
      {
        fprintf (logfp, "STATE: FULL_REQ_RECEIVED -> event_loop_info=PROCESS\n");
        fflush (logfp);
      }
      break;
    case MHD_CONNECTION_START_REPLY:
      if (logfp)
      {
        fprintf (logfp, "STATE: START_REPLY -> ASSERT_ONLY path (mhd_assert(0))\n");
        fflush (logfp);
      }
      mhd_assert (0);
      break;
    case MHD_CONNECTION_HEADERS_SENDING:
      /* headers in buffer, keep writing */
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      if (logfp)
      {
        fprintf (logfp, "STATE: HEADERS_SENDING -> event_loop_info=WRITE\n");
        fflush (logfp);
      }
      break;
    case MHD_CONNECTION_HEADERS_SENT:
      if (logfp)
      {
        fprintf (logfp, "STATE: HEADERS_SENT -> ASSERT_ONLY path (mhd_assert(0))\n");
        fflush (logfp);
      }
      mhd_assert (0);
      break;
    case MHD_CONNECTION_NORMAL_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      if (logfp)
      {
        fprintf (logfp, "STATE: NORMAL_BODY_UNREADY -> event_loop_info=PROCESS\n");
        fflush (logfp);
      }
      break;
    case MHD_CONNECTION_NORMAL_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      if (logfp)
      {
        fprintf (logfp, "STATE: NORMAL_BODY_READY -> event_loop_info=WRITE\n");
        fflush (logfp);
      }
      break;
    case MHD_CONNECTION_CHUNKED_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      if (logfp)
      {
        fprintf (logfp, "STATE: CHUNKED_BODY_UNREADY -> event_loop_info=PROCESS\n");
        fflush (logfp);
      }
      break;
    case MHD_CONNECTION_CHUNKED_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      if (logfp)
      {
        fprintf (logfp, "STATE: CHUNKED_BODY_READY -> event_loop_info=WRITE\n");
        fflush (logfp);
      }
      break;
    case MHD_CONNECTION_CHUNKED_BODY_SENT:
      if (logfp)
      {
        fprintf (logfp, "STATE: CHUNKED_BODY_SENT -> ASSERT_ONLY path (mhd_assert(0))\n");
        fflush (logfp);
      }
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      if (logfp)
      {
        fprintf (logfp, "STATE: FOOTERS_SENDING -> event_loop_info=WRITE\n");
        fflush (logfp);
      }
      break;
    case MHD_CONNECTION_FULL_REPLY_SENT:
      if (logfp)
      {
        fprintf (logfp, "STATE: FULL_REPLY_SENT -> ASSERT_ONLY path (mhd_assert(0))\n");
        fflush (logfp);
      }
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CLOSED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      if (logfp)
      {
        fprintf (logfp, "STATE: CLOSED -> event_loop_info=CLEANUP -> RETURN\n");
        fclose (logfp);
        logfp = NULL;
      }
      return;           /* do nothing, not even reading */
#ifdef UPGRADE_SUPPORT
    case MHD_CONNECTION_UPGRADE:
      if (logfp)
      {
        fprintf (logfp, "STATE: UPGRADE -> ASSERT_ONLY path (mhd_assert(0))\n");
        fflush (logfp);
      }
      mhd_assert (0);
      break;
#endif /* UPGRADE_SUPPORT */
    default:
      if (logfp)
      {
        fprintf (logfp, "STATE: default -> ASSERT_ONLY path (mhd_assert(0)) state=%d\n", (int) connection->state);
        fflush (logfp);
      }
      mhd_assert (0);
    }

    if (0 != (MHD_EVENT_LOOP_INFO_READ & connection->event_loop_info))
    {
      /* Check whether the space is available to receive data */
      if (! check_and_grow_read_buffer_space (connection))
      {
        if (logfp)
        {
          fprintf (logfp, "check_and_grow_read_buffer_space returned false, discard_request=%d -> continue loop\n", (int) connection->discard_request);
          fflush (logfp);
        }
        mhd_assert (connection->discard_request);
        continue;
      }
      else
      {
        if (logfp)
        {
          fprintf (logfp, "check_and_grow_read_buffer_space succeeded\n");
          fflush (logfp);
        }
      }
    }
    break; /* Everything was processed. */
  }

  if (logfp)
  {
    fprintf (logfp, "EXIT: MHD_connection_update_event_loop_info done conn=%p state=%d (%s) event_loop_info=%d\n",
             (void *) connection,
             (int) connection->state,
             MHD_state_to_string (connection->state),
             (int) connection->event_loop_info);
    fclose (logfp);
    logfp = NULL;
  }
}
