static void
MHD_connection_update_event_loop_info (struct MHD_Connection *connection)
{
  /* Open log file for tracing control flow and state. */
  FILE *mhd_log = fopen("/tmp/logs/3678.txt", "a");
  if (mhd_log)
    fprintf (mhd_log, "ENTER MHD_connection_update_event_loop_info: conn=%p suspended=%d\n",
             (void *) connection,
             (int) connection->suspended);

  /* Do not update states of suspended connection */
  if (connection->suspended)
  {
    if (mhd_log)
    {
      fprintf (mhd_log, "connection suspended -> early return\n");
      fclose (mhd_log);
    }
    return; /* States will be updated after resume. */
  }
#ifdef HTTPS_SUPPORT
  if (MHD_TLS_CONN_NO_TLS != connection->tls_state)
  {   /* HTTPS connection. */
    if (mhd_log)
      fprintf (mhd_log, "HTTPS path: tls_state=%d tls_session=%p\n",
               (int) connection->tls_state, (void *) connection->tls_session);
    switch (connection->tls_state)
    {
    case MHD_TLS_CONN_INIT:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      if (mhd_log)
      {
        fprintf (mhd_log, "TLS INIT -> event_loop_info=READ\n");
        fclose (mhd_log);
      }
      return;
    case MHD_TLS_CONN_HANDSHAKING:
    case MHD_TLS_CONN_WR_CLOSING:
      if (0 == gnutls_record_get_direction (connection->tls_session))
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      else
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      if (mhd_log)
      {
        fprintf (mhd_log, "TLS HANDSHAKING/WR_CLOSING -> direction=%d event_loop_info=%d\n",
                 (int) gnutls_record_get_direction (connection->tls_session),
                 (int) connection->event_loop_info);
        fclose (mhd_log);
      }
      return;
    case MHD_TLS_CONN_CONNECTED:
      if (mhd_log)
        fprintf (mhd_log, "TLS CONNECTED -> normal processing\n");
      break; /* Do normal processing */
    case MHD_TLS_CONN_WR_CLOSED:
    case MHD_TLS_CONN_TLS_FAILED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      if (mhd_log)
      {
        fprintf (mhd_log, "TLS WR_CLOSED/TLS_FAILED -> CLEANUP\n");
        fclose (mhd_log);
      }
      return;
    case MHD_TLS_CONN_TLS_CLOSING:  /* Not implemented yet */
    case MHD_TLS_CONN_TLS_CLOSED:   /* Not implemented yet */
    case MHD_TLS_CONN_INVALID_STATE:
    case MHD_TLS_CONN_NO_TLS: /* Not possible */
    default:
      if (mhd_log)
      {
        fprintf (mhd_log, "TLS default/invalid state -> panic (tls_state=%d)\n",
                 (int) connection->tls_state);
        fclose (mhd_log);
      }
      MHD_PANIC (_ ("Invalid TLS state value.\n"));
    }
  }
#endif /* HTTPS_SUPPORT */
  while (1)
  {
#if DEBUG_STATES
    MHD_DLOG (connection->daemon,
              _ ("In function %s handling connection at state: %s\n"),
              MHD_FUNC_,
              MHD_state_to_string (connection->state));
#endif
    if (mhd_log)
    {
      /* Log numeric state and string representation where available. */
      fprintf (mhd_log, "LOOP: conn=%p state=%d state_str=%s event_loop_info(before)=%d\n",
               (void *) connection,
               (int) connection->state,
               MHD_state_to_string (connection->state),
               (int) connection->event_loop_info);
    }

    switch (connection->state)
    {
    case MHD_CONNECTION_INIT:
    case MHD_CONNECTION_REQ_LINE_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      break;
    case MHD_CONNECTION_REQ_LINE_RECEIVED:
      if (mhd_log)
        fprintf (mhd_log, "STATE REQ_LINE_RECEIVED -> mhd_assert(0) will be executed\n");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_REQ_HEADERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      break;
    case MHD_CONNECTION_HEADERS_RECEIVED:
    case MHD_CONNECTION_HEADERS_PROCESSED:
      if (mhd_log)
        fprintf (mhd_log, "STATE HEADERS_RECEIVED/PROCESSED -> mhd_assert(0) will be executed\n");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CONTINUE_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_BODY_RECEIVING:
      if ((connection->rq.some_payload_processed) &&
          has_unprocessed_upload_body_data_in_buffer (connection))
      {
        /* Some data was processed, the buffer must have some free space */
        if (mhd_log)
          fprintf (mhd_log, "BODY_RECEIVING: some_payload_processed=1 read_buffer_offset=%u read_buffer_size=%u\n",
                   (unsigned) connection->read_buffer_offset,
                   (unsigned) connection->read_buffer_size);
        mhd_assert (connection->read_buffer_offset < \
                    connection->read_buffer_size);
        if (! connection->rq.have_chunked_upload)
        {
          /* Not a chunked upload. Do not read more than necessary to
             process the current request. */
          if (connection->rq.remaining_upload_size >=
              connection->read_buffer_offset)
            connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
          else
            connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
          if (mhd_log)
            fprintf (mhd_log, "BODY_RECEIVING non-chunked -> remaining_upload_size=%llu event_loop_info=%d\n",
                     (unsigned long long) connection->rq.remaining_upload_size,
                     (int) connection->event_loop_info);
        }
        else
        {
          /* Chunked upload. The size of the current request is unknown.
             Continue reading as the space in the read buffer is available. */
          connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
          if (mhd_log)
            fprintf (mhd_log, "BODY_RECEIVING chunked -> event_loop_info=PROCESS_READ\n");
        }
      }
      else
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      break;
    case MHD_CONNECTION_BODY_RECEIVED:
      if (mhd_log)
        fprintf (mhd_log, "STATE BODY_RECEIVED -> mhd_assert(0) will be executed\n");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVED:
      if (mhd_log)
        fprintf (mhd_log, "STATE FOOTERS_RECEIVED -> mhd_assert(0) will be executed\n");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FULL_REQ_RECEIVED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      break;
    case MHD_CONNECTION_START_REPLY:
      if (mhd_log)
        fprintf (mhd_log, "STATE START_REPLY -> mhd_assert(0) will be executed\n");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_HEADERS_SENDING:
      /* headers in buffer, keep writing */
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_HEADERS_SENT:
      if (mhd_log)
        fprintf (mhd_log, "STATE HEADERS_SENT -> mhd_assert(0) will be executed\n");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_NORMAL_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      break;
    case MHD_CONNECTION_NORMAL_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_CHUNKED_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      break;
    case MHD_CONNECTION_CHUNKED_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_CHUNKED_BODY_SENT:
      if (mhd_log)
        fprintf (mhd_log, "STATE CHUNKED_BODY_SENT -> mhd_assert(0) will be executed\n");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_FULL_REPLY_SENT:
      if (mhd_log)
        fprintf (mhd_log, "STATE FULL_REPLY_SENT -> mhd_assert(0) will be executed\n");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CLOSED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      if (mhd_log)
      {
        fprintf (mhd_log, "STATE CLOSED -> CLEANUP and return\n");
        fclose (mhd_log);
      }
      return;           /* do nothing, not even reading */
#ifdef UPGRADE_SUPPORT
    case MHD_CONNECTION_UPGRADE:
      if (mhd_log)
        fprintf (mhd_log, "STATE UPGRADE -> mhd_assert(0) will be executed\n");
      mhd_assert (0);
      break;
#endif /* UPGRADE_SUPPORT */
    default:
      if (mhd_log)
        fprintf (mhd_log, "STATE default -> mhd_assert(0) will be executed for state=%d\n",
                 (int) connection->state);
      mhd_assert (0);
    }

    if (0 != (MHD_EVENT_LOOP_INFO_READ & connection->event_loop_info))
    {
      /* Check whether the space is available to receive data */
      if (! check_and_grow_read_buffer_space (connection))
      {
        if (mhd_log)
          fprintf (mhd_log, "check_and_grow_read_buffer_space returned false -> discard_request=%d\n",
                   (int) connection->discard_request);
        mhd_assert (connection->discard_request);
        /* keep log open across continue */
        continue;
      }
    }
    if (mhd_log)
      fprintf (mhd_log, "LOOP end: conn=%p state=%d event_loop_info(after)=%d -> breaking out\n",
               (void *) connection,
               (int) connection->state,
               (int) connection->event_loop_info);
    break; /* Everything was processed. */
  }

  if (mhd_log)
  {
    fprintf (mhd_log, "EXIT MHD_connection_update_event_loop_info: conn=%p final_event_loop_info=%d\n",
             (void *) connection,
             (int) connection->event_loop_info);
    fclose (mhd_log);
  }
}
