static void
MHD_connection_update_event_loop_info (struct MHD_Connection *connection)
{
  FILE *log = NULL;
  /* Open log file once for the function invocation. Use append to preserve previous runs. */
  log = fopen("/tmp/logs/3675.txt", "a");
  if (log)
  {
    fprintf(log, "--- Entering MHD_connection_update_event_loop_info ---\n");
    fprintf(log, "connection=%p suspended=%d state=%d tls_state=%d event_loop_info=%d\n",
            (void *)connection,
            connection ? connection->suspended : -1,
            connection ? (int)connection->state : -1,
            connection ? (int)connection->tls_state : -1,
            connection ? (int)connection->event_loop_info : -1);
    fflush(log);
  }

  /* Do not update states of suspended connection */
  if (connection->suspended)
    goto end; /* States will be updated after resume. */
#ifdef HTTPS_SUPPORT
  if (MHD_TLS_CONN_NO_TLS != connection->tls_state)
  {   /* HTTPS connection. */
    switch (connection->tls_state)
    {
    case MHD_TLS_CONN_INIT:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      goto end;
    case MHD_TLS_CONN_HANDSHAKING:
    case MHD_TLS_CONN_WR_CLOSING:
      if (0 == gnutls_record_get_direction (connection->tls_session))
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      else
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      goto end;
    case MHD_TLS_CONN_CONNECTED:
      break; /* Do normal processing */
    case MHD_TLS_CONN_WR_CLOSED:
    case MHD_TLS_CONN_TLS_FAILED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      goto end;
    case MHD_TLS_CONN_TLS_CLOSING:  /* Not implemented yet */
    case MHD_TLS_CONN_TLS_CLOSED:   /* Not implemented yet */
    case MHD_TLS_CONN_INVALID_STATE:
    case MHD_TLS_CONN_NO_TLS: /* Not possible */
    default:
    {
      /* Probe the message returned by _() to capture its pointer and contents */
      const char *panic_msg = _ ("Invalid TLS state value.\n");
      if (log)
      {
        fprintf(log, "About to call MHD_PANIC with message ptr=%p\n", (const void *)panic_msg);
        /* Print up to 256 chars of the message to avoid overly long logs. */
        if (panic_msg)
        {
          char buf[257];
          size_t i;
          for (i = 0; i < 256 && panic_msg[i]; ++i)
            buf[i] = panic_msg[i];
          buf[i] = '\0';
          fprintf(log, "Panic message (trimmed): '%s'\n", buf);
        }
        fflush(log);
      }
      MHD_PANIC (panic_msg);
    }
    }
  }
#endif /* HTTPS_SUPPORT */
  while (1)
  {
#if DEBUG_STATES
    /* Probe the state string pointer returned by MHD_state_to_string */
    const char *state_s = MHD_state_to_string (connection->state);
    if (log)
    {
      fprintf(log, "DEBUG_STATES: function=%s connection=%p state=%d state_str_ptr=%p\n",
              MHD_FUNC_, (void *)connection, (int)connection->state, (const void *)state_s);
      if (state_s)
      {
        char buf[257];
        size_t i;
        for (i = 0; i < 256 && state_s[i]; ++i)
          buf[i] = state_s[i];
        buf[i] = '\0';
        fprintf(log, "DEBUG_STATES: state_str (trimmed)='%s'\n", buf);
      }
      fflush(log);
    }
    MHD_DLOG (connection->daemon,
              _ ("In function %s handling connection at state: %s\n"),
              MHD_FUNC_,
              MHD_state_to_string (connection->state));
#endif
    switch (connection->state)
    {
    case MHD_CONNECTION_INIT:
    case MHD_CONNECTION_REQ_LINE_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      break;
    case MHD_CONNECTION_REQ_LINE_RECEIVED:
      mhd_assert (0);
      break;
    case MHD_CONNECTION_REQ_HEADERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      break;
    case MHD_CONNECTION_HEADERS_RECEIVED:
    case MHD_CONNECTION_HEADERS_PROCESSED:
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CONTINUE_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_BODY_RECEIVING:
      if ((connection->rq.some_payload_processed) &&
          has_unprocessed_upload_body_data_in_buffer (connection))
      {
        /* Some data was processed, the buffer must have some free space */
        mhd_assert (connection->read_buffer_offset < \
                    connection->read_buffer_size);
        if (! connection->rq.have_chunked_upload)
        {
          /* Not a chunked upload. Do not read more than necessary to
             process the current request. */
          if (connection->rq.remaining_upload_size >=
              connection->read_buffer_offset)
            connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
          else
            connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
        }
        else
        {
          /* Chunked upload. The size of the current request is unknown.
             Continue reading as the space in the read buffer is available. */
          connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
        }
      }
      else
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      break;
    case MHD_CONNECTION_BODY_RECEIVED:
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVED:
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FULL_REQ_RECEIVED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      break;
    case MHD_CONNECTION_START_REPLY:
      mhd_assert (0);
      break;
    case MHD_CONNECTION_HEADERS_SENDING:
      /* headers in buffer, keep writing */
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_HEADERS_SENT:
      mhd_assert (0);
      break;
    case MHD_CONNECTION_NORMAL_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      break;
    case MHD_CONNECTION_NORMAL_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_CHUNKED_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      break;
    case MHD_CONNECTION_CHUNKED_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_CHUNKED_BODY_SENT:
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      break;
    case MHD_CONNECTION_FULL_REPLY_SENT:
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CLOSED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      goto end;           /* do nothing, not even reading */
#ifdef UPGRADE_SUPPORT
    case MHD_CONNECTION_UPGRADE:
      mhd_assert (0);
      break;
#endif /* UPGRADE_SUPPORT */
    default:
      mhd_assert (0);
    }

    if (0 != (MHD_EVENT_LOOP_INFO_READ & connection->event_loop_info))
    {
      /* Check whether the space is available to receive data */
      if (! check_and_grow_read_buffer_space (connection))
      {
        if (log)
        {
          fprintf(log, "check_and_grow_read_buffer_space returned false for connection=%p\n", (void *)connection);
          fprintf(log, "read_buffer_offset=%u read_buffer_size=%u discard_request=%d\n",
                  (unsigned)connection->read_buffer_offset,
                  (unsigned)connection->read_buffer_size,
                  (int)connection->discard_request);
          fflush(log);
        }
        mhd_assert (connection->discard_request);
        continue;
      }
    }
    break; /* Everything was processed. */
  }

end:
  if (log)
  {
    fprintf(log, "--- Exiting MHD_connection_update_event_loop_info: connection=%p state=%d event_loop_info=%d\n",
            (void *)connection, (int)(connection ? connection->state : -1), (int)(connection ? connection->event_loop_info : -1));
    fprintf(log, "----------------------------------------------\n");
    fclose(log);
  }
}
