_MHD_EXTERN struct MHD_PostProcessor *
MHD_create_post_processor (struct MHD_Connection *connection,
                           size_t buffer_size,
                           MHD_PostDataIterator iter,
                           void *iter_cls)
{
  struct MHD_PostProcessor *ret;
  const char *encoding;
  const char *boundary;
  size_t blen;

  /* Minimal inline logging helper usage: open/append/close each time to
     ensure logs are flushed and visible to external test harness. */
  {
    FILE *logf = fopen("/tmp/logs/1688.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "MHD_create_post_processor: entry; connection=%p buffer_size=%zu iter=%p iter_cls=%p\n",
              (void *)connection, buffer_size, (void *)iter, iter_cls);
      fclose(logf);
    }
  }

  if ( (buffer_size < 256) ||
       (NULL == connection) ||
       (NULL == iter))
    MHD_PANIC (_ ("libmicrohttpd API violation.\n"));
  encoding = NULL;
  {
    int lookup_ret =
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_CONTENT_TYPE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_CONTENT_TYPE),
                                     &encoding,
                                     NULL);
    {
      FILE *logf = fopen("/tmp/logs/1688.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "MHD_create_post_processor: after lookup; lookup_ret=%d encoding=%p %s\n",
                lookup_ret, (void *)encoding, (encoding ? encoding : "(null)"));
        fclose(logf);
      }
    }
    if (MHD_NO == lookup_ret)
    {
      FILE *logf = fopen("/tmp/logs/1688.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "MHD_create_post_processor: returning NULL (no content-type header)\n");
        fclose(logf);
      }
      return NULL;
    }
  }
  /* This assertion is the subject of the reported violation scenario. Log
     just before and after to capture whether it is reached and the value of
     encoding. */
  {
    FILE *logf = fopen("/tmp/logs/1688.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "MHD_create_post_processor: about to mhd_assert(NULL != encoding); encoding=%p %s\n",
              (void *)encoding, (encoding ? encoding : "(null)"));
      fclose(logf);
    }
  }
  mhd_assert (NULL != encoding);
  {
    FILE *logf = fopen("/tmp/logs/1688.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "MHD_create_post_processor: passed mhd_assert; encoding=%p %s\n",
              (void *)encoding, (encoding ? encoding : "(null)"));
      fclose(logf);
    }
  }

  boundary = NULL;
  if (! MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_FORM_URLENCODED,
                                   encoding,
                                   MHD_STATICSTR_LEN_ (
                                     MHD_HTTP_POST_ENCODING_FORM_URLENCODED)))
  {
    if (! MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA,
                                     encoding,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA)))
    {
      FILE *logf = fopen("/tmp/logs/1688.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "MHD_create_post_processor: returning NULL (unsupported content-type) encoding=%p %s\n",
                (void *)encoding, (encoding ? encoding : "(null)"));
        fclose(logf);
      }
      return NULL;
    }
    boundary =
      &encoding[MHD_STATICSTR_LEN_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA)];
    /* Q: should this be "strcasestr"? */
    boundary = strstr (boundary, "boundary=");
    if (NULL == boundary)
    {
      FILE *logf = fopen("/tmp/logs/1688.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "MHD_create_post_processor: returning NULL (no boundary found)\n");
        fclose(logf);
      }
      return NULL; /* failed to determine boundary */
    }
    boundary += MHD_STATICSTR_LEN_ ("boundary=");
    blen = strlen (boundary);
    {
      FILE *logf = fopen("/tmp/logs/1688.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "MHD_create_post_processor: boundary present at %p length=%zu\n",
                (void *)boundary, blen);
        fclose(logf);
      }
    }
    if ( (blen < 2) ||
         (blen * 2 + 2 > buffer_size) )
    {
      FILE *logf = fopen("/tmp/logs/1688.txt", "a");
      if (NULL != logf)
      {
        fprintf(logf, "MHD_create_post_processor: returning NULL (invalid/too large boundary) blen=%zu buffer_size=%zu\n",
                blen, buffer_size);
        fclose(logf);
      }
      return NULL;              /* (will be) out of memory or invalid boundary */
    }
    if ( (boundary[0] == '\"') &&
         (boundary[blen - 1] == '\"') )
    {
      /* remove enclosing quotes */
      ++boundary;
      blen -= 2;
      {
        FILE *logf = fopen("/tmp/logs/1688.txt", "a");
        if (NULL != logf)
        {
          fprintf(logf, "MHD_create_post_processor: stripped quotes from boundary now length=%zu\n",
                  blen);
          fclose(logf);
        }
      }
    }
  }
  else
    blen = 0;
  buffer_size += 4; /* round up to get nice block sizes despite boundary search */

  /* add +1 to ensure we ALWAYS have a zero-termination at the end */
  if (NULL == (ret = MHD_calloc_ (1, sizeof (struct MHD_PostProcessor)
                                  + buffer_size + 1)))
  {
    FILE *logf = fopen("/tmp/logs/1688.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "MHD_create_post_processor: returning NULL (allocation failed) requested_size=%zu\n",
              sizeof (struct MHD_PostProcessor) + buffer_size + 1);
      fclose(logf);
    }
    return NULL;
  }
  ret->connection = connection;
  ret->ikvi = iter;
  ret->cls = iter_cls;
  ret->encoding = encoding;
  ret->buffer_size = buffer_size;
  ret->state = PP_Init;
  ret->blen = blen;
  ret->boundary = boundary;
  ret->skip_rn = RN_Inactive;

  {
    FILE *logf = fopen("/tmp/logs/1688.txt", "a");
    if (NULL != logf)
    {
      fprintf(logf, "MHD_create_post_processor: success returning ret=%p encoding=%p %s buffer_size=%zu blen=%zu boundary=%p\n",
              (void *)ret, (void *)encoding, (encoding ? encoding : "(null)"), buffer_size, blen, (void *)boundary);
      fclose(logf);
    }
  }
  return ret;
}
