_MHD_EXTERN struct MHD_PostProcessor *
MHD_create_post_processor (struct MHD_Connection *connection,
                           size_t buffer_size,
                           MHD_PostDataIterator iter,
                           void *iter_cls)
{
  struct MHD_PostProcessor *ret;
  const char *encoding;
  const char *boundary;
  size_t blen;
  FILE *logf = NULL;

  /* Try to open log file for append. If it fails, proceed without logging. */
  logf = fopen("/tmp/logs/1688.txt", "a");
  if (logf)
    fprintf(logf, "ENTER: MHD_create_post_processor(connection=%p, buffer_size=%zu, iter=%p, iter_cls=%p)\n", (void *)connection, buffer_size, (void *)iter, iter_cls);

  if ( (buffer_size < 256) ||
       (NULL == connection) ||
       (NULL == iter))
  {
    if (logf) fprintf(logf, "PANIC: api violation: buffer_size=%zu, connection=%p, iter=%p\n", buffer_size, (void *)connection, (void *)iter);
    if (logf) fclose(logf);
    MHD_PANIC (_ ("libmicrohttpd API violation.\n"));
  }

  if (logf) fprintf(logf, "About to perform assignment: encoding = NULL;\n");
  encoding = NULL;
  if (logf) fprintf(logf, "After assignment: encoding=%p\n", (void *)encoding);

  if (MHD_NO ==
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_CONTENT_TYPE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_CONTENT_TYPE),
                                     &encoding,
                                     NULL))
  {
    if (logf) fprintf(logf, "MHD_lookup_connection_value_n returned MHD_NO; encoding remains=%p; returning NULL\n", (void *)encoding);
    if (logf) fclose(logf);
    return NULL;
  }

  if (logf) {
    fprintf(logf, "MHD_lookup_connection_value_n succeeded; encoding=%p\n", (void *)encoding);
    if (encoding)
      fprintf(logf, "encoding string (possibly partial): '%s'\n", encoding);
  }

  mhd_assert (NULL != encoding);
  boundary = NULL;
  if (! MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_FORM_URLENCODED,
                                   encoding,
                                   MHD_STATICSTR_LEN_ (
                                     MHD_HTTP_POST_ENCODING_FORM_URLENCODED)))
  {
    if (! MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA,
                                     encoding,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA))) {
      if (logf) fprintf(logf, "encoding does not match urlencoded nor multipart/form-data; returning NULL\n");
      if (logf) fclose(logf);
      return NULL;
    }
    boundary =
      &encoding[MHD_STATICSTR_LEN_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA)];
    if (logf) fprintf(logf, "Detected multipart/form-data; boundary starts at offset %ld (ptr=%p)\n", (long)(boundary - encoding), (void *)boundary);
    /* Q: should this be "strcasestr"? */
    boundary = strstr (boundary, "boundary=");
    if (NULL == boundary) {
      if (logf) fprintf(logf, "Failed to find 'boundary=' in content-type header; returning NULL\n");
      if (logf) fclose(logf);
      return NULL; /* failed to determine boundary */
    }
    boundary += MHD_STATICSTR_LEN_ ("boundary=");
    blen = strlen (boundary);
    if (logf) fprintf(logf, "Found boundary (raw)='%s' len=%zu\n", boundary, blen);
    if ( (blen < 2) ||
         (blen * 2 + 2 > buffer_size) ) {
      if (logf) fprintf(logf, "Boundary length invalid or would exceed buffer (blen=%zu, buffer_size=%zu); returning NULL\n", blen, buffer_size);
      if (logf) fclose(logf);
      return NULL;              /* (will be) out of memory or invalid boundary */
    }
    if ( (boundary[0] == '"') &&
         (boundary[blen - 1] == '"') )
    {
      /* remove enclosing quotes */
      ++boundary;
      blen -= 2;
      if (logf) fprintf(logf, "Boundary had enclosing quotes; adjusted boundary='%s' blen=%zu\n", boundary, blen);
    }
  }
  else
    blen = 0;
  buffer_size += 4; /* round up to get nice block sizes despite boundary search */

  /* add +1 to ensure we ALWAYS have a zero-termination at the end */
  if (NULL == (ret = MHD_calloc_ (1, sizeof (struct MHD_PostProcessor)
                                  + buffer_size + 1)))
  {
    if (logf) fprintf(logf, "Memory allocation failed for size=%zu; returning NULL\n", sizeof (struct MHD_PostProcessor) + buffer_size + 1);
    if (logf) fclose(logf);
    return NULL;
  }

  ret->connection = connection;
  ret->ikvi = iter;
  ret->cls = iter_cls;
  ret->encoding = encoding;
  ret->buffer_size = buffer_size;
  ret->state = PP_Init;
  ret->blen = blen;
  ret->boundary = boundary;
  ret->skip_rn = RN_Inactive;

  if (logf) {
    fprintf(logf, "Created MHD_PostProcessor at %p; connection=%p, encoding=%p, buffer_size=%zu, blen=%zu, boundary=%p\n",
            (void *)ret, (void *)ret->connection, (void *)ret->encoding, ret->buffer_size, (size_t)ret->blen, (void *)ret->boundary);
    fclose(logf);
  }
  return ret;
}
