_MHD_EXTERN struct MHD_PostProcessor *
MHD_create_post_processor (struct MHD_Connection *connection,
                           size_t buffer_size,
                           MHD_PostDataIterator iter,
                           void *iter_cls)
{
  struct MHD_PostProcessor *ret;
  const char *encoding;
  const char *boundary;
  size_t blen;
  FILE *logf = NULL;

  /* Minimal entry probe so runs are recorded */
  logf = fopen("/tmp/logs/1688.txt", "a");
  if (logf)
    fprintf(logf, "ENTER: MHD_create_post_processor(connection=%p, buffer_size=%zu, iter=%p, iter_cls=%p)\n",
            (void *)connection, buffer_size, (void *)iter, iter_cls);
  if (logf)
    fclose(logf);

  if ( (buffer_size < 256) ||
       (NULL == connection) ||
       (NULL == iter))
  {
    /* log violation of API preconditions */
    logf = fopen("/tmp/logs/1688.txt", "a");
    if (logf)
      fprintf(logf, "PANIC: API violation: buffer_size=%zu connection=%p iter=%p\n",
              buffer_size, (void *)connection, (void *)iter);
    if (logf)
      fclose(logf);
    MHD_PANIC (_ ("libmicrohttpd API violation.\n"));
  }
  encoding = NULL;
  if (MHD_NO ==
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_CONTENT_TYPE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_CONTENT_TYPE),
                                     &encoding,
                                     NULL))
  {
    logf = fopen("/tmp/logs/1688.txt", "a");
    if (logf) {
      fprintf(logf, "RETURN NULL: content-type lookup failed for connection=%p\n", (void *)connection);
      if (encoding)
        fprintf(logf, "  encoding pointer non-NULL: %p, first byte=%02x\n", (const void *)encoding, (unsigned char)encoding[0]);
    }
    if (logf) fclose(logf);
    return NULL;
  }
  mhd_assert (NULL != encoding);
  boundary = NULL;
  if (! MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_FORM_URLENCODED,
                                   encoding,
                                   MHD_STATICSTR_LEN_ (
                                     MHD_HTTP_POST_ENCODING_FORM_URLENCODED)))
  {
    if (! MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA,
                                     encoding,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA)))
    {
      logf = fopen("/tmp/logs/1688.txt", "a");
      if (logf) {
        fprintf(logf, "RETURN NULL: unsupported encoding for connection=%p, encoding=");
        /* best-effort print encoding string if available */
        if (encoding) fprintf(logf, "%s", encoding);
        else fprintf(logf, "(null)");
        fprintf(logf, "\n");
      }
      if (logf) fclose(logf);
      return NULL;
    }
    boundary =
      &encoding[MHD_STATICSTR_LEN_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA)];
    /* Q: should this be "strcasestr"? */
    boundary = strstr (boundary, "boundary=");
    if (NULL == boundary) {
      logf = fopen("/tmp/logs/1688.txt", "a");
      if (logf) fprintf(logf, "RETURN NULL: multipart but no boundary found\n");
      if (logf) fclose(logf);
      return NULL; /* failed to determine boundary */
    }
    boundary += MHD_STATICSTR_LEN_ ("boundary=");
    blen = strlen (boundary);
    if ( (blen < 2) ||
         (blen * 2 + 2 > buffer_size) ) {
      logf = fopen("/tmp/logs/1688.txt", "a");
      if (logf) fprintf(logf, "RETURN NULL: invalid/too long boundary (blen=%zu) for buffer_size=%zu\n",
                         blen, buffer_size);
      if (logf) fclose(logf);
      return NULL;              /* (will be) out of memory or invalid boundary */
    }
    if ( (boundary[0] == '"') &&
         (boundary[blen - 1] == '"') )
    {
      /* remove enclosing quotes */
      ++boundary;
      blen -= 2;
    }
  }
  else
    blen = 0;
  buffer_size += 4; /* round up to get nice block sizes despite boundary search */

  /* add +1 to ensure we ALWAYS have a zero-termination at the end */
  if (NULL == (ret = MHD_calloc_ (1, sizeof (struct MHD_PostProcessor)
                                  + buffer_size + 1)))
  {
    logf = fopen("/tmp/logs/1688.txt", "a");
    if (logf) fprintf(logf, "RETURN NULL: allocation failed for size=%zu\n",
                       (size_t)(sizeof (struct MHD_PostProcessor) + buffer_size + 1));
    if (logf) fclose(logf);
    return NULL;
  }

  /* Log allocation and initial memory contents for analysis of dead-store */
  logf = fopen("/tmp/logs/1688.txt", "a");
  if (logf) {
    /* Print several important fields and the raw byte at skip_rn offset if possible */
    fprintf(logf, "ALLOC: ret=%p allocated_size=%zu buffer_size=%zu blen=%zu\n",
            (void *)ret,
            (size_t)(sizeof (struct MHD_PostProcessor) + buffer_size + 1),
            buffer_size, blen);
    /* RN_Inactive might be an enum/constant; print as int */
    fprintf(logf, "STATE_BEFORE_ASSIGN: RN_Inactive=%d\n", (int)RN_Inactive);
    /* Check current value at ret->skip_rn before assignment to see if calloc zeroed it */
    /* Use unsigned long to be safe with type sizes */
    fprintf(logf, "ret->skip_rn (before) = %lu\n", (unsigned long)ret->skip_rn);
  }
  if (logf) fclose(logf);

  ret->connection = connection;
  ret->ikvi = iter;
  ret->cls = iter_cls;
  ret->encoding = encoding;
  ret->buffer_size = buffer_size;
  ret->state = PP_Init;
  ret->blen = blen;
  ret->boundary = boundary;

  /* Capture and log the exact moment of the potentially dead assignment */
  logf = fopen("/tmp/logs/1688.txt", "a");
  if (logf) {
    fprintf(logf, "ASSIGN: about to execute ret->skip_rn = RN_Inactive; (ret=%p)\n", (void *)ret);
    fclose(logf);
  }

  ret->skip_rn = RN_Inactive;

  logf = fopen("/tmp/logs/1688.txt", "a");
  if (logf) {
    fprintf(logf, "ASSIGN_DONE: ret->skip_rn (after) = %lu, RN_Inactive=%d\n",
            (unsigned long)ret->skip_rn, (int)RN_Inactive);
    fprintf(logf, "EXIT: MHD_create_post_processor returning ret=%p\n", (void *)ret);
    fclose(logf);
  }
  return ret;
}
