static void __mhd_log_1683(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1683.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fputc('\n', f);
  fclose(f);
}

_MHD_EXTERN struct MHD_PostProcessor *
MHD_create_post_processor (struct MHD_Connection *connection,
                           size_t buffer_size,
                           MHD_PostDataIterator iter,
                           void *iter_cls)
{
  struct MHD_PostProcessor *ret;
  const char *encoding;
  const char *boundary;
  size_t blen;

  /* Log entry and input params */
  __mhd_log_1683("ENTER MHD_create_post_processor: connection=%p buffer_size=%zu iter=%p iter_cls=%p",
                 (void *)connection, buffer_size, (void *)iter, iter_cls);

  if ( (buffer_size < 256) ||
       (NULL == connection) ||
       (NULL == iter))
  {
    __mhd_log_1683("MHD_create_post_processor: API violation detected: buffer_size=%zu connection=%p iter=%p",
                   buffer_size, (void *)connection, (void *)iter);
    MHD_PANIC (_ ("libmicrohttpd API violation.\n"));
  }
  encoding = NULL;
  if (MHD_NO ==
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_CONTENT_TYPE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_CONTENT_TYPE),
                                     &encoding,
                                     NULL))
  {
    __mhd_log_1683("MHD_lookup_connection_value_n returned MHD_NO; encoding=NULL; returning NULL");
    return NULL;
  }
  mhd_assert (NULL != encoding);
  __mhd_log_1683("MHD_lookup_connection_value_n succeeded: encoding=%p", (void *)encoding);

  boundary = NULL;
  if (! MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_FORM_URLENCODED,
                                   encoding,
                                   MHD_STATICSTR_LEN_ (
                                     MHD_HTTP_POST_ENCODING_FORM_URLENCODED)))
  {
    if (! MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA,
                                     encoding,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA)))
    {
      __mhd_log_1683("Content-Type not URL-encoded nor multipart/form-data; encoding=%p; returning NULL", (void *)encoding);
      return NULL;
    }
    boundary =
      &encoding[MHD_STATICSTR_LEN_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA)];
    __mhd_log_1683("Candidate boundary start at offset %zu -> pointer %p",
                   (size_t)MHD_STATICSTR_LEN_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA), (void *)boundary);
    /* Q: should this be "strcasestr"? */
    boundary = strstr (boundary, "boundary=");
    __mhd_log_1683("strstr(search for 'boundary=') returned %p", (void *)boundary);
    if (NULL == boundary)
    {
      __mhd_log_1683("boundary not found (strstr returned NULL); returning NULL");
      return NULL; /* failed to determine boundary */
    }
    boundary += MHD_STATICSTR_LEN_ ("boundary=");
    __mhd_log_1683("boundary now points to %p (after 'boundary=' token)", (void *)boundary);

    /* The original code calls strlen here; keep that call to preserve behavior. */
    blen = strlen (boundary);
    __mhd_log_1683("strlen(boundary) -> blen=%zu", blen);

    if ( (blen < 2) ||
         (blen * 2 + 2 > buffer_size) )
    {
      __mhd_log_1683("Invalid or too long boundary: blen=%zu buffer_size=%zu; returning NULL",
                     blen, buffer_size);
      return NULL;              /* (will be) out of memory or invalid boundary */
    }
    if ( (boundary[0] == '"') &&
         (boundary[blen - 1] == '"') )
    {
      /* remove enclosing quotes */
      ++boundary;
      blen -= 2;
      __mhd_log_1683("Boundary had enclosing quotes -> adjusted boundary=%p blen=%zu", (void *)boundary, blen);
    }
  }
  else
    blen = 0;
  buffer_size += 4; /* round up to get nice block sizes despite boundary search */
  __mhd_log_1683("Adjusted buffer_size=%zu blen=%zu", buffer_size, blen);

  /* add +1 to ensure we ALWAYS have a zero-termination at the end */
  if (NULL == (ret = MHD_calloc_ (1, sizeof (struct MHD_PostProcessor)
                                  + buffer_size + 1)))
  {
    __mhd_log_1683("MHD_calloc_ failed; returning NULL");
    return NULL;
  }
  ret->connection = connection;
  ret->ikvi = iter;
  ret->cls = iter_cls;
  ret->encoding = encoding;
  ret->buffer_size = buffer_size;
  ret->state = PP_Init;
  ret->blen = blen;
  ret->boundary = boundary;
  ret->skip_rn = RN_Inactive;

  __mhd_log_1683("EXIT MHD_create_post_processor: ret=%p connection=%p boundary=%p blen=%zu buffer_size=%zu",
                 (void *)ret, (void *)ret->connection, (void *)ret->boundary, ret->blen, ret->buffer_size);
  return ret;
}
